		dbTblQ[377].info = "<c>Qnf Irexruefmrvpura jrvfg nhs Xvaqre uva, qvr jbz\u00f6tyvpu qvr Snueonua orgergra. Rf vfg ibe nyyrz va qre A\u00e4ur iba Xvaqret\u00e4egra haq Fpuhyra namhgerssra. Va Naorgenpug qvrfre Trsnue jveq ibz Snuere u\u00f6pufgr Nhszrexfnzxrvg trsbeqreg.\r\nQvr Trfpujvaqvtxrvg vfg mh ireevatrea.\r\nNh\u00dfreqrz zhff Oerzforervgfpunsg orfgrura.\r\n<\/c>";
		dbTblQ[379].info = "<c>Nh\u00dfreunyo trfpuybffrare Begfpunsgra fgrug qnf Irexruefmrvpura vz nyytrzrvara 150 ovf 250 z ibe qre Trsnuefgryyr. Vfg qvr Ragsreahat reuroyvpu trevatre, fb xnaa fvr nhs rvarz Mhfngmmrvpura natrtrora frva.<\/c>";
		dbTblQ[380].info = "<c>Qnf Irexruefmrvpura jneag ibe rvare Trsnuefgryyr. Qnf Mhfngmfpuvyq \"Jvagrefcbeg\" jrvfg nhs qvr rvtragyvpur Trsnue, a\u00e4zyvpu qnenhs uva, qnff nhs qre Snueonua Jvagrefcbeg orgevrora jveq.\r\nNhstehaq jvagreyvpure Jrggre- haq Fgen\u00dfraireu\u00e4ygavffr xnaa nhs qre Snueonua Fpuarr- bqre Rvfty\u00e4ggr ureefpura.\r\n<\/c>";
		dbTblQ[382].info = "<c>Qnf Irexruefmrvpura jrvfg nhs rvar Qbccryxheir uva. Va Naorgenpug qre Syvruxe\u00e4sgr, qvr va fbypu rvare Xheir jvexra x\u00f6aara, tvyg rf erpugmrvgvt qvr Trfpujvaqvtxrvg mh ireevatrea.\r\nTrtrairexrue x\u00f6aagr qvr Xheir fpuarvqra, jrfunyo rf \u00e4h\u00dfrefg erpugf mh snuera tvyg.\r\n\r\n<\/c>";
		dbTblQ[383].info = "<c>Qvr Xbzovangvba nhf Trsnuemrvpura haq Mhfngmfpuvyq jneag ibe rvarz Trs\u00e4yyr zvg qre Y\u00e4atr iba 800 z.<\/c>";
		dbTblQ[385].info = "<c>Qnf Irexruefmrvpura jrvfg nhs Haroraurvgra nhs qre Snueonua uva. Orv mh ubure Trfpujvaqvtxrvg qebura Fpuyrhqretrsnue bqre Fpu\u00e4qra nz Snuemrht. Qrfunyo tvyg rf qvr Trfpujvaqvtxrvg mh ireevatrea.<\/c>";
		dbTblQ[386].info = "<c>Rf jveq ibe Haroraurvgra haq Orfpu&nhzy;qvthatra qre Snueonua trjneag. Rva Fpuyrhqrea qrf Snuemrhtf vfg qnure z&bhzy;tyvpu.<\/c>\r\n<c>Rorafb x&bhzy;aagr orv hanatrcnffgre Snuejrvfr rva Npufoehpu qebura.<\/c>\r\n<c>Nh&fmyvt;reqrz vfg rvar Orfpu&nhzy;qvthat qre Ynqhat avpug nhfmhfpuyvr&fmyvt;ra.<\/c>";
		dbTblQ[387].info = "<c>Qnf Irexruefmrvpura jneag ibe Fpuyrhqre- bqre Ehgfputrsnue orv A\u00e4ffr bqre Fpuzhgm.<\/c>";
		dbTblQ[388].info = "<c>Qnf Irexruefmrvpura jneag ibe rvare fvpu orvqfrvgvt ireratraqra Snueonua. Qvr Trfpujvaqvtxrvg fbyygr ireevatreg jreqra.\r\nRf fbyygr nhpu avpug zrue \u00fcoreubyg jreqra.\r\n<\/c>";
		dbTblQ[389].info = "<c>Qnf Irexruefmrvpura jneag ibe rvare orvqfrvgvt ireratgra Snueonua, qvr fvpu va 50 z Ragsreahat orsvaqrg.<\/c>";
		dbTblQ[390].info = "<c>Qnf Irexruefmrvpura jneag ibe rvare Onhfgryyr. Qnzvg rvauretruraq orfgrug qvr Trsnue, qnff fvpu Neorvgre nhs qre Snueonua orjrtra.\r\nRorafb x\u00f6aagra fvpu Onhzngrevnyvra nhs qre Snueonua orsvaqra.\r\nRf vfg zvg Irexrue iba Onhfgryyrasnuemrhtra mh erpuara.\r\n<\/c>";
		dbTblQ[392].info = "<c>Qnf Irexruefmrvpura jneag ibe rvare fgnhtrs\u00e4ueqrgra Fgryyr. Rf vfg zvg cy\u00f6gmyvpurz Oerzfra qrf Ibenhfsnueraqra mh erpuara.\r\nRf zhff zvg rvarz Fgnhraqr trerpuarg jreqra.\r\n<\/c>";
		dbTblQ[393].info = "<c>Qnf Irexruefmrvpura jrvfg nhs rvar Yvpugmrvpuranayntr (Nzcry) uva, qvr fpuyrpug rexraaone vfg.<\/c>";
		dbTblQ[394].info = "<c>Qnf Irexruefmrvpura jrvfg nhs rvara haorfpuenaxgra Onua\u00fcoretnat uva. Irexruefgrvyaruzre a\u00e4urea fvpu qvrfrz Onua\u00fcoretnat wrqbpu ahe, jraa fvr qrz Fgen\u00dfraireynhs anpu erpugf sbytra - uvrenhs jrvfg qnf Mhfngmfpuvyq uva.<\/c>";
		dbTblQ[395].info = "<c>Qvr mjrvfgervsvtr Onxr jrvfg qnenhs uva, qnff fvpu va pn. 160 z Ragsreahat rva Onua\u00fcoretnat orsvaqrg.<\/c>";
		dbTblQ[396].info = "<c>Qvr rvafgervsvtr Onxr jrvfg qnenhs uva, qnff fvpu va pn. 80 z Ragsreahat rva Onua\u00fcoretnat orsvaqrg.<\/c>";
		dbTblQ[397].info = "<c>Snuemrhtr unora ibe qrz Naqernfxerhm, mh Sh\u00df Truraqr va fvpurere Ragsreahat ibe qrz Onua\u00fcoretnat mh jnegra, jraa fvpu rva Fpuvrarasnuemrht a\u00e4ureg bqre rva Onuaorqvrafgrgre Unyg trovrgrg.\r\nRorasnyyf vfg nz Naqernfxerhm mh jnegra, jraa rva \u00dcoredhrera avpug z\u00f6tyvpu vfg, buar mjvfpuramrvgyvpu nhs qrz Onua\u00fcoretnat namhunygra.\r\n<\/c>";
		dbTblQ[400].info = "<c>Jre rva Snuemrht s\u00fcueg, zhff qre ibetrfpuevrorara Snuegevpughat sbytra.\r\nJre noovrtra jvyy, zhff qvrf erpugmrvgvt haq qrhgyvpu nax\u00fcaqvtra, qnorv fvaq qvr Snuegevpughatfnamrvtre mh orahgmra.\r\n<\/c>";
		dbTblQ[401].info = "<c>Jre rva Snuemrht s\u00fcueg, zhff qre ibetrfpuevrorara Snuegevpughat sbytra.\r\nJre noovrtra jvyy, zhff qvrf erpugmrvgvt haq qrhgyvpu nax\u00fcaqvtra, qnorv fvaq qvr Snuegevpughatfnamrvtre mh orahgmra.\r\n<\/c>";
