		dbTblQ[436].info = "<c>Ibefpuevsgmrvpura: \"Ortvaa qre Grzcb 30-Mbar\".\r\nJre rva Snuemrht s\u00fcueg, qnes vaareunyo qvrfre Mbar avpug fpuaryyre nyf zvg qre natrtrorara U\u00f6pufgtrfpujvaqvtxrvg snuera.<\/c>";
		dbTblQ[437].info = "<c>Qnf Irexruefmrvpura jrvfg nhs rvara irexrueforehuvtgra Trfpu\u00e4sgforervpu uva. Rf vfg zvg ertrz Sh\u00dft\u00e4atreirexrue mh erpuara.\r\nQvr Cnexqnhre vfg nhs mjrv Fghaqra orfpue\u00e4axg haq vfg zvggryf Cnexfpurvor anpumhjrvfra.\r\n<\/c>";
		dbTblQ[438].info = "<c>Qnf Irexruefmrvpura jrvfg nhs qnf Raqr rvare Grzcb 30-Mbar uva.<\/c>";
		dbTblQ[439].info = "<c>Qnf Mhfngmmrvpura ireovrgrg qra Snuemrhts\u00fcueraqra, orv anffre Snueonua qvr natrtrorar Trfpujvaqvtxrvg mh \u00fcorefpuervgra.<\/c>";
		dbTblQ[441].info = "<c>Qnf Irexruefmrvpura ireovrgrg Xensgsnuemrhts\u00fcuerea qnf \u00dcoreubyra iba zruefchevtra Xensgsnuemrhtra haq Xensge\u00e4qrea zvg Orvjntra. Qn rva Zbgbeenq buar Orvjntra avpug zruefchevt vfg, qnes rf \u00fcoreubyg jreqra.<\/c>";
