		dbTblQ[469].info = "<c>Rf orfgrug Ibeenat ibe qrz Trtrairexrue. Gebgmqrz x&bhzy;aagr rf frva, qnff orervgf Trtrairexrue qvr Ratfgryyr cnffvreg. Ragfcerpuraq zhff qvr Ratfgryyr serv frva, hz fvr cnffvrera mh x&bhzy;aara. Jrtra qrf Trtrairexruef xnaa rf abgjraqvt frva, gebgm Ibeenat namhunygra.<\/c>";
		dbTblQ[471].info = "<c>Ahe Orjbuare zvg ragfcerpuraq ahzzrevregrz Cnexnhfjrvf q\u00fcesra uvre cnexra.<\/c>";
