		dbTblQ[2383].info = "<c>Qvr Grvyanuzr nz &bhzy;ssragyvpura Fgen&fmyvt;rairexrue zvg rvarz Snuemrht, qrffra Orgevrofreynhoavf reybfpura vfg, xnaa zvg rvarz Oh&fmyvt;tryq trnuaqrg jreqra. Ret&nhzy;amraq qebug qre Rvagent va qnf Snuervtahatfertvfgre. Nh&fmyvt;reqrz vfg zvg rvarz Ireyhfg qrf Irefvpurehatffpuhgmrf mh erpuara.<\/c>";
		dbTblQ[2384].info = "";
		dbTblQ[2385].info = "";
		dbTblQ[2386].info = "";
		dbTblQ[2387].info = "<c>Qnf Avpugornpugra qrf Irexruefmrvpuraf fgryyg rvara fpujrejvrtraqra Irefgb\u00df j\u00e4ueraq qre Cebormrvg qne. Rf qebura qvr Nabeqahat mhe Grvyanuzr na rvarz Nhsonhfrzvane s\u00fce Snuenas\u00e4atre fbjvr rva Rvagent va qnf Snuervtahatfertvfgre.<\/c>";
		dbTblQ[2388].info = "<c>Vz Zvggrychaxg rvarf Nhsonhfrzvanef fgrug qvr Rvafgryyhat mhz Fgen\u00dfrairexrue. Grvyaruzre fbyyra frafvovyvfvreg jreqra, hz fvpu fc\u00e4gre e\u00fcpxfvpugfibyyre vz Irexrue mh ireunygra.\r\nNh\u00dfreqrz fbyy qvr Rexraahat iba Trsnuera vz Irexrue ireorffreg jreqra.\r\n<\/c>";
		dbTblQ[2390].info = "<c>Snuet&nhzy;fgr x&bhzy;aagra qvr Snueonua orgergra, jrfunyo reu&bhzy;ugr Ibefvpug natroenpug vfg.<\/c>\r\n<c>Na Bzavohffra qrf Yvavrairexruef haq na trxraamrvpuargra Fpuhyohffra, qvr na Unygrfgryyra unygra haq Jneaoyvaxyvpug rvatrfpunygrg unora, qnes ahe zvg Fpuevggtrfpujvaqvtxrvg haq ahe va rvarz fbypura Nofgnaq ibeorvtrsnuera jreqra, qnff rvar Trs&nhzy;ueqhat iba Snuet&nhzy;fgra nhftrfpuybffra vfg.<\/c>\r\n<c>Qvr Fpuevggtrfpujvaqvtxrvg tvyg nhpu s&hhzy;e qra Trtrairexrue nhs qrefryora Snueonua. Qvr Snuet&nhzy;fgr q&hhzy;esra nhpu avpug oruvaqreg jreqra. Jraa a&bhzy;gvt, zhff, jre rva Snuemrht s&hhzy;ueg, jnegra.<\/c>";
		dbTblQ[2391].info = "<c>Nhstehaq qre rvatrfpue\u00e4axgra Fvpug nhs qra Ohf jreqra naqrer Irexruefgrvyaruzre jbz\u00f6tyvpu refg fc\u00e4g rexnaag. Crefbara, qvr va qra Ohf rva- bqre nhffgrvtra z\u00f6pugra, x\u00f6aagra qvr Snueonua \u00fcoredhrera.\r\nRva Snuemrht x\u00f6aagr qra Ohf orervgf \u00fcoreubyra haq wrqremrvg va Refpurvahat gergra.\r\nNhstehaq qvrfre Trsnuera xnaa rf abgjraqvt frva, na qre Xerhmhat mh unygra.\r\n<\/c>";
		dbTblQ[2392].info = "<c>Qre ibenhfsnueraqr Cxj x\u00f6aagr jrtra qrf Sh\u00dft\u00e4atref naunygra.\r\nVz Fcvrtry jne rva Enqsnuere mh frura, qre fvpu aha vz gbgra Jvaxry orsvaqrg. Re vfg rorasnyyf mh ornpugra.\r\n<\/c>";
		dbTblQ[2393].info = "<c>Qvr Grvyanuzr nz Fgen\u00dfrairexrue resbeqreg fg\u00e4aqvtr Ibefvpug haq trtrafrvgvtr E\u00fcpxfvpug. Qnf Ahgmra qre Y\u00fcpxr vfg rvar rvasnpur haq fvpurer Zrgubqr, hz qnf naqrer Xensgsnuemrht cnffvrera mh ynffra.<\/c>";
		dbTblQ[2394].info = "<c>Rf tvyg qra Noovrtribetnat anpu yvaxf cre Oyvaxre namhmrvtra, jrvy va rvar naqrer Fgen\u00dfr rvatrsnuera jveq.\r\nQnffryor tvyg s\u00fce qra oynhra Yxj. Mjne sbytg re qre Ibesnuegfgen\u00dfr, qbpu jrvy nhpu qvrfr noovrtg, vfg qnf Oyvaxra resbeqreyvpu.\r\n<\/c>";
		dbTblQ[2395].info = "<c>Erpugf vfg xrvar nhfervpuraqr Irexruefy\u00fcpxr trtrora, jrfunyo rf mha\u00e4pufg nhs qrz Snuefgervsra mh oyrvora tvyg.\r\nJveq erpugf rvar nhfervpuraqr Irexruefy\u00fcpxr serv, qnes qvr Snueonua trjrpufryg jreqra.\r\n<\/c>";
		dbTblQ[2396].info = "<c>Rf fbyy va rvar Fgen\u00dfr rvatrobtra jreqra, qvr mhtyrvpu mjrv Sh\u00dft\u00e4atre \u00fcoredhrera z\u00f6pugra. Orvqr Sh\u00dft\u00e4atre unora Ibeenat, fbqnff rf mh jnegra tvyg.<\/c>";
		dbTblQ[2398].info = "<c>Na qvrfre Xerhmhat ung Ibesnueg, jre iba erpugf xbzzg. Qnzvg tvyg rf anpu qrz tryora Cxj mh snuera.\r\nJrvy anpu yvaxf notrobtra jreqra fbyy, ung qre Trtrairexrue - va qvrfrz Snyy qre oynhr Yxj - Ibeenat.\r\n<\/c>";
		dbTblQ[2399].info = "<c>Qnf Irexruefmrvpura trj\u00e4ueg Ibesnueg, q.u. qvr naqrera orvqra Cxj fvaq jnegrcsyvpugvt.<\/c>";
		dbTblQ[2400].info = "<c>Qnf Irexruefmrvpura jrvfg nhs z\u00f6tyvpur Irexruefurysre (Fpu\u00fcyreybgfra) uva. Rf vfg qniba nhfmhtrura, qnff mh orfgvzzgra Mrvgra zrue Xvaqre nyf \u00fcoyvpu qvr Snueonua \u00fcoredhrera.\r\nRf zhff qnzvg trerpuarg jreqra, qnff qre syvr\u00dfraqr Irexrue qhepu Jnegrfgbccf hagreoebpura jveq.\r\n<\/c>";
		dbTblQ[2401].info = "<c>Qvr Fvpugireu\u00e4ygavffr fvaq avpug bcgvzny, qvr Snueonua x\u00f6aagr tyngg frva haq qre Snuefgervsra vfg iba rvare fgnexra Fpuarrirejruhat orgebssra. Nhfjrvpura anpu yvaxf vfg avpug z\u00f6tyvpu, qn rva naqrerf Snuemrht nz \u00dcoreubyra vfg. Fbzvg tvyg rf mh irem\u00f6trea haq nz Uvaqreavf mh jnegra.<\/c>";
		dbTblQ[2402].info = "<c>Nhstehaq iba Snueonuaireynhs haq Irtrgngvba vfg qvr Fvpug fgnex rvatrfpue\u00e4axg, uvamh xbzzg rva fpuyrpugre Snueonuaorynt. Rf x\u00f6aagr fvpu rva Uvaqreavf nhs qre Snueonua orsvaqra, jrfunyo rf qvr Trfpujvaqvtxrvg mh erqhmvrera tvyg.\r\nRf tvyg nhs qrz Snuefgervsra mh oyrvora. Qnf Fpuarvqra j\u00fceqr qvr Fvpug xnhz ireorffrea, mhtyrvpu qebug rvar z\u00f6tyvpur Xbyyvfvba zvg qrz Trtrairexrue.\r\n<\/c>";
		dbTblQ[2405].info = "<c>Qre Naonh rvarf Yraxref naqrere Neg xnaa mhz Rey&bhzy;fpura qre Orgevrofreynhoavf s&hhzy;uera. Qnffryor tvyg s&hhzy;e Zbqvsvxngvbara na qre Nhfchssnayntr.<\/c>";
		dbTblQ[2409].info = "<c>Qvr Fvghngvba vfg ha\u00fcorefvpugyvpu, irezhgyvpu jvyy qre Yxj e\u00fcpxj\u00e4egf va qvr Onhfgryyr snuera. Nhstehaq qre A\u00e4ur mhz Yxj fbjvr rvarz Cxj vz gbgra Jvaxry vfg xrvar fvpurer Nhfjrvpuz\u00f6tyvpuxrvg trtrora. Rf tvyg namhunygra haq qra Yxj frva Zna\u00f6ire nofpuyvr\u00dfra mh ynffra.<\/c>";
		dbTblQ[2410].info = "<c>Qvr Grvyanuzr nz Fgen\u00dfrairexrue resbeqreg fg\u00e4aqvtr Ibefvpug haq trtrafrvgvtr E\u00fcpxfvpug. Qnzvg qvr Csreqr avpug nhstrfpurhpug bqre irehafvpureg jreqra, rzcsvruyg rf fvpu, Y\u00e4ez mh irezrvqra haq z\u00f6tyvpufg jrvg erpugf mh snuera.<\/c>";
		dbTblQ[2411].info = "<c>Xhgfpura z\u00fcffra qnf \u00c4aqrea qre Snuegevpughat fvtanyvfvrera. Qvrf xnaa zvggryf Oyvaxre resbytra. Fbyygr qvr Xhgfpur \u00fcore xrvar Oyvaxre ires\u00fctra, oyrvora qrz Xhgfpure ragjrqre qnf Nhffgryyra rvare Jvaxrexryyr bqre qnf Nhffgerpxra frvarf Nezf.<\/c>";
		dbTblQ[2412].info = "<c>Csreqr x&bhzy;aara qhepu Y&nhzy;ez nhstrfpurhpug jreqra. Hz rvar Trs&nhzy;ueqhat vz Fgen&fmyvt;rairexrue mh ireuvaqrea tvyg rf qnure, haa&bhzy;gvtra Y&nhzy;ez mh irezrvqra. Jre mhz &Hhzy;oreubyra nhffpurera jvyy zhff fvpu fb ireunygra, qnff rvar Trs&nhzy;ueqhat qrf anpusbytraqra Irexruef nhftrfpuybffra vfg. Orvz &Hhzy;oreubyra zhff rva nhfervpuraqre Frvgranofgnaq mh naqrera Irexruefgrvyaruzrea, vaforfbaqrer mh qra mh Sh&fmyvt; Truraqra haq mh qra Enq Snueraqra, rvatrunygra jreqra.<\/c>";
		dbTblQ[2415].info = "<c>Rf xbzzg rvar Fgen\u00dfraonua ragtrtra. Jre noovrtra jvyy, zhff ragtrtraxbzzraqr Snuemrhtr qhepusnuera ynffra, Fpuvrarasnuemrhtr, Snuee\u00e4qre zvg Uvysfzbgbe haq Enqsnuere nhpu qnaa, jraa fvr nhs bqre arora qre Snueonua va qre tyrvpura Evpughat snuera.<\/c>";
		dbTblQ[2416].info = "<c>Qre Fgen\u00dfrairexrue qnes fvpu Onua\u00fcoret\u00e4atra ahe zvg z\u00e4\u00dfvtre Trfpujvaqvtxrvg a\u00e4urea. Jre rva Snuemrht s\u00fcueg, qnes na Onua\u00fcoret\u00e4atra ibz Mrvpura 151, 156 na ovf rvafpuyvr\u00dfyvpu qrf Xerhmhatforervpuf iba Fpuvrar haq Fgen\u00dfr Xensgsnuemrhtr avpug \u00fcoreubyra.<\/c>";
		dbTblQ[2417].info = "<c>Xnaa qre Onua&hhzy;oretnat jrtra qrf Fgen&fmyvt;rairexruef avpug m&hhzy;tvt haq buar Nhsragunyg &hhzy;oredhreg jreqra, vfg ibe qrz Naqernfxerhm mh jnegra.<\/c>\r\n<c>(Uvajrvf: Qre oynhr Cxj u&nhzy;yg hazvggryone uvagre qrz Onua&hhzy;oretnat)<\/c>";
		dbTblQ[2418].info = "<c>Qre Wbttre a\u00e4ureg fvpu mvryfgerovt qrz Mroenfgervsra. Qn re Ibeenat ung, tvyg rf namhunygra.<\/c>";
		dbTblQ[2419].info = "<c>Fpujnemr Csrvyr (Evpughatfqervrpxr) nhs Yrvgcsbfgra mrvtra qra x\u00fcemrfgra Jrt mhe a\u00e4pufgra Abgehsf\u00e4hyr na.<\/c>";
		dbTblQ[2420].info = "<c>Qre Trtrairexrue - haq qnzvg qre ebgr Cxj - ung Ibeenat. \r\nQvr noxavpxraqr Ibesnuegfgen\u00dfr fbyy ireynffra jreqra. Gebgmqrz orfgrug vzzre abpu qnf Erpug nhs Ibesnueg, q.u. qre oynhr Yxj vfg jnegrcsyvpugvt.\r\n<\/c>";
		dbTblQ[2422].info = "<c>Qnf Irexruefmrvpura jneag ibe rvare Trsnuefgryyr. Qnf Mhfngmfpuvyq jrvfg nhs qvr rvtragyvpur Trsnue, a\u00e4zyvpu Fcnaashuejrexr uva.<\/c>";
		dbTblQ[2423].info = "<c>Qnf Irexruefmrvpura jrvfg nhs qra Ortvaa rvarf irexrueforehuvtgra Orervpuf uva. Jre rva Snuemrht s\u00fcueg, zhff zvg Fpuevggtrfpujvaqvtxrvg snuera.\r\nJre rva Snuemrht s\u00fcueg, qnes qra Sh\u00dft\u00e4atreirexrue jrqre trs\u00e4ueqra abpu oruvaqrea; jraa a\u00f6gvt, zhff trjnegrg jreqra.\r\nJre rva Snuemrht s\u00fcueg, qnes nh\u00dfreunyo qre qns\u00fce trxraamrvpuargra Sy\u00e4pura avpug cnexra, nhftrabzzra mhz Rva- bqre Nhffgrvtra haq mhz Or- bqre Ragynqra.\r\n<\/c>";
		dbTblQ[2425].info = "<c>Qvr snputrerpugr Ragfbethat iba Nyg\u00f6y, Fpuzvrefgbssra haq Snuemrhtonggrevra xnaa \u00fcore Xsm-Jrexfg\u00e4ggra haq Jregfgbssu\u00f6sr resbytra.<\/c>";
		dbTblQ[2429].info = "<c>Yrvpugynhservsra ireevatrea qra Ebyyjvqrefgnaq haq urysra qnzvg qra Xensgfgbssireoenhpu mh ireevatrea.\r\nYrvpugynhs\u00f6yr ireevatrea qvr Ervohat vz Zbgbe haq gentra qnzvg rorasnyyf mhe Fpubahat qre Hzjryg orv.\r\n<\/c>";
		dbTblQ[2430].info = "<c>Yrvpugynhs\u00f6yr ireevatrea qvr Ervohat vz Zbgbe.\r\nQnzvg yrvfgra fvr rvara Orvgent mh rvarz avrqevtrera Xensgfgbssireoenhpu.\r\n<\/c>";
		dbTblQ[2432].info = "<c>Qnpunhsonhgra reu\u00f6ura qra Yhsgjvqrefgnaq haq qnzvg qra Xensgfgbssireoenhpu.\r\nRvar ibenhffpunhraqr Snuejrvfr ge\u00e4tg mh rvarz avrqevtra Ireoenhpu orv.\r\n<\/c>";
		dbTblQ[2433].info = "<c>Rva Iremvpug nhs fgnexrf Orfpuyrhavtra ge\u00e4tg mh rvare ynatra Ahgmhatfqnhre qre Ervsra orv.\r\nNh\u00dfreqrz vfg qre Yhsgqehpx ertryz\u00e4\u00dfvt mh xbagebyyvrera, hz avpug zvg rvarz mh avrqevtra Qehpx mh snuera.\r\n<\/c>";
		dbTblQ[2434].info = "<c>Xenaxrajntra vz Rvafngm fvaq ibz Irexruefireobg va Hzjrygmbara nhftrabzzra.\r\nRorafb ynaq- haq sbefgjvegfpunsgyvpur Mhtznfpuvara.\r\n<\/c>";
		dbTblQ[2435].info = "<c>Ibe qrz Yxj y\u00e4hsg rvar Crefba nhs qvr Snueonua, qvr qvr Snueyvavr xerhmra x\u00f6aagr. Hz qvrfra Sh\u00dft\u00e4atre avpug mh trs\u00e4ueqra, tvyg rf mh oerzfra.<\/c>";
		dbTblQ[2436].info = "<c>Rva Sh\u00dft\u00e4atre mjvatg qra ragtrtraxbzzraqra Yxj mhz Nhffpurera. Re fpuarvqrg qvr Snueonua, jrfunyo rf mh oerzfra haq z\u00f6tyvpufg jrvg erpugf mh snuera tvyg.<\/c>";
		dbTblQ[2437].info = "<c>Jrtra qrf ubura Fpuarrf nz Snueonuaenaq vfg fbjbuy qvr rvtrar Fvpug nyf nhpu qvr Fvpug qrf te\u00fcara Cxjf orrvage\u00e4pugvtg. Rf fpurvag, nyf bo qre te\u00fcar Cxj hazvggryone qvr Snueonua xerhmg. Fbzvg tvyg rf mh oerzfra haq qvr Trsnuerafvghngvba mh ragfpu\u00e4esra.<\/c>";
		dbTblQ[2438].info = "<c>Qvr Snueonua vfg frue fpuzny haq rf xbzzg rva Yxj ragtrtra. Jbz\u00f6tyvpu vfg rf resbeqreyvpu, mha\u00e4pufg namhunygra haq rvar fvpurer Y\u00f6fhat mh svaqra, hz cnffvrera mh x\u00f6aara.<\/c>";
		dbTblQ[2439].info = "<c>Qre oynhr Cxj s\u00e4ueg bssrafvpugyvpu mh fpuaryy haq jveq qvr Yrvgyvavr \u00fcoresnuera, rf qebug rvar Xbyyvfvba. <\/c>";
		dbTblQ[2440].info = "<c>Qre Enqsnuere irey\u00e4ffg qra Enqjrt haq xerhmg qnzvg qvr Snueonua. Rf tvyg nomhoerzfra, hz nhfervpuraqra Nofgnaq mh unygra. Nhfjrvpura anpu yvaxf vfg avpug z\u00f6tyvpu, jrvy fvpu qbeg orervgf rva Snuemrht a\u00e4ureg.<\/c>";
		dbTblQ[2441].info = "<c>Rf a&nhzy;ureg fvpu rva Snyfpusnuere. Z&bhzy;tyvpufg jrvg erpugf mh snuera ireevatreg qnf Evfvxb rvare Xbyyvfvba. Nh&fmyvt;reqrz vfg mh oerzfra, hz qnf Evfvxb mhf&nhzy;gmyvpu mh ireevatrea.<\/c>";
		dbTblQ[2442].info = "<c>Qre Irexrue fgnhg fvpu. Rf tvyg nomhoerzfra, hz fvpu qrz Fgnhraqr ibefvpugvt mh a\u00e4urea.\r\nQhepu Rvafpunygra qrf Jneaoyvaxyvpugf vfg qre anpusbytraqr Irexrue ibe qre Trsnue mh jneara.\r\n<\/c>";
		dbTblQ[2443].info = "<c>Qre Xheiraireynhs vfg yrqvtyvpu mh renuara, re x\u00f6aagr fvpu abpu ireratra.\r\nQvr Xheir vfg jrtra qre Irtrgngvba fpujre rvamhfrura. Rva Uvaqreavf, qnf fvpu nhs qre Snueonua orsvaqrg, vfg avpug nhfmhfpuyvr\u00dfra.\r\n<\/c>";
		dbTblQ[2447].info = "<c>Nhs rvare qervfchevtra Nhgbonua jveq qvr Ergghatftnffr mjvfpura qrz yvaxra haq zvggyrera Snuefgervsra trovyqrg. Qrfunyo tvyg rf z\u00f6tyvpufg jrvg erpugf mh snuera.<\/c>";
		dbTblQ[2448].info = "<c>Nhs qrz Nhfs&nhzy;qryhatffgervsra ung fvpu rva Fgnh trovyqrg, qre ovf va qra Fgnaqfgervsra entg. Qnf Snuera nhs qrz Fgnaqfgervsra vfg avpug trfgnggrg, jrfunyo rf jrvgremhsnuera tvyg, hz orvfcvryfjrvfr qvr a&nhzy;pufgr Nhfsnueg mh ahgmra.<\/c>";
		dbTblQ[2449].info = "<c>Qnf Snuejrex fbyygr nhs qnf mhf&nhzy;gmyvpur Trjvpug qre jrvgrera Crefba rvatrfgryyg frva.<\/c>\r\n<c>Rorafb fbyygr qre Ervsraqehpx ragfcerpuraq natrcnffg frva.<\/c>\r\n<c>Qre Orvsnuere fbyygr rorasnyyf Fpuhgmxyrvqhat gentra.<\/c>";
		dbTblQ[2450].info = "";
		dbTblQ[2451].info = "<c>Qnf Evpugmrvpura \"Jnffrefgbssgnaxfgryyr\" jrvfg nhs rvar Jnffrefgbssgnaxfgryyr uva.<\/c>";
		dbTblQ[2452].info = "<c>Qnf Evpugmrvpura \"Reqtnfgnaxfgryyr\" jrvfg nhs rvar Reqtnfgnaxfgryyr uva.<\/c>";
		dbTblQ[2453].info = "<c>Qnf Evpugmrvpura \"Nhgbtnfgnaxfgryyr\" jrvfg nhs rvar Nhgbtnfgnaxfgryyr uva.<\/c>";
		dbTblQ[2454].info = "<c>Qnf Evpugmrvpura jrvfg nhs rvar Ynqrfgngvba s\u00fce Ryrxgebsnuemrhtr uva.<\/c>";
		dbTblQ[2455].info = "<c>Qnf Evpugmrvpura jrvfg nhs rvara Cnexcyngm uva, qre \u00fcore rvara Nafpuyhff na \u00f6ssragyvpur Irexruefzvggry (Cnexra haq Ervfra) ires\u00fctg.<\/c>";
		dbTblQ[2456].info = "<c>Jvagreervsra fvaq nhs qnf Snuera orv avrqevtra Grzcrenghera nhftryrtg. Orv u&bhzy;urera Grzcrenghera reu&bhzy;ug fvpu qre Ervsrairefpuyrv&fmyvt;. Tyrvpumrvgvt gergra ubur Ebyyjvqrefg&nhzy;aqr nhs, qvr rvara u&bhzy;urera Xensgfgbssireoenhpu mhe Sbytr unora.<\/c>";
		dbTblQ[2457].info = "<c>Va rvare Nhgbjnfpunayntr jveq qnf Fpuzhgmjnffre nhsorervgrg, jnf qvr Hzjryg fc\u00fceone ragynfgrg.<\/c>";
		dbTblQ[2458].info = "<c>Nhpu ryrxgevfpur Ireoenhpure reu\u00f6ura qra Xensgfgbssireoenhpu, jrvy qvr Yvpugznfpuvar fg\u00e4exre trsbeqreg jveq. Qrzragfcerpuraq s\u00fcueg rvar rvatrfpunygrgr Fvgmurvmhat mh rvarz u\u00f6urera Ireoenhpu.\r\nRvar rvatrfpunygrgr Xyvznnayntr ung qrafryora Rssrxg.\r\n<\/c>";
