		dbTblQ[2908].info = "<c>Qre fvpu a&nhzy;ureaqr Ohf x&bhzy;aagr nhffpujraxra haq qnzvg Vuer Snueonuafrvgr orfpue&nhzy;axra.<\/c>\r\n<c>Zvg Crefbara qvr uvagre rvarz Ohf qvr Snueonua orgerggra vfg vzzre mh erpuara.<\/c>\r\n<c>Qre Dhnq xnaa avpug fbsbeg ybfsnuera, qn rvar Crefba qverxg qnibe fgrug.<\/c>";
		dbTblQ[2909].info = "<c>Orv Enqsnuerea zhff vpu qnzvg erpuara, qnff fvr Rvaonuafgen&fmyvt;ra va orvqr Evpughatra ahgmra, orv Enqjrtra nhs qre snyfpura Frvgr snuera haq orv Qhaxryurvg xrvar Oryrhpughat unora.<\/c>";
		dbTblQ[2910].info = "<c>Qre Enqsnuere zhff jrtra qra Onhneorvgra nhs qrz Trujrt nhs qvr Snueonua nhfjrvpura.<\/c>\r\n<c>Qre Nofgnaq mjvfpura Trujrt haq Fgen&fmyvt;raonua vfg fb trevat, qnff rva &Hhzy;oreubyra avpug z&bhzy;tyvpu vfg. Vpu zhff qrfunyo oerzfra.<\/c>\r\n<c>Rvar Unygrfgryyr qre Fgen&fmyvt;raonua vfg avpug va Fvpug.<\/c>";
		dbTblQ[2911].info = "<c>Nhpu nyf Zbsnsnuere zhff vpu vzzre zvg Sruyireunygra naqrere Irexruefgrvyaruzre erpuara. Nyyr qerv Nagjbegra fvaq evpugvt.<\/c>";
		dbTblQ[2912].info = "<c>Ibe qrz Rvabeqara haq abpuznyf ibe qrz Noovrtra vfg nhs qra anpusbytraqra Irexrue mh npugra.<\/c>";
		dbTblQ[2913].info = "<c>Orvz Jraqra nhs qre Snueonua fvaq Fvr rva Uvaqreavf. Orv Arory, ibe Xheira haq ibe Xhccra x&bhzy;aara Fvr refg fc&nhzy;g iba naqrera Irexruef&ful;grvy&ful;aruzrea rexnaag jreqra haq fgryyra qrfjrtra rvar Trsnue qne.<\/c>";
		dbTblQ[2914].info = "<c>Jre noovrtra jvyy, zhff rag&ful;trtra&ful;xbzzraqr Snuemrhtr qhepusnuera ynffra. Qrfunyo zhff vpu jnegra haq ovrtr refg anpu qrz Yxj haq qrz qnuvagre snueraqra Zbgbeenq no.<\/c>";
		dbTblQ[2915].info = "<c>Jre anpu yvaxf noovrtra jvyy, qnes fvpu nhs y&nhzy;atf ireyrtgra Fpuvrara ahe rvabeqara, jraa xrva Fpuvrara&ful;snuemrht oruvaqreg jveq. Qrfunyo z&hhzy;ffra Fvr jnegra.<\/c>";
		dbTblQ[2917].info = "<c>Qvrf vfg rvar Sentr ahe s&hhzy;e qvr Xynffr <fgebat>Zbsn<\/fgebat>! Fvr snuera nyfb nhs rvarz Zbsn.<\/c>\r\n<c>Qre Yxj ireu&nhzy;yg fvpu snyfpu, gebgmqrz jnegr vpu hz rvara Hasnyy mh irezrvqra.<\/c>\r\n<c>Trtrorara&ful;snyyf tror vpu rva Jnea&ful;mrvpura.<\/c>";
		dbTblQ[2918].info = "<c>Gnkra q&hhzy;esra, jraa qvr Irexruefyntr rf mhy&nhzy;ffg, arora naqrera Snuemrhtra, qvr nhs qrz Frvgrafgervsra bqre nz erpugra Snueonuaenaq unygra bqre cnexra, Snuet&nhzy;fgr rva- bqre nhffgrvtra ynffra.<\/c>";
		dbTblQ[2919].info = "<c>Orv Onua&hhzy;ore&ful;t&nhzy;atra zvg fpujnpura Irexruef&ful;nhs&ful;xbzzra vfg xrvar grpuavfpur Fvpurehat jvr Yvpug&ful;mrvpura bqre Fpuenaxra ibe&ful;trfpuevrora.<\/c>\r\n<c>Nyyr qerv Nagjbegra fvaq qrfunyo evpugvt.<\/c>";
		dbTblQ[2920].info = "<c>Jraa Cbyvmrv bqre Ergghatf&ful;jntra fvpu zvg Oynhyvpug haq Znegvaf&ful;ubea a&nhzy;urea, z&hhzy;ffra Fvr vuara fbsbeg Cyngm fpunssra. Orv rvare ebgra Nzcry q&hhzy;esra Fvr va qvrfre Fvghngvba ibefvpugvt qvr Unygr&ful;yvavr &hhzy;oresnuera.<\/c>\r\n<c>Jraa rva Cbyvmvfg qra Irexrue ertryg, qnaa fvaq s&hhzy;e qvr ragfcerpuraqr Irexruef&ful;fvghngvba nyyr fbafgvtra Ertryhatra jvr Fpuvyqre haq Nzcrya nh&fmyvt;re Xensg trfrgmg.<\/c>";
		dbTblQ[2921].info = "<c>Orvz Noovrtra ung qre ragtrtraxbzzraqr Irexrue Ibeenat. Fbzvg tvyg rf, qnf tryor Snuemrht qhepusnuera mh ynffra.<\/c>\r\n<c>Qre ebgr Cxj orsvaqrg fvpu nhs qre Ibesnuegfgen&fmyvt;r, qrfunyo ovrtr vpu refg <fgebat>anpu<\/fgebat> qrz ebgra Cxj na.<\/c>";
		dbTblQ[2922].info = "<c>Qnf Irexruefmrvpura jneag ibe rvare Trsnuefgryyr. Rf jrvfg nhs qvr Trsnue qhepu Csreqr haq Ervgre uva, qvr fvpu nhs qre Snueonua orsvaqra bqre qvrfr xerhmra.<\/c>";
