		dbTblQ[928].info = "<c>Gur oenxvat qvfgnapr vf 16 z nppbeqvat gb gur ehyr bs guhzo naq gur ernpgvba qvfgnapr vf 12 z, juvpu erfhygf va n fgbccvat qvfgnapr bs 28 z. Gurersber, gur pebffvat fubhyq or pebffrq dhvpxyl.<\/c>";
		dbTblQ[929].info = "<c>Gur oenxvat qvfgnapr vf 16 z nppbeqvat gb gur ehyr bs guhzo naq gur ernpgvba qvfgnapr vf 12 z, juvpu erfhygf va n fgbccvat qvfgnapr bs 28 z. Pbafvqrevat gur qvfgnapr gb gur genssvp yvtug, gur qevire fubhyq fgbc.<\/c>";
		dbTblQ[930].info = "<c>Gur genssvp yvtug pna fjvgpu gb lryybj ng nal gvzr. Gurersber, vg vf vzcbegnag gb xrrc n tbbq rlr ba gur genssvp yvtug. Gur zbgbevfg fubhyq or ernql gb oenxr vs arprffnel naq gb oevat gur iruvpyr gb n fgnaqfgvyy.<\/c>";
		dbTblQ[933].info = "<c>Vapernfr nggragvba naq vapernfr gur qvfgnapr gb gur iruvpyr va sebag. Vs lbh frr n genssvp wnz, fybj qbja naq fjvgpu ba gur unmneq jneavat yvtugf.<\/c>";
		dbTblQ[934].info = "<c>Gur fvta nyregvat bs n cbgragvnyyl qnatrebhf \u201cXheir\u201d jneaf bs na harkcrpgrq be gvtug oraq. Lbh zhfg erqhpr lbhe fcrrq fb gung ab rkprffvir pragevshtny sbeprf bpphe va gur oraq. Va nqqvgvba, qevir sne gb gur evtug va beqre gb cerirag cbffvoyr pbyyvfvbaf jvgu bapbzvat genssvp.<\/c>";
		dbTblQ[935].info = "<c>Gur qnatre fvta \"Jvyqjrpufry\" jneaf bs jvyq navznyf gung znl ragre gur ebnq. Vg vf arprffnel gb nqwhfg gur fcrrq gb or ernql gb oenxr va beqre gb fgbc gur iruvpyr va gvzr va na rzretrapl. Jura fvtugvat jvyq navznyf, lbh zhfg qvz lbhe yvtugf naq fybj qbja. Lbh fubhyq abg fjreir va beqre gb nibvq gur navzny vs guvf pna or nibvqrq, orpnhfr pbyyvfvbaf jvgu bapbzvat genssvp znl bpphe.<\/c>";
		dbTblQ[936].info = "<c>Gur genssvp fvta \"Cebuvovgvba sbe iruvpyrf bs nyy glcrf\" cebuvovgf gur pbagvahngvba bs gur wbhearl. Ubjrire, n zbcrq znl or chfurq.<\/c>";
		dbTblQ[937].info = "<c>Gur genssvp fvta cerfpevorf gur hfr bs fabj punvaf. Gur znkvzhz nhgubevfrq fcrrq sbe zbgbe iruvpyrf rdhvccrq jvgu fabj punvaf vf 50 xz\/u, rira haqre gur zbfg snibhenoyr pbaqvgvbaf.<\/c>";
		dbTblQ[938].info = "<c>Vs n znff fhpu nf '7,5 g' vf vaqvpngrq ba na nqqvgvbany fvta, gur cebuvovgvba funyy nccyl bayl vs gur znkvzhz nhgubevfrq znff bs fhpu zrnaf bs genafcbeg rkprrqf gur yvzvg vaqvpngrq. Vs gur znff vf abg vaqvpngrq ba na nqqvgvbany fvta, ohg vg vf vaqvpngrq ba gur cevznel fvta, gur npghny znff nccyvrf.<\/c>";
		dbTblQ[939].info = "<c>Vs gur znff vf fcrpvsvpnyyl vaqvpngrq ba gur genssvp fvta, gur npghny jrvtug bs gur iruvpyr be pbzovangvba nccyvrf. Vs n flzoby vf fubja, ubjrire, gur crezvffvoyr gbgny jrvtug nccyvrf.<\/c>";
		dbTblQ[940].info = "<c>Gur flzoby bs na nkyr ba gur genssvp fvta vaqvpngrf gung gur znff fcrpvsvpngvba qbrf abg ersre gb gur gbgny znff bs gur iruvpyr, ohg gb gur nkyr ybnq. Vs gur znff vf abg vaqvpngrq ba na nqqvgvbany fvta, ohg vg vf vaqvpngrq ba gur cevznel genssvp fvta, gur npghny nkyr ybnq nccyvrf.<\/c>";
		dbTblQ[941].info = "<c>Gur jvqgu bs gur cnffntr vf yvzvgrq. Gur jvqgu bs gur iruvpyr funyy abg rkprrq 2 zrgerf. Jura qrgrezvavat gur jvqgu bs gur iruvpyr, gur rkgrevbe zveebef zhfg or gnxra vagb nppbhag.<\/c>";
		dbTblQ[942].info = "<c>Gur cnffntr vf yvzvgrq va urvtug. Gur genssvp fvta vaqvpngrf gur urvtug gung zhfg abg or rkprrqrq ol gur iruvpyr naq vgf ybnq.<\/c>";
		dbTblQ[943].info = "<c>Gur genssvp fvta cebuvovgf gur cnegvpvcngvba va genssvp bs iruvpyrf jubfr qvzrafvbaf vapyhqvat ybnq rkprrq gur fcrpvsvrq npghny yvzvg bs 10 zrgerf. Gur cebuvovgvba nccyvrf gb iruvpyr pbzovangvbaf sbe gur gbgny yratgu. Gur gehpx fubja freirf bayl nf n flzoby; gur genssvp fvta nyfb nccyvrf gb bgure iruvpyrf naq pbzovangvbaf.<\/c>";
		dbTblQ[944].info = "";
		dbTblQ[945].info = "";
		dbTblQ[946].info = "";
		dbTblQ[947].info = "";
		dbTblQ[948].info = "<c>Gur \"Raq bs gur nhgbonua\" genssvp fvta vaqvpngrf gur raq bs gur nhgbonua.<\/c>";
		dbTblQ[949].info = "<c>Gur \"Raq bs gur pyrnejnl sbe zbgbe iruvpyrf bayl\" genssvp fvta vaqvpngrf gur raq bs gur pyrnejnl sbe zbgbe iruvpyrf bayl.<\/c>";
		dbTblQ[950].info = "<c>Gur \"Nhgbonuara\" genssvp fvta vaqvpngrf gur ahzore bs gur erfcrpgvir nhgbonua.<\/c>";
		dbTblQ[951].info = "<c>Gur \"nhgbonua whapgvbaf\" genssvp fvta vaqvpngrf gur ahzore bs gur nhgbonua rkvg.<\/c>";
		dbTblQ[952].info = "<c>Gur \"qvirefvba vf erdhverq\" fvta vaqvpngrf na nygreangvir ebhgr va gur fhobeqvangr ebnq argjbex orgjrra zbgbejnl whapgvbaf.<\/c>";
		dbTblQ[953].info = "<c>Gur fvta tvivat n gbja be ivyyntrf anzr genssvp fvta vaqvpngrf gur ortvaavat bs n ohvyg-hc nern. N znkvzhz fcrrq bs 50 xz\/u vf crezvggrq. Bayl iruvpyrf jvgu n crezvffvoyr gbgny znff bs hc gb 3.5 g unir serr pubvpr bs ynarf.<\/c>";
		dbTblQ[954].info = "<c>Gur fvta tvivat n gbja be ivyyntrf anzr genssvp fvta vaqvpngrf gur ortvaavat bs n ohvyg-hc nern. Fbhaq naq yvtug fvtanyf znl bayl or tvira ol gubfr jub biregnxr bhgfvqr ohvyg-hc nernf be jub frr gurzfryirf be bguref raqnatrerq. Va ohvyg-hc nernf, vg vf fhssvpvrag gb znex bayl gur fvqr bs gur iruvpyr snpvat gur ebnq jvgu cnexvat ynzcf be bgure nccebirq zrnaf; frcnengr yvtugvat vf abg erdhverq vs gur fgerrg yvtugvat znxrf gur iruvpyr pyrneyl ivfvoyr ng n fhssvpvrag qvfgnapr.<\/c>";
		dbTblQ[955].info = "<c>Gur \"Cnexvat ba fvqrjnyxf\"  fvta crezvgf iruvpyrf jvgu n crezvffvoyr gbgny znff bs 2.8 g (naq guhf nyfb pneninaf nf n ehyr) gb cnex va gur znaare fubja.<\/c>";
		dbTblQ[957].info = "<c>Gur neebj vaqvpngrf gur qverpgvba va juvpu gur arkg rzretrapl pnyy gryrcubar vf ybpngrq.<\/c>";
		dbTblQ[958].info = "<c>Punatrf gb gur vagnxr naq rkunhfg flfgrzf pna trarengr haarprffnel abvfr. Qevivat ng uvtu fcrrq vf abvfl naq fubhyq guhf or nibvqrq.<\/c>";
		dbTblQ[963].info = "<c>N qrsrpgvir rkunhfg flfgrz trarengrf haarprffnel abvfr. Gur fnzr nccyvrf gb cynlvat jvgu gur guebggyr, orpnhfr vg haarprffnevyl nppryrengrf gur ratvar gb uvtu erif naq guhf envfrf gur abvfr yriry.<\/c>";
		dbTblQ[964].info = "<c>Jura fgnegvat funecyl, gur zbgbe vf dhvpxyl oebhtug gb n uvtu fcrrq, juvpu trarengrf haarprffnel abvfr. Gur gler jrne vf vapernfrq, juvpu vf abg arprffnel.<\/c>";
		dbTblQ[969].info = "<c>N pngnylgvp pbairegre vf guerngrarq jvgu qrfgehpgvba vs haohearq shry sybjf vagb vg. Guvf pna unccra jura gbjvat gur iruvpyr. Gur evfx nyfb rkvfgf va gur pnfr bs ahzrebhf snvyrq fgneg nggrzcgf.<\/c>";
		dbTblQ[972].info = "<c>Nibvqvat genssvp crnxf zrnaf nibvqvat \"fgbc-naq-tb genssvp\" be serdhrag fgbccvat naq fgnegvat, guhf erqhpvat shry pbafhzcgvba. Nibvqvat ebnqf jvgu erthyne genssvp wnzf unf gur fnzr rssrpg. Pnecbbyvat erfhygf va srjre iruvpyrf ba gur ebnqf naq guhf eryvrirf gur oheqra ba gur raivebazrag.<\/c>";
		dbTblQ[973].info = "<c>Ebbs enpxf naq fxv enpxf vapernfr nve erfvfgnapr naq guhf vapernfr shry pbafhzcgvba. Hahfrq vgrzf vapernfr gur iruvpyr jrvtug naq guhf unir n artngvir rssrpg ba pbafhzcgvba.<\/c>";
		dbTblQ[974].info = "<c>Ebbs enpxf naq fxv enpxf vapernfr nve erfvfgnapr naq guhf shry pbafhzcgvba. Gur fnzr nccyvrf gb glerf jubfr cerffher vf abg pbeerpgyl nqwhfgrq.<\/c>";
		dbTblQ[976].info = "<c>Pnecbbyf ner raivebazragnyyl fbhaq orpnhfr gurl erqhpr gur ahzore bs iruvpyrf ba gur ebnq. Fjvgpuvat bss gur ratvar va genssvp wnzf ceriragf haarprffnel pbzohfgvba bs shry. Pbyyrpgvir cnexvat nernf ner n tbbq jnl gb frg hc pnecbbyf be hfr choyvp genafcbeg.<\/c>";
		dbTblQ[978].info = "<c>Qevivat va uvtu trnef erfhygf va ybj erif naq guhf erqhprf abvfr trarengvba.<\/c>";
		dbTblQ[983].info = "<c>Gb vqragvsl n zbgbe iruvpyr, gur fgvpxre zhfg or nssvkrq ivfvoyl gb gur vafvqr bs gur jvaqfperra. Gur fgvpxre fubhyq or nssvkrq gb gur nern ba gur ybjre evtug (sebz gur qevire'f crefcrpgvir).<\/c>";
		dbTblQ[986].info = "";
		dbTblQ[987].info = "";
		dbTblQ[989].info = "";
		dbTblQ[990].info = "<c>Zbgbeplpyrf zhfg or fhozvggrq sbe na bssvpvny vafcrpgvba rirel gjb lrnef.<\/c>";
		dbTblQ[993].info = "<c>Gur npghny hfr unf ab vasyhrapr ba gur qngr bs gur znva vafcrpgvba. Gur byq qngr erznvaf inyvq.<\/c>";
		dbTblQ[994].info = "";
		dbTblQ[998].info = "<c>Gur svggvat bs n fvqrpne naq gur hfr bs glerf bgure guna gubfr fcrpvsvrq va gur qbphzragf jvyy nssrpg gur qevivat orunivbhe bs gur iruvpyr naq znl erfhyg va gur  pnapryyngvba bs gur bcrengvat yvprapr.<\/c>";
		dbTblQ[999].info = "<c>Gur glcr nccebiny vf gur crezvffvba sebz na nhgubevgl gb bcrengr zbgbe iruvpyrf naq gurve rdhvczrag.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg znl rkcver vs bar bs gur sbyybjvat guerr cbvagf nccyvrf:<\/c>\r\n<hy>\r\n<yv>gur iruvpyr glcr nccebirq va gur glcr-nccebiny vf zbqvsvrq,<\/yv>\r\n<yv>n qnatre gb ebnq hfref vf gb or rkcrpgrq<\/yv>\r\n<yv>gur rkunhfg be abvfr orunivbhe qrgrevbengrf.<\/yv>\r\n<\/hy>\r\n<c>Gur vafgnyyngvba bs na ratvar jvgu n qvssrerag cbjre bhgchg pna yrnq gb gur rkcvengvba bs gur glcr nccebiny. Gur fnzr nccyvrf gb gur vafgnyyngvba bs n snvevat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><\/c>";
		dbTblQ[1003].info = "<c>Gur yvprapr funyy or inyvq sbe zbgbeplpyrf (vapyhqvat fvqrpnef) jvgu n plyvaqre pncnpvgl abg rkprrqvat 125 pz&fhc3; naq na ratvar cbjre abg rkprrqvat 11 xJ va juvpu gur engvb bs cbjre gb jrvtug qbrf abg rkprrq 0,1 xJ\/xt.<\/c>";
		dbTblQ[1005].info = "<c>Jvgubhg yvnovyvgl vafhenapr, cnegvpvcngvba va ebnq genssvp vf abg crezvggrq.<\/c>";
		dbTblQ[1006].info = "<c>Jvgubhg yvnovyvgl vafhenapr, cnegvpvcngvba va choyvp ebnq genssvp vf abg crezvggrq. Gur fnzr nccyvrf gb n zbcrq gung vf abg ebnqjbegul.<\/c>";
		dbTblQ[1007].info = "<c>Gur zvavzhz ntr sbe qevivat n zbgbe iruvpyr sbe juvpu n yvprapr vf abg erdhverq vf 15 lrnef bs ntr.<\/c>";
		dbTblQ[1009].info = "<c>Zbqvsvpngvbaf bs gur fvyrapre ner abg crezvggrq naq jvyy ibvq gur bcrengvat crezvg. Gur fnzr nccyvrf gb n punatr va gur genafyngvba engvb.<\/c>";
		dbTblQ[1010].info = "<c>Zbgbevfgf ner boyvtrq gb pneel n crezvg sbe hfr jvgu gurz. Gur fnzr nccyvrf gb gur grfg pregvsvpngr. Rivqrapr bs vafhenapr zhfg nyfb or pneevrq.<\/c>";
		dbTblQ[1011].info = "<c>Sbe gur cnegvpvcngvba va choyvp ebnq genssvp, yvnovyvgl vafhenapr zhfg or gnxra bhg.<\/c>";
		dbTblQ[1012].info = "<c>Gur rkvfgrapr bs guveq-cnegl yvnovyvgl vafhenapr pna or irevsvrq ba gur ahzore cyngr. Gur fnzr nccyvrf gb gur vafhenapr pregvsvpngr.<\/c>";
		dbTblQ[1013].info = "<c>Gur vafhenapr vaqvpngbe vf inyvq sbe n znkvzhz bs bar lrne.<\/c>";
		dbTblQ[1019].info = "<c>Va gur pnfr bs n pngrtbel O qevivat yvprapr, gur gbgny crezvffvoyr znff bs gur pne naq genvyre znl abg rkprrq 3,500 xt. Va guvf pnfr, bayl n genvyre jvgu n znkvzhz crezvffvoyr gbgny znff bs 1,100 xt pna or pbafvqrerq (3,500 xt - 2,400 xt = 1,100 xt). Nppbeqvatyl, gur genvyre jvgu n crezvffvoyr gbgny znff bs 1,000 xt zhfg or fryrpgrq.<\/c>";
