		dbTblQ[1272].info = "<c>Cynl va gur fgrrevat urnq ornevat pna unir na rkgerzryl artngvir rssrpg ba fgrrevat orunivbhe naq zhfg guhf or ryvzvangrq.<\/c>";
		dbTblQ[1273].info = "<c>N ybpxvat cbvag va gur fgrrevat urnq ornevat pna unir na rkgerzryl artngvir rssrpg ba fgrrevat orunivbhe naq zhfg guhf or ryvzvangrq.<\/c>";
		dbTblQ[1274].info = "<c>Cynl va gur fgrrevat urnq ornevat pna unir na rkgerzryl artngvir rssrpg ba fgrrevat orunivbhe naq zhfg guhf or ryvzvangrq.<\/c>";
		dbTblQ[1275].info = "<c>Vs n fgrrevat qnzcre vf qrsrpgvir, vg znl pnhfr gur zbgbeplpyr gb fuvzzl. Gurersber, gurer vf gur cbffvovyvgl gung gur qevivat fnsrgl jvyy or erqhprq.<\/c>";
		dbTblQ[1276].info = "<c>Cynl va gur fgrrevat urnq ornevat pna unir na rkgerzryl artngvir rssrpg ba fgrrevat orunivbhe naq zhfg guhf or ryvzvangrq. Gur fnzr nccyvrf gb n jbea bhg fgrrevat urnq ornevat naq qrsrpgvir fgrrevat qnzcref.<\/c>";
		dbTblQ[1277].info = "<c>N qrsyrpgrq onpx sbex pna or erfcbafvoyr sbe gur cbbe genpx orunivbhe. Nabgure pnhfr pna or gbb zhpu cynl va gur jurry ornevat.<\/c>";
		dbTblQ[1278].info = "<c>N orag sbex pna or erfcbafvoyr sbe gur onq genpxvat orunivbhe. N orag senzr pna or nabgure pnhfr bs gur onq genpxvat orunivbhe.<\/c>";
		dbTblQ[1279].info = "<c>Gbb zhpu cynl va gur sbex ornevat znl or erfcbafvoyr sbe gur cbbe genpxvat orunivbhe. Nabgure pnhfr pna or gbb zhpu cynl va gur erne jurry ornevat.<\/c>";
		dbTblQ[1281].info = "<c>N fvtavsvpnagyl ybatre yrire geniry jvgu n ulqenhyvp oenxr vaqvpngrf n qrsrpg. N ercnve zhfg or pneevrq bhg.<\/c>";
		dbTblQ[1283].info = "<c>Gb rafher fnsr cnegvpvcngvba va genssvp, gur ybbfr zveebe ubyqre zhfg or ernggnpurq.<\/c>";
		dbTblQ[1285].info = "<c>Gur gernq qrcgu pna or qrgrezvarq qhevat gler vafcrpgvba. Gur fnzr nccyvrf gb jurgure gur gernq cnggrea vf jbea rirayl. Gur ntr pna nyfb or ernq sebz gur gler.<\/c>";
		dbTblQ[1289].info = "<c>Qvssrerag iruvpyrf znl or hasnzvyvne, vapyhqvat gurve oenxvat orunivbhe. Ol zrnaf bs na rneyl oenxr grfg, na vavgvny srryvat sbe gur oenxr flfgrz pna or qrirybcrq. Nsgre n cebybatrq fgnaqvat gvzr, gurer vf n evfx bs erqhprq oenxvat cbjre, juvpu zhfg or qrgrpgrq va n gvzryl znaare.<\/c>";
		dbTblQ[1290].info = "<c>N erthyne purpx bs gur nve cerffher vf arprffnel gb nibvq qevivat ng gbb ybj n cerffher. Rkgreany qnzntr fubhyq nyfb or qrgrpgrq ng na rneyl fgntr, bgurejvfr guvf pbhyq yrnq gb gler ohefgvat. Erthyne purpxvat bs gur gernq cnggrea fubhyq rafher gung gur gler qbrf abg snyy orybj gur zvavzhz gernq qrcgu.<\/c>";
		dbTblQ[1296].info = "<c>N ehfgrq rkunhfg flfgrz trarengrf haarprffnel abvfr gung pna naabl bgure crbcyr. Gurer vf n evfx gung cnegf bs gur rkunhfg flfgrz znl pbzr ybbfr naq snyy bagb gur ebnq juvyr qevivat, cbgragvnyyl raqnatrevat bgure zbgbevfgf.<\/c>";
		dbTblQ[1297].info = "<c>Na hahfhnyyl ybhq rkunhfg znl vaqvpngr n ceboyrz fhpu nf n qrsrpg qhr gb jrne. Pbafrdhragyl, gur rkunhfg flfgrz fubhyq or purpxrq.<\/c>";
		dbTblQ[1298].info = "<c>Vs gur ubea qbrf abg jbex, gur pbageby qrivpr be gur pbagnpg ba gur fgrrevat jurry be unaqyrone znl or qrsrpgvir. Vg vf nyfb cbffvoyr gung n shfr znl unir oybja.<\/c>";
		dbTblQ[1301].info = "<c>Vs gur oenxr npgf fgebatyl ba bar fvqr, yngrenyyl npgvat pragevshtny sbeprf pna bpphe gung pnhfr gur iruvpyr gb irre bhg. Ng gur fnzr gvzr, gur oenxvat cbjre vf erqhprq, juvpu vapernfrf gur oenxvat qvfgnapr.<\/c>";
		dbTblQ[1303].info = "<c>Gurer frrzf gb or n frevbhf qrsrpg va gur oenxr flfgrz. Fvapr fnsr cnegvpvcngvba va genssvp vf abg cbffvoyr va guvf jnl, gur iruvpyr zhfg or cnexrq vzzrqvngryl. Gura gur qrsrpg zhfg or cebsrffvbanyyl ercnverq.<\/c>";
		dbTblQ[1305].info = "<c>Qrsrpgvir oenxr yvtugf vzcnve fnsr cnegvpvcngvba va ebnq genssvp. Gurersber, gur yvtugf zhfg or ercnverq vzzrqvngryl.<\/c>";
		dbTblQ[1306].info = "<c>Vs gur yvtug ohyo bs n synfure vf qrsrpgvir, guvf vf vaqvpngrq ol gur vaqvpngbe yvtug synfuvat snfgre.<\/c>";
		dbTblQ[1307].info = "<c>Gur bapbzvat genssvp frrzf gb or qnmmyrq. Guvf znl or pnhfrq ol vapbeerpgyl nqwhfgrq urnqyvtugf. Vg vf nyfb cbffvoyr gung gur urnqynzc yriryyvat pbageby vf frg vapbeerpgyl be unf abg orra nqncgrq gb gur ybnq pbaqvgvba.<\/c>";
		dbTblQ[1308].info = "<c>Gur yrsg bhgfvqr zveebe vf rffragvny sbe gur qevire naq guhf znaqngbel. Nal qnzntr zhfg or ercnverq.<\/c>";
		dbTblQ[1310].info = "<c>Vs gurer vf ab jngre va gur fgbentr gnax, gur flfgrz pnaabg shapgvba. Pybttrq abmmyrf pna nyfb pnhfr gur jvaqfperra jnfure flfgrz gb znyshapgvba. Vs gur shfr vf qrsrpgvir, gur chzc bs gur jvaqfperra jnfure flfgrz qbrf abg jbex.<\/c>";
		dbTblQ[1311].info = "<c>Gbb yvggyr jngre vaqvpngrf oybpxrq abmmyrf bs gur jvaqfperra jnfure flfgrz. Vg vf nyfb cbffvoyr gung gur jngre srrq chzc vf qrsrpgvir.<\/c>";
		dbTblQ[1312].info = "<c>N cbbe erfhyg vf hfhnyyl qhr gb jbea jvcre oynqrf. Gurer znl nyfb or na bvy svyz ba gur jvaqfperra.<\/c>";
		dbTblQ[1313].info = "<c>N cbffvoyr pnhfr bs znyshapgvbavat jvaqfperra jvcref znl or n qrsrpgvir jvcre zbgbe. N oybja shfr vf nabgure cbffvoyr pnhfr bs znyshapgvbavat jvaqfperra jvcref .<\/c>";
		dbTblQ[1314].info = "<c>Gur frng oryg zhfg or noyr gb or snfgrarq naq zhfg nyfb shapgvba va bgure jnlf. Gurersber, n ercnve vf nofbyhgryl arprffnel.<\/c>";
		dbTblQ[1318].info = "<c>Qnzntrq fhfcrafvba pna yrnq gb rkprffvir gler jrne. Gur fnzr nccyvrf gb vapbeerpg gler cerffher.<\/c>";
