		dbTblQ[1489].info = "<c>Gur svefg gvzr n qevivat yvprapr vf npdhverq, vg vf vffhrq ba n cebongvbanel  onfvf. Gur cebongvbanel crevbq vf or gjb lrnef sebz gur qngr bs vffhr.<\/c>";
		dbTblQ[2073].info = "";
		dbTblQ[2074].info = "<c>Gur crezvffvoyr fcrrq sbe pnef jvgu genvyref bhgfvqr bs ohvyg-hc nernf vf 80 xz\/u.<\/c>";
		dbTblQ[2075].info = "<c>Gur znkvzhz nhgubevfrq fcrrq, rira va gur zbfg snibhenoyr pvephzfgnaprf bhgfvqr bs ohvyg-hc nernf, vf 80 xz\/u sbe<\/c>\r\n<c>(n) zbgbe iruvpyrf jvgu n znkvzhz nhgubevfrq znff rkprrqvat 3.5 gbaarf ohg abg rkprrqvat 7.5 gbaarf, rkpyhqvat cnffratre pnef;<\/c>\r\n<c>(o) cnffratre pnef jvgu genvyref,<\/c>\r\n<c>(p) ybeevrf naq zbgbe pneninaf, rnpu hc gb n znkvzhz nhgubevfrq znff bs 3.5 gbaarf jvgu genvyref; naq<\/c>\r\n<c>(q) ohfrf naq pbnpurf, jvgu be jvgubhg yhttntr genvyref.<\/c>";
		dbTblQ[2076].info = "";
		dbTblQ[2078].info = "";
		dbTblQ[2083].info = "<c>Gur puvyq fgnaqvat ba gur evtug orgjrra gur cnexrq iruvpyrf znl harkcrpgrqyl evqr gb gur jbzna ba gur yrsg fvqr bs gur ebnq. Vg vf arprffnel gb fybj qbja gb jnyxvat fcrrq naq gb erznva ernql gb oenxr.<\/c>";
		dbTblQ[2085].info = "<c>Crqrfgevnaf frrz gb jnag gb pebff gur ynar ng gur mroen pebffvat naq guhf unir cevbevgl - rira vs gurl ner ba gur bccbfvgr fvqr.<\/c>\r\n<c>Ol oenxvat rneyl, lbh fvtany gung crqrfgevnaf pna ragre gur ebnq.<\/c>";
		dbTblQ[2086].info = "<c>Vg vf cbffvoyr gung crqrfgevnaf pbzvat sebz gur evtug jvyy pebff gur ebnq gb trg gb gur genz. Cnffratref trggvat bss gur ohf sebz gur yrsg pbhyq nyfb pebff gur ebnq.<\/c>";
		dbTblQ[2087].info = "<c>Orpnhfr gur gehpx vf yrnivat gur cevbevgl ebnq naq gheavat vagb nabgure ynar, vg vf yvxryl gung gur gehpx jvyy tvir jnl gb gur crqrfgevna.<\/c>\r\n<c>Vg vf nyfb cbffvoyr gung gur crqrfgevna jvyy svefg yrg gur gehpx cnff naq gura pebff gur ebnq.<\/c>\r\n<c>Gur gehpx erfgevpgf ivfvovyvgl, juvpu zrnaf gung bgure iruvpyrf ba gur ebnq pnaabg or frra lrg.<\/c>";
		dbTblQ[2088].info = "<c>Gurer ner ahzrebhf crqrfgevnaf jub pbhyq pebff gur ebnq. Gur iruvpyrf cnexrq ba obgu fvqrf bofgehpg ivfvovyvgl, juvpu zrnaf, sbe rknzcyr, gung crqrfgevnaf pebffvat gur ebnq pna bayl or frra ng n yngr fgntr.<\/c>\r\n<c>Cnffratref pbhyq trg bhg bs gur cnexrq iruvpyrf ng nal gvzr naq bcra gur qbbef.<\/c>";
		dbTblQ[2089].info = "<c>Puvyqera pbhyq nccrne orgjrra gur iruvpyrf ng nal gvzr - gur genssvp fvta rira rkcyvpvgyl jneaf bs guvf qnatre. Va nqqvgvba gb puvyqera, nqhygf pbhyq nyfb eha bagb gur ebnq.<\/c>\r\n<c>Qhr gb gur qrafryl cnexrq iruvpyrf, crqrfgevnaf naq bgure zbgbevfgf znl bayl or ivfvoyr ng n yngr fgntr, fb gung urnil oenxvat znl or arprffnel ng nal gvzr.<\/c>";
		dbTblQ[2090].info = "<c>Ernpgvivgl naq frafbel creprcgvba qrpernfr jvgu vapernfvat ntr. Guvf vf jul byqre crqrfgevnaf bsgra ernpg zber fybjyl naq ner yrff zbovyr.<\/c>\r\n<c>Gurl bsgra zvfwhqtr gur fcrrqf bs iruvpyrf. Yvxrjvfr, gurl znl abg frr be urne nf jryy.<\/c>";
		dbTblQ[2091].info = "<c>Va erfvqragvny nernf jvgu yvggyr genssvp, gurer vf na vapernfrq cebonovyvgl gung zbgbevfgf jvyy haqrerfgvzngr gur evfxf.<\/c>\r\n<c>Gurersber, plpyvfgf naq bgure zbgbevfgf znl or vanggragvir.<\/c>\r\n<c>Vg pnaabg or ehyrq bhg gung puvyqera znl or cynlvat arne gur ebnq naq cbffvoyl ragre vg.<\/c>";
		dbTblQ[2092].info = "<c>Gur orunivbhe bs crqrfgevnaf vf qvssvphyg gb cerqvpg. Hayvxr zbgbevfgf, gurl pna ghea nebhaq zhpu zber rnfvyl unysjnl npebff gur ebnq.<\/c>\r\n<c>Fbzr crqrfgevnaf eryl rkpyhfviryl ba gurve urnevat; gurersber, gurl ybbx arvgure gb gur yrsg abe gb gur evtug. Crqrfgevnaf pna ragre gur ebnq harkcrpgrqyl ng nal gvzr.<\/c>";
		dbTblQ[2093].info = "<c>Juvgr fgvpxf freir nf na bevragngvba nvq sbe crbcyr jvgu ivfhny vzcnvezragf. Ivfhnyyl vzcnverq crbcyr unir zhpu zber qvssvphygl nffrffvat gur genssvp fvghngvba. Pbafvqrengvba zhfg or fubja, v.r., lbh zhfg erqhpr lbhe fcrrq naq fgbc vs arprffnel.<\/c>";
		dbTblQ[2094].info = "<c>Gur cnirzragf ner qvssvphyg gb frr qhr gb gur cnexrq iruvpyrf. Crqrfgevnaf pbhyq ragre gur ebnq ng nal gvzr. Puvyqera pbhyq nyfb nccrne orgjrra gur iruvpyrf ng nal gvzr. Gur genssvp fvta rkcyvpvgyl jneaf bs guvf qnatre.<\/c>";
		dbTblQ[2095].info = "<c>Gur cnirzragf ner qvssvphyg gb frr qhr gb gur cnexrq iruvpyrf. Crqrfgevnaf pbhyq ragre gur ebnq ng nal gvzr.<\/c>\r\n<c>Yvxrjvfr, crbcyr ragrevat be yrnivat iruvpyrf pbhyq bcra gur qbbef ng nal gvzr. Frireny iruvpyrf pbhyq or nccebnpuvat ng nal zbzrag.<\/c>";
		dbTblQ[2096].info = "<c>Gur crqrfgevna qvq abg ybbx onpx naq guhf qvq abg xrrc na rlr ba gur pbzvat genssvp. Fur pbhyq fgrc bagb gur ebnq ng nal gvzr. Gur vagragvba bs gur fpbbgre qevire vf abg pyrne; gurer vf n evfx gung ur znl ghea nebhaq be znl fhqqrayl pebff gur ebnq.<\/c>";
		dbTblQ[2097].info = "<c>Gur lryybj nezonaq vaqvpngrf gung gur crefba vf ivfhnyyl vzcnverq. Gurer vf guhf n fcrpvny qhgl bs pbaqhpg va genssvp: vapernfrq pbafvqrengvba bs guvf crefba.<\/c>";
		dbTblQ[2099].info = "<c>Lbh ner nobhg gb ghea bagb n ebnq jvgu n ebnqjnl gung vf boivbhfyl vpl. Bapbzvat iruvpyrf oenxvat ng gur zbhgu bs gur vagrefrpgvba pbhyq fxvq.<\/c>\r\n<c>Lbhe bja iruvpyr pbhyq nyfb fjreir be fxvq juvyr gheavat gur pbeare, fb na nccebcevngr fcrrq vf erdhverq. Gur fyvccrel ebnq nyfb erfhygf va n ybatre oenxvat qvfgnapr.<\/c>";
		dbTblQ[2101].info = "<c>Cbbe fgerrg yvtugvat znxrf vg rnfvre gb bireybbx crbcyr ba gur ebnq. Vs iruvpyrf ner cbbeyl yvg, gurl ner qvssvphyg gb qrgrpg.<\/c>";
		dbTblQ[2103].info = "<c>Va haqretebhaq pne cnexf, crqrfgevnaf ner bsgra ba gur iruvpyr ynarf. Qrfcvgr gur yvtugvat, vg pna or eryngviryl qnex va na haqretebhaq pne cnex pbzcnerq gb gur cerivbhf raivebazrag, fb gung gur rlrf unir gb trg hfrq gb gur arj yvtugvat pbaqvgvbaf svefg.<\/c>\r\n<c>Aneebj ragenaprf naq rkvgf ner abg hapbzzba va haqretebhaq tnentrf. Glerf pna or qnzntrq ol pbagnpg jvgu xreof be jnyyf.<\/c>";
		dbTblQ[2104].info = "<c>Fpurqhyrq ohfrf naq znexrq fpubby ohfrf fgbccrq ng ohf fgbcf jvgu unmneq jneavat yvtugf ghearq ba znl bayl or biregnxra ng jnyxvat fcrrq naq ng fhpu n qvfgnapr gung cnffratref ner abg raqnatrerq.<\/c>\r\n<c>Jnyxvat fcrrq nyfb nccyvrf gb bapbzvat genssvp ba gur fnzr ebnq. Ohf cnffratref zhfg abg or bofgehpgrq. Vs arprffnel, nalbar qevivat n iruvpyr zhfg jnvg.<\/c>";
		dbTblQ[2114].info = "<c>Qhr gb gur fabj-pbirerq ebnq fhesnpr, n ybatre oenxvat qvfgnapr pna or nffhzrq. Ng gur fnzr gvzr, gurer vf na vapernfrq evfx bs fyvccvat qhr gb gur jvagre ebnq pbaqvgvbaf.<\/c>\r\n<c>Gur plpyvfg vf nyfb nssrpgrq ol gur qvssvphyg pbaqvgvbaf, juvpu pna erfhyg va hafnsr evqvat.<\/c>";
		dbTblQ[2116].info = "<c>Biregnxvat zhfg abg bpphe va guvf pnfr, orpnhfr biregnxvat erdhverf fhssvpvrag yngreny pyrnenapr sebz bgure zbgbevfgf, va cnegvphyne crqrfgevnaf naq plpyvfgf.<\/c>\r\n<c>Gur crefba biregnxvat unf gb zbir onpx gb gur evtug nf fbba nf cbffvoyr. Zbgbevfgf zhfg abg bofgehpg gur iruvpyr gung vf gb or biregnxra.<\/c>";
		dbTblQ[2119].info = "<c>Gur bapbzvat qevire jnagf gb biregnxr gur plpyvfg, juvpu ur unf fvtanyyrq. Vg vf gb or rkcrpgrq gung ur jvyy chyy bhg naq qevir bire gur qvivqvat yvar va gur zvqqyr bs gur ebnq.<\/c>";
		dbTblQ[2120].info = "<c>Gur plpyr cngu raqf naq ng gur fnzr gvzr gheaf vagb n sbbgcngu gung znl abg or hfrq ol plpyvfgf. Gur plpyvfg vf yvxryl gb fjvgpu gb gur ebnq. Ur znl unir gb jnvg, ohg vs ur qbrf abg jnvg, gurer vf n qnatre. Pbagvahvat gb qevir gur pne va n crefvfgrag znaare znl erfhyg va n pbyyvfvba vs gur plpyvfg qbrf abg fgbc.<\/c>";
		dbTblQ[2128].info = "<c>Gur erirefvat yvtugf bs gur oyhr pne fvtany gung vg pbhyq erirefr naq guhf phg vagb gur ebnq. Gurersber, vg vf vzcbegnag gb erqhpr lbhe bja fcrrq naq gb or ernql gb oenxr.<\/c>";
		dbTblQ[2129].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner evqvat be qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Orpnhfr gurer zvtug or nabgure plpyvfg be zbgbevfg jub pnaabg or frra va gur zveebe, ybbxvat bire lbhe fubhyqre vf arprffnel.<\/c>";
		dbTblQ[2130].info = "<c>Gur crefba orfvqr gur ina pbhyq fgrc onpxjneqf naq gurerol perngr n qnatre. Gur crqrfgevna pbhyq qrpvqr gb pebff gur ebnq va sebag bs gur genafcbegre. Vg vf gb or rkcrpgrq gung bgure crqrfgevnaf jvyy ragre gur ynar ng nal gvzr be gung iruvpyrf gung ner fgnaqvat fgvyy jvyy fgneg qevivat. Guvf vf jul gur zbgbeplpyvfg pbhyq chyy bhg.<\/c>";
		dbTblQ[2131].info = "<c>Gur pne pbhyq pbagvahr gb erirefr orpnhfr vg jnagf gb yrnir gur cnexvat fcnpr naq ragre gur ebnq. Gur zbgbeplpyr znl unir gb oenxr fhqqrayl orpnhfr bs gur pne. Gur oenxvat qvfgnapr pbhyq vapernfr qhr gb gur jvagre ebnq pbaqvgvbaf.<\/c>";
		dbTblQ[2133].info = "<c>Va gur rirag bs na nppvqrag, zbgbevfgf evqvat n gjb-jurryre unir na vapernfrq evfx bs vawhel. Qhr gb gurve pbzcnpg qvzrafvbaf, gjb-jurryref ner bireybbxrq zber bsgra va pbzcnevfba gb bgure zbgbe iruvpyrf. Orpnhfr bs gurve qvzrafvbaf, gur fcrrq bs gjb-jurryref vf nyfb bsgra haqrerfgvzngrq.<\/c>";
		dbTblQ[2134].info = "<c>N fnsr punatr gb gur evtug ynar vf ab ybatre cbffvoyr. Erznva va gur ynar naq ghea yrsg.<\/c>";
		dbTblQ[2135].info = "<c>Va gur rirag bs na nppvqrag, oernxqbja be nal bgure qnatrebhf fvghngvba, lbh arrq gb xabj ubj naq jurer lbh pna cnex naq yrnir gur iruvpyr va gur ghaary. Vg vf vzcbegnag gb xabj ubj rfpncr ebhgrf ner znexrq va gur ghaary va beqre gb zbir va gur evtug qverpgvba va na rzretrapl.<\/c>";
		dbTblQ[2136].info = "<c>Fjvgpuvat bss gur ratvar erqhprf gur evfx bs gur iruvpyr zbivat va na hapbagebyyrq znaare. Fjvgpuvat ba gur unmneq jneavat yvtugf urycf bgure zbgbevfgf gb erpbtavfr gur cbgragvnyyl qnatrebhf fvghngvba rneyvre. Gur vtavgvba xrl erznvaf va gur vtavgvba fb gung gur ratvar pna or erfgnegrq dhvpxyl vs arprffnel.<\/c>";
		dbTblQ[2137].info = "<c>Gur sver nynez zhfg or gevttrerq fb gung gur sver oevtnqr naq, vs arprffnel, bgure erfphref pna znxr gurve jnl gb gur nern va qnatre. Vs gur fvghngvba nyybjf, gel gb rkgvathvfu gur sver lbhefrys. Vs guvf vf abg cbffvoyr be vs n ybg bs fzbxr qrirybcf, gur ghaary zhfg or rkvgrq ivn gur arnerfg rfpncr ebhgr.<\/c>";
		dbTblQ[2138].info = "<c>Nalbar qevivat n iruvpyr zhfg fubj cnegvphyne pner gbjneqf puvyqera, gur qvfnoyrq naq gur ryqreyl, va cnegvphyne ol erqhpvat gur fcrrq bs gur iruvpyr naq ol orvat ernql gb oenxr.<\/c>";
		dbTblQ[2139].info = "<c>Vs gur yvar qryvzvgf n cnirq fvqr fgevc, ntevphygheny genpgbef naq jbex iruvpyrf, pnegf naq fvzvyne fybj iruvpyrf (vapyhqvat zbcrqf) zhfg qevir nf sne gb gur evtug bs gur ynar nf cbffvoyr.<\/c>";
		dbTblQ[2144].info = "<c>Qeviref bs fybjre iruvpyrf zhfg erqhpr gurve fcrrq ng n fhvgnoyr cynpr. Vs arprffnel, gurl fubhyq jnvg sbe frireny iruvpyrf vzzrqvngryl sbyybjvat gb biregnxr gur fybjre iruvpyr vs guvf vf cbffvoyr. Fhvgnoyr fvqr fgevcf pna nyfb or hfrq sbe guvf checbfr. Guvf qbrfa'g nccyl gb uvtujnlf.<\/c>";
		dbTblQ[2157].info = "<c>Ba ebnqf jvgu frireny ynarf sbe bar qverpgvba, vs vg vf abg cbffvoyr gb qevir pbagvahbhfyl ba bar ynar be vs bar ynar raqf, gur iruvpyrf ceriragrq sebz pbagvahvat fubhyq or nyybjrq gb cnff bire gb gur nqwnprag ynar va fhpu n jnl gung gurfr iruvpyrf pna nygreangr jvgu n iruvpyr geniryyvat ba gur pbagvahbhf ynar vzzrqvngryl orsber gur aneebjvat ortvaf (mvccre srrq-va zrgubq).<\/c>";
		dbTblQ[2163].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl qevir ba be orfvqr gur ebnq va gur fnzr qverpgvba.<\/c>";
		dbTblQ[2164].info = "<c>Orsber ragrevat n ynar naq ntnva orsber gheavat, cnl nggragvba gb gur genssvp oruvaq lbhe iruvpyr. Vs lbh jnag gb ghea, lbh zhfg vaqvpngr guvf pyrneyl naq va n gvzryl znaare ol hfvat gur vaqvpngbef.<\/c>";
		dbTblQ[2165].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Nalbar qevivat n iruvpyr zhfg fgbc ng gur fgbc fvta naq tvir jnl.<\/c>";
		dbTblQ[2167].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Fcrpvny pbafvqrengvba zhfg or tvira gb crqrfgevnaf. Vs arprffnel, jnvg.<\/c>";
		dbTblQ[2168].info = "<c>Jura gheavat yrsg, zvfhaqrefgnaqvatf jvgu bguref gheavat yrsg pnaabg or ehyrq bhg. Vg vf cbffvoyr gung jura lbh ghea yrsg, lbh jvyy pbzr irel pybfr gb gur bapbzvat genssvp gung vf nyfb gheavat yrsg. Ivfvovyvgl erfgevpgvbaf qhr gb bapbzvat genssvp ner cbffvoyr.<\/c>";
		dbTblQ[2169].info = "<c>Nalbar jub jnagf gb ragre gur ebnq sebz n genssvp-pnyzrq nern be nccebnpu sebz gur rqtr bs gur ebnq zhfg orunir va fhpu n jnl gung gurer vf ab qnatre gb bgure zbgbevfgf. Gur vagragvba gb ragre gur ebnq be fgneg gur iruvpyr zhfg or fvtanyyrq pyrneyl naq va n gvzryl znaare ol hfvat gur qverpgvba vaqvpngbef.<\/c>";
		dbTblQ[2170].info = "<c>Nalbar jvfuvat gb ragre gur ebnq sebz n genssvp-pnyzrq nern zhfg orunir va fhpu n jnl nf gb nibvq raqnatrevat bgure hfref bs gur ebnq. Gurersber, vg vf arprffnel sbe zbgbevfgf gb jnvg sbe plpyvfgf.<\/c>";
		dbTblQ[2171].info = "<c>Nalbar jub jnagf gb qevir vagb gur ebnq sebz n cebcregl be sebz gur rqtr bs gur ebnq zhfg orunir va fhpu n jnl gung gurer vf ab qnatre gb bgure hfref bs gur ebnq. Gurersber, vg vf arprffnel sbe zbgbevfgf gb jnvg sbe gur plpyvfgf.<\/c>";
		dbTblQ[2172].info = "<c>Nalbar jub jnagf gb qevir bagb gur ebnq sebz n cebcregl be sebz bgure cnegf bs gur ebnq zhfg orunir va fhpu n jnl gung gurer vf ab qnatre gb bgure hfref bs gur ebnq. Vs arprffnel, lbh zhfg yrg lbhefrys or qverpgrq.<\/c>";
		dbTblQ[2173].info = "<c>Nalbar jub jnagf gb qevir vagb gur ebnq sebz n cebcregl be sebz gur rqtr bs gur ebnq zhfg orunir va fhpu n jnl gung gurer vf ab qnatre gb bgure hfref bs gur ebnq. Fb jr unir gb jnvg hagvy gur crqrfgevnaf unir cnffrq. Vs gur crqrfgevnaf erabhapr gurve evtug bs jnl, vg vf cbffvoyr gb qevir ba gur ebnq.<\/c>";
		dbTblQ[2174].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Nf n genz vf nccebnpuvat, lbh unir gb jnvg naq xrrc gur genssvp vfynaq serr.<\/c>";
		dbTblQ[2175].info = "<c>Nalbar jub jnagf gb biregnxr unf gb orunir va fhpu n jnl gung gurer vf ab qnatre gb gur genssvp oruvaq uvf be ure iruvpyr. Jura biregnxvat, fhssvpvrag yngreny pyrnenapr zhfg or znvagnvarq sebz bgure hfref bs gur ebnq, va cnegvphyne sebz crqrfgevnaf naq plpyvfgf. Orpnhfr gur plpyvfg vf tbvat gb chyy bhg, vg vf arprffnel gb fybj qbja naq qevir oruvaq uvz.<\/c>";
		dbTblQ[2177].info = "<c>Vs gurer vf genssvp pbatrfgvba, lbh zhfg abg qevir vagb gur vagrefrpgvba be whapgvba qrfcvgr gur evtug bs jnl be terra yvtug fvtany. Gur fvtaf naq vafgehpgvbaf bs cbyvpr bssvpref ner gb or sbyybjrq. Gurl gnxr cerprqrapr bire nyy bgure beqref naq bgure ehyrf, ohg gurl qb abg eryrnfr gur zbgbevfg sebz gur qhgl gb cnl nggragvba.<\/c>";
		dbTblQ[2179].info = "<c>Fgbccvat vf abg crezvggrq ba zretvat naq qrzretvat ynarf. Nalbar qevivat n iruvpyr vf abg nyybjrq gb fgbc ba gur cneg bs gur ebnq gung vf znexrq jvgu neebjf.<\/c>";
		dbTblQ[2180].info = "<c>Nalbar qevivat n iruvpyr zhfg abg fgbc ba crqrfgevna pebffvatf be hc gb 5 z va sebag bs gurz.<\/c>";
		dbTblQ[2181].info = "<c>Vs gur erq synfuvat yvtug unf gur funcr bs na neebj, bayl gubfr jub jnag gb qevir va gur qverpgvba bs gur neebj unir gb jnvg.<\/c>";
		dbTblQ[2182].info = "<c>Gur fcrrq yvzvg nyybjf lbh gb nccebnpu gur yriry pebffvat ng n znkvzhz fcrrq bs 10 xz\/u. Orpnhfr gur yvzvgngvba vf inyvq hagvy nsgre gur envy pebffvat, gur pebffvat zhfg or genirefrq jvgu znkvzhz 10 xz\/u.<\/c>";
		dbTblQ[2184].info = "<c>Fpurqhyrq ohfrf, genzf naq znexrq fpubby ohfrf fgbccvat ng ohf fgbcf znl bayl or biregnxra pnershyyl, rira va gur rirag bs bapbzvat genssvp. Jura cnffratref ner obneqvat be qvfrzonexvat, iruvpyrf znl bayl cnff ba gur evtug fvqr ng n jnyxvat fcrrq naq bayl ng fhpu n qvfgnapr gung gurer vf ab qnatre gb cnffratref. Gurl zhfg nyfb abg or bofgehpgrq. Vs arprffnel, nalbar qevivat n iruvpyr zhfg jnvg.<\/c>";
		dbTblQ[2185].info = "<c>Fpurqhyrq ohfrf, genzf naq znexrq fpubby ohfrf fgbccvat ng ohf fgbcf znl bayl or biregnxra pnershyyl, rira va gur rirag bs bapbzvat genssvp. Jura cnffratref ner obneqvat be qvfrzonexvat, iruvpyrf znl bayl cnff ba gur evtug fvqr ng n jnyxvat fcrrq naq bayl ng fhpu n qvfgnapr gung gurer vf ab qnatre gb cnffratref. Gurl zhfg nyfb abg or bofgehpgrq. Vs arprffnel, nalbar qevivat n iruvpyr zhfg jnvg.<\/c>";
		dbTblQ[2186].info = "<c>Gur ybnq zhfg abg vagresrer jvgu gur qevire ng nal gvzr. Vg zhfg or fb jryy frpherq gung vg qbrf abg fyvc rira qhevat na rzretrapl fgbc.<\/c>";
		dbTblQ[2187].info = "<c>Gur ybnq znl abg cebwrpg va sebag bs lbhe ireuvpyr vs vg vf fznyre guna 2,50z. Vs guvf vf abg gur pnfr lbhe ybnq zl cebwrpg hc gb 50pz va sebag bs lbhe iruvpyr.<\/c>";
		dbTblQ[2188].info = "<c>Nalbar qevivat n iruvpyr znl abg hfr n zbovyr be pne gryrcubar vs gur zbovyr gryrcubar be gur unaqfrg bs gur pne gryrcubar zhfg or cvpxrq hc be uryq sbe guvf checbfr. Guvf qbrf abg nccyl vs gur iruvpyr vf fgngvbanel naq gur ratvar vf fjvgpurq bss va gur pnfr bs zbgbe iruvpyrf. Bcrengvat gur cubar pna qvfgenpg lbh sebz gur genssvp. Rira gur pbairefngvba vgfrys pna or qvfgenpgvat.<\/c>";
		dbTblQ[2189].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Fcrpvny pbafvqrengvba zhfg or tvira gb crqrfgevnaf. Vs arprffnel, jnvg.<\/c>";
		dbTblQ[2190].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Fcrpvny pbafvqrengvba zhfg or tvira gb crqrfgevnaf. Vs arprffnel, jnvg.<\/c>";
		dbTblQ[2191].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Fcrpvny pbafvqrengvba zhfg or tvira gb crqrfgevnaf. Vs arprffnel, jnvg.<\/c>";
		dbTblQ[2192].info = "<c>Vs nabgure iruvpyr vf nyernql jnvgvat ng gur crqrfgevna pebffvat, vg vf arprffnel gb nccebnpu vg pnershyyl. Vs crqrfgevnaf jvfu gb pebff gur pebffvat, gurl fubhyq nccebnpu ng zbqrengr fcrrq be jnvg vs arprffnel. N pnershy nccebnpu vf nyfb arprffnel va gur rirag bs yvzvgrq ivfvovyvgl bs gur pebffjnyx.<\/c>";
		dbTblQ[2194].info = "<c>Ng crqrfgevna pebffvatf, iruvpyrf, jvgu gur rkprcgvba bs envy iruvpyrf, funyy ranoyr crqrfgevnaf naq jurrypunve hfref jub jvfu gb hfr gur pebffvat va n erpbtavfnoyr znaare gb pebff gur pneevntrjnl. Gura gurl znl bayl nccebnpu ng n zbqrengr fcrrq. Vs arprffnel, gurl zhfg jnvg. Biregnxvat zhfg abg bpphe.<\/c>";
		dbTblQ[2195].info = "<c>Ng crqrfgevna pebffvatf, iruvpyrf, jvgu gur rkprcgvba bs envy iruvpyrf, funyy ranoyr crqrfgevnaf naq jurrypunve hfref jub jvfu gb hfr gur pebffvat va n erpbtavfnoyr znaare gb pebff gur pneevntrjnl. Gura gurl znl bayl nccebnpu jvgu zbqrengr fcrrq. Vs arprffnel, gurl zhfg jnvg. Vs genssvp vf ng n fgnaqfgvyy, iruvpyrf znl abg qevir bagb gur pebffjnyx vs gurl jbhyq unir gb jnvg ba vg.<\/c>";
		dbTblQ[2196].info = "<c>Crqrfgevna pebffvatf zhfg or nccebnpurq ng n zbqrengr fcrrq. Guvf jnl, gur fnsrgl bs crqrfgevnaf vf abg wrbcneqvmrq. Ng gur fnzr gvzr, gur evfx bs n cbffvoyr erne-raq pbyyvfvba vf erqhprq.<\/c>";
		dbTblQ[2197].info = "<c>Gur gehpx erfgevpgf gur ivrj bs gur crqrfgevna pebffvat. Gurersber, vg vf arprffnel gb nccebnpu gur pebffvat ng n zbqrengr fcrrq.<\/c>";
		dbTblQ[2198].info = "<c>Cnegvpvcngvba va ebnq genssvp erdhverf pbafgnag pnhgvba naq zhghny erfcrpg. Nalbar gnxvat cneg va genssvp zhfg orunir va fhpu n jnl gung ab bar ryfr vf unezrq, raqnatrerq be unenffrq. Nygubhtu be cerpvfryl orpnhfr gur plpyvfg jbhyq unir gb qvfzbhag, qrsrafvir orunivbhe vf erdhverq, v.r., gur plpyvfg zhfg or nyybjrq gb cnff.<\/c>";
		dbTblQ[2199].info = "<c>Vs genssvp vf ng n fgnaqfgvyy, iruvpyrf zhfg xrrc gur pebffjnyx serr bs genssvp.<\/c>";
		dbTblQ[2200].info = "<c>Nsgre n genssvp nppvqrag, jubrire vf vaibyirq va vg zhfg fgbc vzzrqvngryl, frpher gur genssvp naq, va gur rirag bs zvabe qnzntr, vzzrqvngryl qevir gb gur fvqr bs gur ebnq. Lbh zhfg fgngr gung lbh unir orra vaibyirq va gur nppvqrag naq, ba erdhrfg, lbh zhfg fgngr lbhe anzr naq nqqerff, fubj lbhe qevivat yvprapr naq ertvfgengvba qbphzrag naq tvir qrgnvyf bs lbhe pvivy yvnovyvgl vafhenapr gb gur orfg bs lbhe xabjyrqtr.<\/c>";
		dbTblQ[2201].info = "<c>Vs n zhygv-ynar iruvpyr fgbcf ng n cbvag jurer vg pnaabg or qrgrpgrq va gvzr nf n fgngvbanel bofgnpyr, unmneq yvtugf zhfg or fjvgpurq ba vzzrqvngryl. Gurernsgre, ng yrnfg bar pbafcvphbhf jneavat fvta funyy or cynprq ng n fhssvpvrag qvfgnapr naq va n pyrneyl ivfvoyr znaare, nobhg 100 z sebz gur fgbccrq iruvpyr.  Cerfpevorq fnsrgl zrnfherf, fhpu nf n jneavat gevnatyr, funyy or hfrq. Va nqqvgvba, gur erthyngvbaf ba gur yvtugvat bs iruvpyrf gbjvat bgure iruvpyrf nccyl. Vs arprffnel, bgure zbgbevfgf zhfg or jnearq, r.t., ol unaq fvtanyf.<\/c>";
		dbTblQ[2202].info = "<c>Gur terra neebj ng gur genssvp yvtug nyybjf lbh gb ghea evtug.<\/c>";
		dbTblQ[2203].info = "<c>Qhr gb gur fcrrq naq gur fubeg qvfgnapr gb gur genssvp yvtug, vg jbhyq or qvssvphyg gb fgbc va gvzr. Oenxvat jbhyq or qnatrebhf, rfcrpvnyyl orpnhfr bs gur gehpx oruvaq lbhe iruvpyr.<\/c>";
		dbTblQ[2204].info = "<c>N terra neebj gb gur yrsg oruvaq gur vagrefrpgvba vaqvpngrf gung bapbzvat genssvp vf fgbccrq ng n erq yvtug naq gung vs lbh ghea yrsg, lbh pna ragre naq yrnir gur vagrefrpgvba hauvaqrerq va gur qverpgvba bs gur terra neebj.<\/c>";
		dbTblQ[2206].info = "<c>Qhr gb gur fcrrq naq qvfgnapr gb gur genssvp yvtug, fnsr oenxvat vf cbffvoyr. Fb jr unir gb fgbc. <\/c>";
		dbTblQ[2207].info = "<c>Na rzretrapl iruvpyr jvgu synfuvat yvtugf vf nccebnpuvat. Gur oyhr yvtug beqref: \"Nyy bgure zbgbevfgf zhfg vzzrqvngryl pyrne gur jnl\". Nf ybat nf gurer vf ab qnatre gb bgure zbgbevfgf, gur iruvpyr znl or qevira shegure nurnq rira ntnvafg n erq yvtug va beqre gb ranoyr gur rzretrapl iruvpyr gb pbagvahr vgf wbhearl.<\/c>";
		dbTblQ[2208].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Gurersber, gur zbgbeplpyvfg unf cevbevgl. Ubjrire, ur zhfg lvryq gb gur plpyvfg. Gur plpyvfg orybatf gb gur bapbzvat genssvp naq guhf unf cevbevgl.<\/c>";
		dbTblQ[2209].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb zbgbevfgf pbzvat sebz gur evtug. Gur erq gehpx unf gb jnvg. Gur fnzr tbrf sbe gur plpyvfg jub, nobhg gb znxr n yrsg-unaq ghea, zhfg lvryq gb bapbzvat genssvp.<\/c>";
		dbTblQ[2210].info = "<c>Gur bapbzvat plpyvfg pbhagf nf bapbzvat genssvp naq guhf unf cevbevgl. Nalbar jub vf bgurejvfr nyybjrq gb pbagvahr qevivat nppbeqvat gb gur genssvp ehyrf be unf cevbevgl ryfrjurer zhfg ersenva sebz cebprrqvat vs gur genssvp fvghngvba fb erdhverf. Va guvf pnfr, vg vf arprffnel gb jnvg, orpnhfr gur ynar vf oybpxrq ol gur fgbccrq tneontr iruvpyr.<\/c>";
		dbTblQ[2211].info = "<c>Ng pebffebnqf naq whapgvbaf, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Guvf qbrf abg nccyl gb iruvpyrf pbzvat sebz n qveg ebnq be sberfg ebnq gb nabgure ebnq.<\/c>";
		dbTblQ[2212].info = "<c>Gur genssvp fvta \"Tenag evtug bs jnl\" erthyngrf gur evtug bs jnl. Gurer vf na boyvtngvba gb jnvg sbe gur plpyvfg.<\/c>";
		dbTblQ[2213].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Gur plpyvfg znl guhf ghea svefg. Cevbevgl zhfg nyfb or tvira gb gur oyhr pne.<\/c>";
		dbTblQ[2215].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Gur oyhr gehpx guhf zhfg jnvg. Svefg bs nyy, gur plpyvfg zhfg or tvira cevbevgl, orpnhfr vs lbh jnag gb ghea, lbh unir gb yrg bapbzvat iruvpyrf naq plpyvfgf cnff guebhtu gur vagrefrpgvba svefg, rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba.<\/c>";
		dbTblQ[2216].info = "<c>Orpnhfr bs gur FGBC fvta, lbh naq gur ohf zhfg fgbc ng gur fgbc yvar naq tvir jnl gb gur plpyvfg. Vs lbh ghea yrsg, lbh unir gb jnvg naq tvir jnl gb gur bapbzvat genssvp \u2013 va guvf pnfr, gur ohf.<\/c>";
		dbTblQ[2217].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Gurersber, gur plpyvfg unf cevbevgl. Nsgrejneqf, lbh pna cebprrq, naq gur zbgbeplpyvfg unf gb jnvg.<\/c>";
		dbTblQ[2218].info = "<c>Nalbar jub unf gb bofreir gur evtug bs jnl zhfg znxr vg pyrne gung ur be fur jvyy jnvg naq znxr guvf pyrne va n gvzryl znaare ol uvf be ure qevivat orunivbhe, va cnegvphyne ol qevivat ng n zbqrengr fcrrq. Ur be fur znl bayl pbagvahr qevivat vs vg vf pyrne gung gur crefba jub unf gur evtug bs jnl jvyy abg or vzcrqrq. Vs guvf vf abg pyrne orpnhfr gur fgerrg frpgvba vf hapyrne, ur be fur znl pnershyyl qevir vagb gur vagrefrpgvba be whapgvba hagvy gur ivrj vf pyrne. Rira vs gur crefba jub unf gur evtug bs jnl gheaf vagb gur bgure ebnq, gur crefba boyvtrq gb jnvg zhfg abg bofgehpg gur bgure zbgbevfg fvtavsvpnagyl.<\/c>";
		dbTblQ[2219].info = "<c>Nf lbh ner jnagvat gb ghea yrsg ba gur cevbevgl ebnq, bapbzvat genssvp &aqnfu; naq guhf gur oyhr gehpx &aqnfu; zhfg or tvira cevbevgl. Pbzvat sebz gur cevbevgl ebnq, gurer vf cevbevgl bire gur terra pne, fb gung gur terra pne vf boyvtrq gb jnvg.<\/c>";
		dbTblQ[2220].info = "<c>Gur guerr-fgevcrq ornpba vaqvpngrf gung gurer vf n yriry pebffvat nccebk. 240 z njnl.<\/c>";
		dbTblQ[2223].info = "<c>Gur genssvp fvta pbzovangvba jneaf bs n yriry pebffvat va nccebk. 240 z qvfgnapr. Biregnxvat vf abg crezvggrq hc gb naq vapyhqvat gur vagrefrpgvba bs envy naq ebnq.<\/c>";
		dbTblQ[2224].info = "<c>Gur qnatre fvta jneaf bs n pbafgehpgvba fvgr. Gur nqqvgvbany fvta vaqvpngrf gur qvfgnapr gb gur pbafgehpgvba fvgr, juvpu vf nccebk. 100 z.<\/c>";
		dbTblQ[2230].info = "<c>Gur genssvp fvta vaqvpngrf n yriry pebffvat.<\/c>";
		dbTblQ[2231].info = "<c>Gur genssvp fvta vaqvpngrf n funec evtug ghea. Vg vf cbffvoyr gung iruvpyrf va sebag qevir vagb gur oraq gbb snfg naq gura fhqqrayl oenxr funecyl. Sebz gur cbvag bs ivrj bs bapbzvat genssvp, guvf vf n yrsg-unaq oraq gung znl or phg.<\/c>";
		dbTblQ[2232].info = "<c>Gur genssvp fvta vaqvpngrf plpyvfgf jub ner zretvat bagb gur ebnq be pebffvat vg, r.t., orpnhfr n plpyr cngu raqf be pebffrf gur ebnq.<\/c>";
		dbTblQ[2235].info = "<c>Plpyvfgf znl abg hfr gur ebnq. Plpyvfgf zhfg hfr gur plpyr cngu. (Gurer vf na boyvtngvba gb hfr gur plpyr cngu.)<\/c>";
		dbTblQ[2236].info = "<c>Gur genssvp fvta \"Unyg. Tenag evtug bs jnl\" nccrnef 100 z nurnq.<\/c>";
		dbTblQ[2237].info = "<c>Bayl crqrfgevnaf znl hfr gur fvqrjnyx.<\/c>";
		dbTblQ[2238].info = "<c>Gur genssvp fvta vaqvpngrf n cebuvovgvba sbe iruvpyrf bs nyy xvaqf. Guvf qbrf abg nccyl gb iruvpyrf gung ner chfurq, ubefronpx evqref, thvqrf bs ubefrf naq qeviref naq thvqrf bs yvirfgbpx. Zbgbeplpyrf naq ovplpyrf znl or chfurq.<\/c>";
		dbTblQ[2239].info = "<c>Bayl crqrfgevnaf znl hfr gur fvqrjnyx. Gur nqqvgvbany flzoby vaqvpngrf na rkprcgvba sbe qryvirel genssvp. Ubjrire, gur qevire znl bayl qevir ng n jnyxvat fcrrq naq zhfg cnl cnegvphyne nggragvba gb crqrfgevnaf.<\/c>";
		dbTblQ[2240].info = "<c>Vs lbh qevir n iruvpyr, lbh pna'g ghea nebhaq urer.<\/c>";
		dbTblQ[2241].info = "<c>Nalbar qevivat n iruvpyr znl abg qevir snfgre guna gur fgngrq znkvzhz fcrrq.<\/c>";
		dbTblQ[2242].info = "<c>Nalbar qevivat n iruvpyr zhfg sbyybj gur cerfpevorq qverpgvba bs geniry.<\/c>";
		dbTblQ[2243].info = "<c>Nalbar qevivat n iruvpyr zhfg tvir jnl. Gur fvta fgnaqf vzzrqvngryl orsber gur pebffvat be whapgvba. Vg znl or vaqvpngrq ol gur fnzr punenpgre jvgu na nqqvgvbany punenpgre vaqvpngvat gur qvfgnapr. Vg vf vzcbegnag gb or ernql gb oenxr naq nccebnpu ng zbqrengr fcrrq. Cevbevgl fubhyq or tvira gb pebff-jvfr zbivat genssvp.<\/c>";
		dbTblQ[2244].info = "<c>Ubefronpx evqref ner abg nyybjrq gb evqr ba gur ebnq, ohg zhfg hfr gur evqvat cngu. Guvf nyfb nccyvrf gb gur qevivat bs ubefrf. Bgure genssvp vf abg nyybjrq gb hfr gur evqvat cngu .<\/c>";
		dbTblQ[2245].info = "<c>Gur genssvp fvta vaqvpngrf n cevbevgl ebnq. Nalbar qevivat n iruvpyr vf abg crezvggrq gb cnex ba gur ynarf bs cevbevgl ebnqf bhgfvqr ohvyg-hc nernf.<\/c>";
		dbTblQ[2246].info = "<c>Gur genssvp fvta vaqvpngrf n pbagvahngvba bs gur qvirefvba.<\/c>";
		dbTblQ[2247].info = "<c>Jubrire qevirf n iruvpyr naq jnagf gb sbyybj gur cevbevgl ebnq gung oraqf bss zhfg fvtany guvf pyrneyl naq va n gvzryl znaare. Gur qverpgvba vaqvpngbef funyy or hfrq sbe guvf checbfr.<\/c>";
		dbTblQ[2249].info = "<c>Gur genssvp fvta vaqvpngrf n crqrfgevna pebffvat. Zbgbevfgf zhfg ersenva sebz fgbccvat ba gur crqrfgevna pebffvat be oybpxvat gur pebffvat. Crqrfgevnaf unir gb or gnxra vagb pbafvqrengvba; gurl zhfg or nyybjrq gb pebff. Crqrfgevnaf jub erpbtavmnoyl jnag gb pebff gur crqrfgevna pebffvat fubhyq or nccebnpurq fybjyl.<\/c>";
		dbTblQ[2252].info = "<c>Pnyphyngr gur fgbccvat qvfgnapr nf sbyybjf: (oenxvat qvfgnapr sbe qnatrebhf oenxvat + ernpgvba qvfgnapr). Oenxvat qvfgnapr ng qnatrebhf oenxvat = (fcrrq ng xz\/u \/ 10) k (fcrrq ng xz\/u \/ 10) \/ 2 Ernpgvba qvfgnapr = (fcrrq ng xz\/u \/ 10) k 3 Ng 50 xz\/u gurer vf n fgbccvat qvfgnapr bs 27.5 z, juvpu vf guhf gbb ybat.<\/c>";
		dbTblQ[2254].info = "<c>Na nirahr vf pbzvat hc. Qhr gb gur pbafgnag punatr bs yvtug naq funqbj, qvfgnaprf naq bapbzvat genssvp ner bsgra qvssvphyg gb rfgvzngr. Ng gur fnzr gvzr, vg znxrf vg zber qvssvphyg gb erpbtavfr bgure zbgbevfgf, va cnegvphyne bapbzvat zbgbevfgf. Vs ivfvovyvgl vf qvssvphyg, gur fcrrq zhfg or nqwhfgrq.<\/c>";
		dbTblQ[2255].info = "<c>Gur pbhefr bs gur ebnq naq irtrgngvba znxr vg vzcbffvoyr gb qrgrpg cbffvoyr bapbzvat genssvp va n gvzryl znaare. Gur biregnxvat cebprff zhfg guhf or fgbccrq. Orpnhfr gur fubegrarq fnsrgl qvfgnapr pnaabg or znvagnvarq, gur qvfgnapr orgjrra lbhe iruvpyr naq gur iruvpyr va sebag bs lbh zhfg or vapernfrq.<\/c>";
		dbTblQ[2256].info = "<c>Jura evqvat jvguva n tebhc, vg vf fnsrfg sbe nyy evqref gb znvagnva gurve cbfvgvbaf.<\/c>";
		dbTblQ[2257].info = "<c>Gurer vf na vapernfrq evfx bs snyyvat qhevat fgrrevat be oenxvat jura qevivat ba n ebnq jvgu cbbe ebnq tevc. Ybbfr puvccvatf, jrg pbooyrfgbar naq n jrg ebnqjnl va trareny pna yrnq gb guvf qnatre.<\/c>";
		dbTblQ[2258].info = "<c>Vg vf orfg abg gb nppryrengr be oenxr ba jrg ebnq znexvatf fb gung gur sbeprf npgvat ba gur glerf erznva hapunatrq. Pebffvat jrg ebnq znexvatf va tncf rafherf n orggre tevc, nf gur tebhaq qbrf abg punatr.<\/c>";
		dbTblQ[2259].info = "<c>Jura gur znva ornz vf fjvgpurq ba, bapbzvat genssvp vf qnmmyrq, juvpu vf jul vg vf nyjnlf vzcbegnag gb qvz gur yvtugf va n gvzryl znaare. Nf fbba nf jvyq navznyf ner qnmmyrq ol gur ybat-qvfgnapr yvtug, gurl bsgra fgbc va gur ornz bs yvtug. Lbh zhfg ghea bss lbhe znva ornz yvtugf vzzrqvngryl va guvf pnfr.<\/c>";
		dbTblQ[2260].info = "<c>Gurer vf n cbffvovyvgl gung crqrfgevnaf znl or raqnatrerq.  Va beqre gb cerirag n pbyyvfvba, gurer vf ab pubvpr ohg gb erqhpr gur fcrrq naq fgbc vs arprffnel. Qverpg lbhe ivrj gb gur rqtr bs gur ebnq fb nf abg gb or qnmmyrq ol bapbzvat genssvp.<\/c>";
		dbTblQ[2262].info = "<c>Cnegvpvcngvba va ebnq genssvp erdhverf pbafgnag pnhgvba naq zhghny erfcrpg. Vg vf arprffnel gb erqhpr lbhe fcrrq fb gung gur biregnxvat znabrhier pna or pbzcyrgrq naq gnxr cynpr snfgre.<\/c>";
		dbTblQ[2263].info = "<c>Gur ehyr bs guhzo sbe oenxvat qvfgnapr vf nf sbyybjf: (fcrrq ng xz\/u \/ 10) k (fcrrq ng xz\/u \/ 10) Rknzcyr pnyphyngvba (50 xz\/u \/ 10) k (50 xz\/u \/ 10) = 25 z (100 xz\/u \/ 10) k (100 xz\/u \/ 10) = 100 z Gvc: Qbhoyr fcrrq = sbhe gvzrf oenxvat qvfgnapr<\/c>";
		dbTblQ[2265].info = "<c>Ol punatvat gur ynar, vg jbhyq or cbffvoyr gb nibvq gur erq pne. Ohg gurer vf n zbgbeovxr ba gur yrsg, fb gung n ynar punatr vf bhg bs gur dhrfgvba orpnhfr bs gur qnatre. Gur bayl bcgvba yrsg vf gb erqhpr lbhe bja fcrrq naq or ernql gb oenxr.<\/c>";
		dbTblQ[2266].info = "<c>Erne-ivrj zveebef qb abg znxr vg cbffvoyr gb qrgrpg rirelguvat oruvaq lbhe iruvpyr. Crbcyr be bowrpgf va gur oyvaq fcbg ner abg ivfvoyr. Gur zveebef bayl uryc gur qevire vs gurl ner pbeerpgyl nqwhfgrq.<\/c>";
		dbTblQ[2267].info = "<c>Rfcrpvnyyl snfg bapbzvat genssvp bsgra graqf gb phg pheirf. Sebz gur zbgbeplpyvfg'f cbvag bs ivrj, guvf vf n yrsg-unaq oraq, fb gung jura evqvat, gur zbgbeplpyvfg gvygf vajneqf naq znl cbffvoyl cebwrpg vagb gur ynar bs bapbzvat genssvp.<\/c>";
		dbTblQ[2269].info = "<c>Va gur pnfr bs n ybat dhrhr bs iruvpyrf, gur fvghngvba zhfg svefg or gnxra vagb nppbhag va beqre gb haqrefgnaq jul gur dhrhr unf sbezrq naq ubj gur bgure zbgbevfgf ner orunivat. Va beqre abg gb raqnatre nalbar, vg vf arprffnel gb xrrc pnyz naq jnvg.<\/c>";
		dbTblQ[2270].info = "<c>Bgure zbgbevfgf zhfg or gnxra vagb pbafvqrengvba. Gurersber, gur qevire unf gb purpx jurgure gur cnffratref pna trg bhg jvgubhg raqnatrevat nalbar. Ng gur fnzr gvzr, gurer vf n fcrpvny qhgl bs pner gbjneqf gur cnffratref, juvpu vf jul gurl unir gb or vasbezrq nobhg guvf cbffvoyr qnatre.<\/c>";
		dbTblQ[2271].info = "<c>Gur ehyr bs guhzo sbe gur ernpgvba qvfgnapr vf nf sbyybjf: (fcrrq va xz\/u \/ 10) k 3 (50 xz\/u \/ 10) k 3 = 15 z (100 xz\/u \/ 10) k 3 = 30 z<\/c>";
		dbTblQ[2272].info = "<c>Va nyy pnfrf, n ynar znl bayl or punatrq vs gurer vf ab qnatre gb bgure zbgbevfgf. Nal punatr bs ynar funyy or fvtanyyrq pyrneyl naq va n gvzryl znaare ol hfvat gur qverpgvba vaqvpngbef. Gur gehpx va sebag zretrf gb biregnxr. Va fcvgr bs gur rkvfgrapr bs nabgure ynar, vg vf abg cbffvoyr gb chyy bhg gb gur yrsg orpnhfr n iruvpyr vf nyernql nccebnpuvat va guvf ynar. Vg vf arprffnel gb fgnl va lbhe bja ynar naq gb fybj qbja.<\/c>";
		dbTblQ[2274].info = "<c>Ba zbgbejnlf naq bgure ebnqf bhgfvqr bs ohvyg-hc nernf, lbh znl qevir snfgre ba gur zretvat ynar guna ba gur pbagvahbhf ynarf. Va nyy pnfrf, n ynar znl bayl or punatrq vs guvf cerfragf ab qnatre gb bgure zbgbevfgf. Nal punatr bs ynar funyy or fvtanyyrq pyrneyl naq va n gvzryl znaare ol hfvat gur qverpgvba vaqvpngbef. Gur guebhtu-qevivat genssvp guhf unf cevbevgl.<\/c>";
		dbTblQ[2275].info = "<c>Qb abg qevir snfgre ba gur rkvg ynarf guna ba gur pbagvahbhf ynarf. Vs genssvp vf oybpxrq be fgnaqvat ba gur pbagvahbhf ynar, na biregnxvat znabrhier znl or cresbezrq ba gur rkvg ynar ng zbqrengr fcrrq naq jvgu fcrpvny pner. Orpnhfr gur erpbzzraqrq fcrrq sbe yrnivat gur zbgbejnl vf gbb uvtu, vg vf arprffnel gb erqhpr lbhe fcrrq. Vs lbh biregnxr, gur biregnxvat cebprff zhfg or pbzcyrgrq va n gvzryl znaare va beqre gb gura fnsryl ernpu gur rkvg ynar.<\/c>";
		dbTblQ[2276].info = "<c>Gur fcrrq vf bsgra haqrerfgvzngrq qhevat ybatre wbhearlf ng uvtu fcrrq. N pbagebyyrq tynapr ng gur fcrrqbzrgre urycf gb rafher gung lbh ner qevivat vagb gur rkvg ynar ng na nccebcevngr fcrrq. Qb abg qevir snfgre ba gur rkvg ynar guna ba gur pbagvahbhf ynarf. Gur svefg guvat gb qb vf gb fvtany gung lbh ner punatvat ynarf, punatr ynarf naq gura oenxr vs arprffnel.<\/c>";
		dbTblQ[2277].info = "<c>Bgure zbgbevfgf zhfg or gnxra vagb pbafvqrengvba. Gurersber, vg vf arprffnel gb zretr ng n fhssvpvrag qvfgnapr fb nf abg gb bofgehpg gur bgure zbgbevfgf.<\/c>";
		dbTblQ[2282].info = "<c>Qrfcvgr gur vanccebcevngr orunivbhe bs gur bgure ebnq hfre, vg vf vzcbegnag gb erznva pnyz. Lbh zhfg oenxr va beqre gb znvagnva n fhssvpvrag fnsrgl qvfgnapr. Bgurejvfr n pbyyvfvba pbhyq bpphe va n qnatrebhf fvghngvba.<\/c>";
		dbTblQ[2283].info = "<c>Znal lbhat zbgbevfgf unir yvggyr qevivat rkcrevrapr, juvpu vapernfrf gur evfx bs nppvqragf. Lbhat zbgbevfgf nyfb graq gb or zber jvyyvat gb gnxr evfxf. Va nqqvgvba, bar'f bja novyvgvrf ner bsgra birerfgvzngrq.<\/c>";
		dbTblQ[2284].info = "<c>Nygubhtu gur bgure zbgbevfg vf ivbyngvat gur genssvp ehyrf, vg vf arprffnel gb fubj pbafvqrengvba naq yrg guvf zbgbevfg qevir.<\/c>";
		dbTblQ[2285].info = "<c>Vg vf jebat gb nyybj barfrys gb or cerffherq naq, sbe rknzcyr, qryvorengryl qevir gbb snfg. Gurersber, lbhe fcrrq fubhyq or znvagnvarq. Xrrcvat gb gur evtug znxrf vg rnfvre sbe gur bgure iruvpyr gb biregnxr.<\/c>";
		dbTblQ[2287].info = "<c>Rfcrpvnyyl va pbzovangvba jvgu frireny bpphcnagf, znal lbhat zbgbevfgf ner grzcgrq gb gnxr evfxf. Nf fbba nf gurer ner frireny bpphcnagf va gur pne, gurer vf n evfx gung gur qevire znl orpbzr vapernfvatyl qvfgenpgrq.<\/c>";
		dbTblQ[2289].info = "<c>Gurer vf n evfx bs orvat bireybbxrq ol gur gehpx qevire. Gur fnzr nccyvrf gb bgure zbgbevfgf, orpnhfr gur gehpx erfgevpgf gurve ivrj. Gur gehpx nyfb erfgevpgf gur qevire'f bja ivrj, juvpu znl pnhfr bgure zbgbevfgf gb or bireybbxrq.<\/c>";
		dbTblQ[2291].info = "<c>Biregnxvat qvfgnapr = fcrrq bs bja iruvpyr (100) qvivqrq ol gur qvssrerapr fcrrq (100-70=30) zhygvcyvrq ol gur fhz bs gur gjb gjb-frpbaq fnsrgl qvfgnaprf naq gur yratgu bs obgu iruvpyrf (50 z + 50 z + nccebk. 4.5 + 15.5 z). Guvf erfhygf va na biregnxvat qvfgnapr bs 400 z. Qhr gb gur qbzr, gur cngu unf gb or qbhoyrq, erfhygvat va gur 800 z.<\/c>";
		dbTblQ[2292].info = "";
		dbTblQ[2293].info = "<c>Pebffvat n fbyvq yvar vf nyfb abg crezvggrq qhevat biregnxvat. Gurersber, gur biregnxvat cebprff zhfg or pbzcyrgrq va n gvzryl znaare. Gur fnzr nccyvrf orsber eryrinag fvtaf sbeovqqvat biregnxvat.<\/c>";
		dbTblQ[2294].info = "";
		dbTblQ[2295].info = "";
		dbTblQ[2296].info = "";
		dbTblQ[2297].info = "<c>Nalbar qevivat n iruvpyr znl abg fgbc hc gb 10 z va sebag bs n qvntbany pebff vs guvf pbaprnyf vg. Gur fnzr nccyvrf gb gur fvta \"Unyg. Tvir jnl.\"<\/c>";
		dbTblQ[2299].info = "<c>Na nggrzcg fubhyq or znqr gb fgbc nf sne gb gur evtug nf cbffvoyr. Ng gur fnzr gvzr, gur unmneq jneavat yvtugf zhfg or fjvgpurq ba vzzrqvngryl. Gurernsgre, ng yrnfg bar pbafcvphbhf jneavat fvta funyy or cynprq va n pyrneyl ivfvoyr ybpngvba ng n fhssvpvrag qvfgnapr. Va gur pnfr bs snfg genssvp, vg fubhyq or cynprq ng n qvfgnapr bs nccebkvzngryl 100 zrgerf.<\/c>";
		dbTblQ[2300].info = "<c>Vs gur zbgbe snvyf, gur oenxr obbfgre vf abg ninvynoyr. Va beqre gb npuvrir n pbzcnenoyr oenxvat sbepr, nccylvat n uvture crqny cerffher vf arprffnel. Gbjvat ol zrnaf bs n gbj one perngrf n pbaarpgvba gung npgf orgjrra gur iruvpyrf va obgu qverpgvbaf naq guhf cebivqrf fbzr fhccbeg qhevat oenxvat.<\/c>";
		dbTblQ[2301].info = "<c>Abar bs gur yvtugf znl or pbaprnyrq. Vs guvf vf gur pnfr qhr gb ybnq be nggnpuzragf, na nqqvgvbany yvtug pneevre zhfg or svggrq.<\/c>";
		dbTblQ[2302].info = "<c>Vg zhfg or cbffvoyr gb qvc lbhe yvtugf va n gvzryl znaare vs n iruvpyr nccebnpurf be qevirf nurnq ng n fubeg qvfgnapr be vs vg vf bgurejvfr erdhverq va beqre gb rafher gur fnsrgl bs genssvp ba be bss gur ebnq. Bgure zbgbevfgf zhfg abg or qnmmyrq.<\/c>";
		dbTblQ[2303].info = "<c>Qb abg qevir jvgu znva ornz yvtugf ba ebnqf jvgu pbagvahbhf, nqrdhngr yvtugvat. Gur urnqynzcf bs jnvgvat zbgbe iruvpyrf zhfg abg qnmmyr nalobql.<\/c>";
		dbTblQ[2304].info = "<c>Vs sbt, fabjsnyy be enva pbafvqrenoyl bofgehpgf ivfvovyvgl, lbh fubhyq nyfb qevir jvgu qvccrq urnqyvtugf qhevat gur qnl. Gur fjvgpurq-ba urnqyvtugf uryc bgure zbgbevfgf gb erpbtavfr gur iruvpyr va n gvzryl znaare.<\/c>";
		dbTblQ[2306].info = "<c>Ba zbgbejnlf, vg vf crezvggrq gb qevir snfgre ba gur zretvat ynar guna ba gur guebhtu-ynar. Sbe gur fnsrgl bs nyy zbgbevfgf, vg vf vzcbegnag gb nppryrengr va n pbagebyyrq znaare naq gb trg nybat nppbeqvat gb gur ehyrf bs gur evtug bs jnl.<\/c>";
		dbTblQ[2307].info = "<c>Gurer ner znal qnatref vaibyirq va abg jrnevat n frngoryg &aqnfu; obgu va gur sebag naq erne pne frngf. Rira ng ybj fcrrqf gurer vf n evfx bs vawhel. Cnffratref jub ner abg fgenccrq va ner ng evfx bs orvat guebja bhg bs gur iruvpyr qhr gb gur rabezbhf sbeprf gung pna or rkregrq va n pbyyvfvba. Vs cnffratref ner abg fgenccrq va, gurve obqvrf be gurve yvzof znl or guebja ntnvafg bgure bpphcnagf naq pnhfr vawhel.<\/c>";
		dbTblQ[2308].info = "";
		dbTblQ[2309].info = "";
		dbTblQ[2310].info = "";
		dbTblQ[2311].info = "<c>Fnsr bcrengvba bs gur zbgbe iruvpyr zhfg or rafherq. Gurersber, gur qevire zhfg pneel bhg n ivfhny vafcrpgvba bs gur glerf orsber fgnegvat gur wbhearl. Vg vf nyfb arprffnel gb purpx gung gur yvtugvat jbexf cresrpgyl.<\/c>";
		dbTblQ[2312].info = "";
		dbTblQ[2315].info = "<c>Vs gur cnffratre qbrf abg jrne pbafcvphbhf pybguvat, gur iruvpyr vf yrff ivfvoyr gb gur sbyybjvat genssvp.<\/c>";
		dbTblQ[2316].info = "<c>Vs n zbgbeplpyr nccebnpurf irel dhvpxyl, vg znl or bireybbxrq qrfcvgr gur ybj ornz. Qvfperrg pybguvat pna nyfb or erfcbafvoyr sbe guvf. Va nqqvgvba, zbgbeplpyrf ner aneebjre guna cnffratre pnef naq guhf yrff ivfvoyr.<\/c>";
		dbTblQ[2317].info = "<c>Zbgbeplpyvfgf ner zber qvssvphyg gb erpbtavfr va ebnq genssvp vs pbafcvphbhf pybguvat vf abg jbea. Guvf vapernfrf gur evfx bs nppvqragf.<\/c>";
		dbTblQ[2318].info = "<c>Zbgbeplpyr fnsrgl pybguvat pna znxr n fvtavsvpnag pbagevohgvba gb vawhel cebgrpgvba va gur rirag bs n snyy.<\/c>";
		dbTblQ[2319].info = "";
		dbTblQ[2320].info = "<c>Qevivat jvgu qvccrq urnqyvtugf vf crezvggrq va beqre gb or zber yvxryl gb or erpbtavfrq va ebnq genssvp qhevat gur qnl. Gur fnzr nccyvrf gb qevivat jvgu qnlgvzr ehaavat yvtugf.<\/c>";
		dbTblQ[2321].info = "<c>Nalbar evqvat n ovplpyr be n zbgbeovxr znl abg or chyyrq nybat ol bgure iruvpyrf. Vg vf nyfb abg crezvggrq gb chyy bgure zbgbevfgf.<\/c>";
		dbTblQ[2322].info = "<c>Gurer vf na boyvtngvba gb jrne n fhvgnoyr cebgrpgvir uryzrg.<\/c>";
		dbTblQ[2325].info = "";
		dbTblQ[2326].info = "<c>Pbafcvphbhf pybguvat znxrf zbgbeplpyvfgf zber erpbtavmnoyr rneyvre ba. Va gur qnex, pybguvat znqr bs ergeb-ersyrpgvir zngrevny vzcebirf ivfvovyvgl.<\/c>";
		dbTblQ[2327].info = "<c>Fhvgnoyr tybirf ner na rffragvny cneg bs cebgrpgvir pybguvat. Gur fnzr nccyvrf gb n fhvgnoyr fnsrgl uryzrg.<\/c>";
		dbTblQ[2328].info = "<c>Onpx cebgrpgbef pbagevohgr gb fnsrgl jura evqvat n zbgbeplpyr. Gur fnzr nccyvrf gb fcrpvny zbgbeplpyr cebgrpgvir pybguvat.<\/c>";
		dbTblQ[2329].info = "<c>Qhr gb gur punatrq jrvtug ybnq, vg znl or arprffnel gb nqwhfg gur erne fhfcrafvba. Vg fubhyq or abgrq gung gur crezvffvoyr gbgny znff vf abg rkprrqrq. Gur gler cerffher zhfg nyfb or nqwhfgrq.<\/c>";
		dbTblQ[2331].info = "<c>Gur qnatre fvta \"fvqr jvaq\" pna znvayl or sbhaq va wnttrq greenva naq ba oevqtrf. Vg jneaf ntnvafg fvqrjnlf jvaqf gung, qrcraqvat ba gurve fgeratgu, vasyhrapr gur qevivat orunivbhe bs gur iruvpyr naq znl erdhver pbhagre-fgrrevat. Nqwhfg lbhe fcrrq vs arprffnel.<\/c>";
		dbTblQ[2332].info = "<c>Gur qnatre fvta \"fvqr jvaq\" pna znvayl or sbhaq va wnttrq greenva naq ba oevqtrf. Vg jneaf ntnvafg fvqrjnlf jvaqf gung, qrcraqvat ba gurve fgeratgu, vasyhrapr gur qevivat orunivbhe bs gur iruvpyr naq znl erdhver pbhagre-fgrrevat. Nqwhfg lbhe fcrrq vs arprffnel.<\/c>";
		dbTblQ[2333].info = "<c>Jvgubhg yvnovyvgl vafhenapr, cnegvpvcngvba va ebnq genssvp vf abg crezvggrq. Ivbyngvbaf pna or chavfurq jvgu n svar be rira vzcevfbazrag. Va gur rirag bs qnzntr, gurer lbh evfx univat gb cnl gur shyy pbfg bs gur qnzntr lbhefrys.<\/c>";
		dbTblQ[2334].info = "<c>Sbe cnegvpvcngvba va choyvp ebnq genssvp, obgu gur ertvfgengvba pregvsvpngr Cneg V naq n inyvq qevivat yvprapr zhfg or pneevrq.<\/c>";
		dbTblQ[2335].info = "<c>Sbe cnegvpvcngvba va choyvp ebnq genssvp, obgu gur ertvfgengvba pregvsvpngr Cneg V naq n inyvq qevivat yvprapr zhfg or pneevrq.<\/c>";
		dbTblQ[2336].info = "<c>Gur cbffrffvba bs n qevivat yvprapr zhfg or cebira ol n inyvq bssvpvny pregvsvpngr (qevivat yvprapr) nf jryy nf gur bcrengvat crezvg be gur qrpynengvba bs pbasbezvgl (PBP qbphzrag). Gur fnzr nccyvrf gb vafhenapr pregvsvpngrf va beqre gb or noyr gb cebir gur rkvfgvat vafhenapr pbirentr.<\/c>";
		dbTblQ[2337].info = "<c>N qevivat yvprapr zhfg or pneevrq jura qevivat zbgbe iruvpyrf. Gur fnzr nccyvrf gb gur ertvfgengvba pregvsvpngr Cneg V be vgf pbeerfcbaqvat pregvsvpngr.<\/c>";
		dbTblQ[2338].info = "";
		dbTblQ[2340].info = "<c>Vs na vafhenapr ahzore cyngr vf ab ybatre inyvq, cebgrpgvba ab ybatre rkvfgf. Jvgubhg yvnovyvgl vafhenapr, cnegvpvcngvba va ebnq genssvp vf abg crezvggrq.<\/c>";
		dbTblQ[2341].info = "";
		dbTblQ[2342].info = "";
		dbTblQ[2343].info = "";
		dbTblQ[2344].info = "";
		dbTblQ[2345].info = "";
		dbTblQ[2346].info = "<c>Va gur pnfr bs evtvq qenjone genvyref jvgu n crezvffvoyr gbgny jrvtug bs abg zber guna 3.5 gbaarf, gur zvavzhz fhccbegvat ybnq gb or nofbeorq ol gur gbjvat iruvpyr fubhyq abg or yrff guna 4 creprag bs gur npghny gbgny jrvtug bs gur genvyre, ohg fubhyq abg rkprrq 25 xt. Sbhe creprag bs 600 xt pbeerfcbaqf gb 24 xt.<\/c>";
		dbTblQ[2348].info = "";
		dbTblQ[2349].info = "";
		dbTblQ[2350].info = "";
		dbTblQ[2351].info = "";
		dbTblQ[2352].info = "";
		dbTblQ[2355].info = "";
		dbTblQ[2356].info = "";
		dbTblQ[2357].info = "";
		dbTblQ[2358].info = "<c>Gur cnffratre znl snyy bss gur zbgbeplpyr qhr gb znff varegvn naq cbbe fhccbeg. Orpnhfr gur qevir vf ivn gur erne jurry, gur sebag jurry guerngraf gb ybfr pbagnpg jvgu gur tebhaq naq yvsg bss. Gurer vf n evfx bs ybfvat pbageby bs gur zbgbeplpyr.<\/c>";
		dbTblQ[2359].info = "";
		dbTblQ[2361].info = "<c>Gb rafher gung pragevshtny sbeprf qb abg cbfr n qnatre, oenxvat fubhyq or nccyvrq va sebag bs gur pheir. Bayl sebz gur genafvgvba gb gur fgenvtug yvar (ncrk) pna pnershy nppryrengvba gnxr cynpr ntnva.<\/c>";
		dbTblQ[2363].info = "";
		dbTblQ[2366].info = "<c>Lbh zhfg svefg purpx gung gur pbaarpgvat qrivpr vf ybpxrq, v.r., lbh zhfg rafher gung gur onyy vf ratntrq va gur pbhcyre. Gur oernxnjnl pnoyr zhfg or nggnpurq fb gung vg pna or rzcyblrq va na rzretrapl va beqre gb oenxr gur genvyre. Purpx gung nyy gur yvtugf ba gur genvyre ner va jbexvat beqre.<\/c>";
		dbTblQ[2367].info = "";
		dbTblQ[2370].info = "<c>Nalbar jub jnagf gb biregnxr unf gb orunir va fhpu n jnl gung gurer vf ab qnatre gb gur fhofrdhrag genssvp.<\/c>\r\n<c>Jura biregnxvat, fhssvpvrag yngreny pyrnenapr zhfg or znvagnvarq sebz bgure zbgbevfgf, va cnegvphyne sebz crqrfgevnaf naq plpyvfgf. Vs lbh biregnxr, lbh unir gb trg onpx vagb gur evtug ynar nf fbba nf cbffvoyr. Gubfr jub biregnxr zhfg abg uvaqre gubfr jub ner orvat biregnxra.<\/c>\r\n<c>Orpnhfr gur oyhr pne vf yrnivat gur cnexvat fcnpr, gur plpyvfg jvyy cebonoyl unir gb znxr n qrgbhe gb gur yrsg. Gur cebprff bs biregnxvat zhfg guhf or fgbccrq.<\/c>";
		dbTblQ[2374].info = "<c>Gur gehpx pbhyq fjvat bhg rira shegure va beqre gb trg nebhaq gur oraq jvgu gur genvyre. Gur gehpx unf gb jnvg bccbfvgr gur crqrfgevna, juvpu vf jul vg znl fgbc.<\/c>";
		dbTblQ[2377].info = "<c>Gur genssvp yvtug gnxrf cerprqrapr bire bgure genssvp fvtaf gung erthyngr cevbevgl. Vs - nf va guvf pnfr - n fgbc yvar rkvfgf, lbhe iruvpyr zhfg fgbc ng guvf yvar.<\/c>";
		dbTblQ[2378].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Iruvpyrf sebz gur yrsg naq sebz gur evtug zhfg or jngpurq orpnhfr gurl ner qevivat ba gur cevbevgl ebnq.<\/c>";
		dbTblQ[2379].info = "<c>Biregnxvat vf bayl nyybjrq vs lbh pna rafher gung ab bofgehpgvba bs bapbzvat genssvp jvyy gnxr cynpr qhevat gur ragver biregnxvat cebprff. Biregnxvat vf abg crezvggrq vs gur genssvp fvghngvba vf hapyrne. Va guvf pnfr, gurer vf erthyne bapbzvat genssvp. Va nqqvgvba, gur genssvp fvghngvba vf hapyrne orpnhfr gur ivrj bs gur ebnqjnl vf frireryl erfgevpgrq ol gerrf.<\/c>";
		dbTblQ[2380].info = "";
		dbTblQ[2381].info = "<c>Gurer vf n evfx bs orvat bireybbxrq ol bgure zbgbevfgf orpnhfr gur gehpx erfgevpgf gurve ivfvovyvgl. Gur gehpx nyfb erfgevpgf gur qevire'f bja ivfvovyvgl, juvpu znl pnhfr bgure zbgbevfgf gb or bireybbxrq.<\/c>";
		dbTblQ[2382].info = "<c>Juvyr orvat gbjrq, obgu iruvpyrf zhfg unir gurve unmneq jneavat yvtugf fjvgpurq ba. Gur gbjvat pnoyr fubhyq or xrcg nf gnhg nf cbffvoyr gb cerirag qnzntr (r.t., qhr gb n fgebat wbyg be tevaqvat ba gur ebnq fhesnpr).<\/c>";
		dbTblQ[2383].info = "<c>Cnegvpvcngvba va choyvp ebnq genssvp jvgu n iruvpyr jubfr bcrengvat yvprafr unf rkcverq pna or chavfurq jvgu n svar. Va nqqvgvba, gurer vf n guerng bs ragel vagb gur Prageny Ertvfgre bs Ebnq Genssvp Bssraqref. Va nqqvgvba, n ybff bs vafhenapr pbirentr vf gb or rkcrpgrq.<\/c>";
		dbTblQ[2384].info = "";
		dbTblQ[2385].info = "";
		dbTblQ[2386].info = "";
		dbTblQ[2387].info = "<c>Snvyher gb bofreir gur genssvp fvta pbafgvghgrf n frevbhf vasevatrzrag qhevat gur cebongvbanel crevbq. Gurer vf gur guerng bs na beqre gb cnegvpvcngr va na nqinaprq frzvane sbe arj qeviref naq na ragel va gur Prageny Ertvfgre bs Ebnq Genssvp Bssraqref.<\/c>";
		dbTblQ[2388].info = "<c>Gur sbphf bs na nqinaprq frzvane vf gur nggvghqr gbjneqf ebnq genssvp. Cnegvpvcnagf fubhyq or frafvgvfrq gb orunir zber pbafvqrengryl va genssvp yngre ba. Va nqqvgvba, gur qrgrpgvba bs genssvp unmneqf vf gb or vzcebirq.<\/c>";
		dbTblQ[2390].info = "<c>Cnffratref znl ragre gur ebnq, fb vapernfrq pnhgvba vf nqivfnoyr.<\/c>\r\n<c>Fpurqhyrq ohfrf naq znexrq fpubby ohfrf fgbccrq ng ohf fgbcf jvgu unmneq jneavat yvtugf ghearq ba znl bayl or biregnxra ng jnyxvat fcrrq naq ng fhpu n qvfgnapr gung cnffratref ner abg raqnatrerq.<\/c>\r\n<c>Jnyxvat fcrrq nyfb nccyvrf gb bapbzvat genssvp ba gur fnzr ebnq. Ohf cnffratref zhfg abg or bofgehpgrq. Vs arprffnel, nalbar qevivat n iruvpyr zhfg jnvg.<\/c>";
		dbTblQ[2391].info = "<c>Qhr gb gur yvzvgrq ivrj bs gur ohf, bgure iruvpyrf znl abg or frra hagvy yngre. Crbcyr jub jnag gb trg ba be bss gur ohf pbhyq pebff gur ebnq. N iruvpyr pbhyq biregnxr gur ohf naq nccrne ng nal gvzr. Qhr gb gurfr qnatref, vg znl or arprffnel gb fgbc ng gur vagrefrpgvba.<\/c>";
		dbTblQ[2392].info = "<c>Gur pne va sebag pbhyq fgbc orpnhfr bs gur crqrfgevna. N plpyvfg jnf frra va gur zveebe, naq gur plpyvfg unf abj ragrerq gur oyvaq fcbg. Guvf zhfg nyfb or bofreirq.<\/c>";
		dbTblQ[2393].info = "<c>Cnegvpvcngvba va ebnq genssvp erdhverf pbafgnag pnhgvba naq zhghny erfcrpg. Hfvat gur tnc vf n fvzcyr naq fnsr jnl gb yrg gur bgure iruvpyr cnff.<\/c>";
		dbTblQ[2394].info = "<c>Lbh zhfg vaqvpngr n ghea gb gur yrsg iruvpyr vf ragrevat nabgure ebnq. Gur fnzr tbrf sbe gur oyhr gehpx. Vg sbyybjf gur znva ebnq, ohg orpnhfr vg vf nyfb gheavat bss gur ebnq, fvtanyyvat vf arprffnel.<\/c>";
		dbTblQ[2395].info = "<c>Gurer vf ab fhssvpvrag fcnpr va gur genssvp ba gur evtug, fb vg vf vzcbegnag gb fgnl va ynar sbe gur gvzr orvat. Vs n fhssvpvrag fcnpr va gur genssvp vf znvagnvarq ba gur evtug, gur ynar znl or punatrq.<\/c>";
		dbTblQ[2396].info = "<c>Lbhe iruvpyr vf nobhg gb ghea bagb n fgerrg gung znl nyfb or pebffrq ol gjb crqrfgevnaf ng gur fnzr gvzr. Obgu crqrfgevnaf unir cevbevgl, fb lbh unir gb jnvg.<\/c>";
		dbTblQ[2398].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Guvf zrnaf gung lbh unir gb qevir oruvaq gur lryybj pne. Orpnhfr lbh unir gb ghea yrsg, bapbzvat genssvp \u2013 va guvf pnfr gur oyhr gehpx \u2013 unf cevbevgl.<\/c>";
		dbTblQ[2399].info = "<c>Gur genssvp fvta tenagf evtug bs jnl, zrnavat gur bgure gjb pnef zhfg jnvg.<\/c>";
		dbTblQ[2400].info = "<c>Gur genssvp fvta vaqvpngrf cbffvoyr genssvp nffvfgnagf (ybyyvcbc zra). Vg pna or nffhzrq gung zber puvyqera guna hfhny pebff gur ebnq ng pregnva gvzrf. Vg vf gb or rkcrpgrq gung gur sybjvat genssvp jvyy or vagreehcgrq ol fgbcf.<\/c>";
		dbTblQ[2401].info = "<c>Gur ivfvovyvgl pbaqvgvbaf ner abg tbbq; gur ebnq fhesnpr pbhyq or fyvccrel naq gur ynar pbhyq or nssrpgrq ol urnil fabjqevsgf. Vg vf abg cbffvoyr gb zretr gb gur yrsg orpnhfr nabgure iruvpyr vf biregnxvat. Gurersber, vg vf arprffnel gb fybj qbja naq jnvg ng gur bofgnpyr.<\/c>";
		dbTblQ[2402].info = "<c>Qhr gb gur cngu bs gur ebnq naq gur irtrgngvba, ivfvovyvgl vf frireryl erfgevpgrq naq gur ebnq fhesnpr vf cbbe. Gurer znl or na bofgnpyr ba gur ebnq, juvpu vf jul vg vf arprffnel gb erqhpr lbhe fcrrq. Vg vf arprffnel gb fgnl va lbhe ynar. Zretvat vagb nabgure ynar jbhyq uneqyl vzcebir ivfvovyvgl, juvyfg vapernfvat gur evfx bs pbyyvfvba jvgu bapbzvat genssvp.<\/c>";
		dbTblQ[2405].info = "<c>Gur vafgnyyngvba bs n fgrrevat jurry bs n qvssrerag glcr pna yrnq gb gur pnapryyngvba bs gur bcrengvat yvprapr. Gur fnzr nccyvrf gb zbqvsvpngvbaf gb gur rkunhfg flfgrz.<\/c>";
		dbTblQ[2409].info = "<c>Gur fvghngvba vf pbashfvat: Gur gehpx qevire cebonoyl jnagf gb qevir onpxjneqf vagb gur pbafgehpgvba fvgr. Qhr gb gur cebkvzvgl bs gur gehpx naq n pne va gur oyvaq fcbg, gurer vf ab fnsr jnl bhg. Vg vf arprffnel gb fgbc naq yrg gur gehpx pbzcyrgr vgf znabrhier.<\/c>";
		dbTblQ[2410].info = "<c>Cnegvpvcngvba va ebnq genssvp erdhverf pbafgnag pnhgvba naq zhghny erfcrpg. Gb cerirag gur ubefrf sebz orvat fgnegyrq be hafrggyrq, vg vf nqivfnoyr gb nibvq abvfr naq qevir nf sne gb gur evtug nf cbffvoyr.<\/c>";
		dbTblQ[2411].info = "<c>Ubefr-qenja pneevntrf zhfg fvtany gur punatvat bs gur qverpgvba bs geniry. Guvf pna or qbar hfvat vaqvpngbef. Vs gur pneevntr unf ab vaqvpngbef, gur qevire bs gur pbnpu unf gur bcgvba bs rvgure qvfcynlvat n genssvp cnqqyr be fgergpuvat bhg uvf be ure nez.<\/c>";
		dbTblQ[2412].info = "<c>Ubefrf pna or fgnegyrq ol abvfr. Va beqre gb cerirag unmneqf va ebnq genssvp, haarprffnel abvfr zhfg guhf or nibvqrq. Nalbar jub jnagf gb biregnxr unf gb orunir va fhpu n jnl gung gurer vf ab qnatre gb gur genssvp oruvaq uvf be ure iruvpyr. Jura biregnxvat, fhssvpvrag yngreny pyrnenapr zhfg or znvagnvarq sebz bgure zbgbevfgf, va cnegvphyne sebz crqrfgevnaf naq plpyvfgf.<\/c>";
		dbTblQ[2415].info = "<c>Gurer vf n genz pbzvat gbjneqf lbh. Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba.<\/c>";
		dbTblQ[2416].info = "<c>Ebnq genssvp znl nccebnpu yriry pebffvatf bayl ng n zbqrengr fcrrq. Nalbar qevivat n iruvpyr znl abg biregnxr zbgbe iruvpyrf ng yriry pebffvatf sebz fvta 151, 156 hc gb naq vapyhqvat gur envy pebffvat nern orgjrra gur genpxf naq gur ebnq.<\/c>";
		dbTblQ[2417].info = "<c>Vs gur yriry pebffvat pnaabg or pebffrq dhvpxyl naq jvgubhg n fgbc qhr gb ebnq genssvp, jnvg va sebag bs gur Fg. Naqerj'f Pebff. (Abgr: Gur oyhr pne vf fgbccrq qverpgyl oruvaq gur yriry pebffvat.)<\/c>";
		dbTblQ[2418].info = "<c>Gur wbttre vf nccebnpuvat gur mroen pebffvat checbfrshyyl. Fvapr ur unf cevbevgl, jr zhfg fgbc.<\/c>";
		dbTblQ[2419].info = "<c>Oynpx neebjf (qverpgvbany gevnatyrf) ba qryvarngbef vaqvpngr gur fubegrfg ebhgr gb gur arnerfg rzretrapl gryrcubar.<\/c>";
		dbTblQ[2420].info = "<c>Bapbzvat genssvp \u2013 naq guhf gur erq pne \u2013 unf cevbevgl. Gur cevbevgl ebnq gung oraqf bss vf gb or rkvgrq. Arireguryrff, gur evtug bs jnl fgvyy rkvfgf, zrnavat gur oyhr gehpx unf gb jnvg.<\/c>";
		dbTblQ[2422].info = "<c>Gur genssvp fvta jneaf bs qnatre. Gur nqqvgvbany cyngr cbvagf bhg gur npghny qnatre, v.r., ubefr pneevntrf.<\/c>";
		dbTblQ[2423].info = "<c>Gur genssvp fvta vaqvpngrf gur ortvaavat bs n genssvp-pnyzrq nern. Nalbar jub qevirf n iruvpyr zhfg qevir ng jnyxvat fcrrq. Nalbar qevivat n iruvpyr zhfg abg raqnatre be bofgehpg crqrfgevna genssvp. Vs arprffnel, iruvpyrf zhfg jnvg. Nalbar qevivat n iruvpyr znl abg cnex bhgfvqr gur qrfvtangrq nernf, rkprcg sbe obneqvat, qvfrzonexvat, ybnqvat be haybnqvat.<\/c>";
		dbTblQ[2425].info = "<c>Gur cebsrffvbany qvfcbfny bs jnfgr bvy, yhoevpnagf naq iruvpyr onggrevrf pna or pneevrq bhg ivn pne jbexfubcf naq erplpyvat pragerf.<\/c>";
		dbTblQ[2429].info = "<c>Ybj-erfvfgnapr glerf erqhpr ebyyvat erfvfgnapr naq guhf uryc gb erqhpr shry pbafhzcgvba. Ybj-ivfpbfvgl bvyf erqhpr sevpgvba va gur ratvar naq guhf nyfb uryc gb cebgrpg gur raivebazrag.<\/c>";
		dbTblQ[2430].info = "<c>Ybj-ivfpbfvgl bvyf erqhpr sevpgvba va gur ratvar. Va guvf jnl, gurl pbagevohgr gb ybjre shry pbafhzcgvba.<\/c>";
		dbTblQ[2432].info = "<c>Ebbs fhcrefgehpgherf vapernfr nve erfvfgnapr naq guhf vapernfr shry pbafhzcgvba. N cebnpgvir qevivat fglyr pbagevohgrf gb ybj shry pbafhzcgvba.<\/c>";
		dbTblQ[2433].info = "<c>Nibvqvat fgebat nppryrengvba pbagevohgrf gb n ybat freivpr yvsr bs gur glerf. Va nqqvgvba, gur nve cerffher zhfg or purpxrq erthyneyl gb nibvq qevivat jvgu gler cerffher gung vf gbb ybj.<\/c>";
		dbTblQ[2434].info = "<c>Nzohynaprf va hfr ner rkrzcgrq sebz gur genssvp ona va raivebazragny mbarf, nf ner ntevphygheny naq sberfgel genpgbef.<\/c>";
		dbTblQ[2435].info = "<c>N crefba vf ehaavat bagb gur ebnq va sebag bs gur gehpx naq pbhyq pebff gur ynar. Va beqre abg gb raqnatre guvf crqrfgevna, vg vf arprffnel gb oenxr.<\/c>";
		dbTblQ[2436].info = "<c>N crqrfgevna sbeprf gur bapbzvat gehpx gb chyy bhg. Vg phgf vagb gur ebnq, juvpu vf jul lbh unir gb oenxr naq qevir nf sne gb gur evtug nf cbffvoyr.<\/c>";
		dbTblQ[2437].info = "<c>Qhr gb gur uvtu fabjsnyy ng gur rqtr bs gur ebnq, obgu lbhe bja ivfvovyvgl naq gur ivfvovyvgl bs gur terra pne vf vzcnverq. Vg frrzf nf vs gur terra pne jvyy pebff gur ebnq vzzrqvngryl. Vg vf guhf arprffnel gb oenxr naq qrshfr gur qnatrebhf fvghngvba.<\/c>";
		dbTblQ[2438].info = "<c>Gur ebnq vf irel aneebj naq gurer vf n gehpx nccebnpuvat. Vg znl or arprffnel gb fgbc svefg naq svaq n fnsr fbyhgvba va beqre gb cnff.<\/c>";
		dbTblQ[2439].info = "<c>Gur oyhr pne vf boivbhfyl qevivat gbb snfg naq vf pebffvat gur qvivqvat yvar, guerngravat n pbyyvfvba.<\/c>";
		dbTblQ[2440].info = "<c>Gur plpyvfg yrnirf gur plpyr cngu naq pebffrf gur ebnq. Vg vf arprffnel gb oenxr va beqre gb xrrc fhssvpvrag qvfgnapr. Vg vf abg cbffvoyr gb zretr vagb gur yrsg-unaq ynar orpnhfr n iruvpyr vf nyernql nccebnpuvat gurer.<\/c>";
		dbTblQ[2441].info = "<c>Gurer vf n qevire nccebnpuvat va gur jebat qverpgvba. Qevivat nf sne gb gur evtug nf cbffvoyr erqhprf gur evfx bs n pbyyvfvba. Va nqqvgvba, lbh zhfg oenxr va beqre gb shegure erqhpr gur evfx.<\/c>";
		dbTblQ[2442].info = "<c>Gurer vf n genssvp wnz. Vg vf arprffnel gb fybj qbja va beqre gb nccebnpu gur raq bs gur pbatrfgvba pnershyyl. Ol fjvgpuvat ba gur unmneq jneavat yvtug, gur sbyybjvat genssvp jvyy or jnearq bs gur qnatre.<\/c>";
		dbTblQ[2443].info = "<c>Gur ynlbhg bs gur oraq pna bayl or thrffrq; vg pbhyq fgvyy aneebj. Gur oraq vf qvssvphyg gb frr orpnhfr bs gur irtrgngvba. Na bofgnpyr ba gur ebnq pnaabg or ehyrq bhg.<\/c>";
		dbTblQ[2447].info = "<c>Ba n guerr-ynar zbgbejnl, gur erfphr ynar vf sbezrq orgjrra gur yrsg naq zvqqyr ynarf. Vg vf guhf vzcbegnag gb qevir nf sne gb gur evtug nf cbffvoyr.<\/c>";
		dbTblQ[2448].info = "<c>N genssvp wnz unf sbezrq ba gur rkvg ynar gung cebgehqrf vagb gur uneq fubhyqre. Qevivat ba gur uneq fubhyqre vf abg crezvggrq, juvpu vf jul vg vf arprffnel gb pbagvahr qevivat va beqre gb hfr gur arkg rkvg, sbe rknzcyr.<\/c>";
		dbTblQ[2449].info = "<c>Gur punffvf fubhyq or nqwhfgrq sbe gur nqqvgvbany jrvtug bs gur nqqvgvbany crefba. Gur gler cerffher fubhyq nyfb or nqwhfgrq nppbeqvatyl. Gur cnffratre fubhyq nyfb jrne cebgrpgvir pybguvat.<\/c>";
		dbTblQ[2450].info = "";
		dbTblQ[2451].info = "<c>Gur \"ulqebtra svyyvat fgngvba\" flzoby vaqvpngrf n ulqebtra svyyvat fgngvba.<\/c>";
		dbTblQ[2452].info = "<c>Gur \"angheny tnf svyyvat fgngvba\" flzoby vaqvpngrf n angheny tnf svyyvat fgngvba.<\/c>";
		dbTblQ[2453].info = "<c>Gur \"Nhgbtnfgnaxfgryyr\" (nhgb tnf svyyvat fgngvba) fvta vaqvpngrf na nhgb tnf svyyvat fgngvba.<\/c>";
		dbTblQ[2454].info = "<c>Gur genssvp fvta vaqvpngrf n punetvat fgngvba sbe ryrpgevp iruvpyrf.<\/c>";
		dbTblQ[2455].info = "<c>Guvf genssvp fvta vaqvpngrf n pne cnex gung unf npprff gb choyvp genafcbeg (cnexvat naq geniry).<\/c>";
		dbTblQ[2456].info = "<c>Jvagre glerf ner qrfvtarq sbe qevivat ng ybj grzcrengherf. Gler jrne vapernfrf ng uvture grzcrengherf. Ng gur fnzr gvzr, uvtu ebyyvat erfvfgnaprf bpphe, erfhygvat va uvture shry pbafhzcgvba.<\/c>";
		dbTblQ[2457].info = "<c>Jnfgr jngre vf gerngrq ng n pnejnfu, juvpu abgvprnoyl erqhprf gur oheqra ba gur raivebazrag.<\/c>";
		dbTblQ[2458].info = "<c>Ryrpgevp pbafhzcgvba nyfb vapernfrf shry pbafhzcgvba orpnhfr guvf fhowrpgf gur nygreangbe gb terngre qrznaqf. Nppbeqvatyl, gur npgvingvba bs urngrq frngvat yrnqf gb uvture shry pbafhzcgvba. N fjvgpurq ba nve pbaqvgvbare unf gur fnzr rssrpg.<\/c>";
		dbTblQ[2459].info = "";
		dbTblQ[2460].info = "<c>Nalbar qevivat n iruvpyr zhfg orunir va fhpu n jnl gung gurer vf ab qnatre gb bgure zbgbevfgf jura gheavat vagb n cebcregl, jura gheavat naq jura erirefvat. Vs arprffnel, gurl zhfg yrg gurzfryirf or qverpgrq. Vs gur yvar-bs-fvtug bs gur crefba qverpgvat vf uvaqrerq be oebxra, fgbc.<\/c>";
		dbTblQ[2461].info = "<c>Vs gur iruvpyr chyyf gb gur fvqr jura oenxvat, guvf vaqvpngrf grpuavpny ceboyrzf jvgu gur oenxr flfgrz. Nf guvf vzcnvef fnsrgl, n fcrpvnyvfg ng n pne jbexfubc zhfg or pbafhygrq.<\/c>";
		dbTblQ[2463].info = "<c>Vg vf gbb yngr gb punatr gur ynar; gur yvar vf haqvivqrq. Fgnl va gur yrsg ynar naq rvgure qevir fgenvtug nurnq be ghea.<\/c>";
		dbTblQ[2464].info = "<c>Gur pne fhqqrayl gheavat erqhprf gur fnsrgl qvfgnapr. Lbh zhfg oenxr gb erfgber na nccebcevngr fnsrgl qvfgnapr.<\/c>";
		dbTblQ[2465].info = "<c>Sebz gur yrsg, n zbgbeplpyr vf nccebnpuvat ng uvtu fcrrq naq gheavat bss jvgubhg lvryqvat. Qrsrafvir qevivat orunivbhe vf erdhverq, zrnavat oenxvat vf erdhverq gb cerirag n pbyyvfvba.<\/c>";
		dbTblQ[2467].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Gur zbgbeplpyvfg guhf unf cevbevgl.<\/c>";
		dbTblQ[2468].info = "<c>Nygubhtu cevbevgl vf tvira gb gur genpgbe, vg vf arprffnel sbe gubfr gheavat yrsg gb tvir cevbevgl gb gur nccebnpuvat genz.<\/c>";
		dbTblQ[2472].info = "<c>Gur genssvp fvta \"Fgbc! Tvir jnl!\" (fgbc fvta) beqref gb fgbc naq tvir jnl.<\/c>";
		dbTblQ[2473].info = "<c>Qhr gb gur genssvp fvta \"Fgbc! Nyybj evtug bs jnl\", vg vf arprffnel gb fgbc naq nyybj evtug bs jnl. Gurer vf ab fgbc yvar, fb lbh zhfg fgbc ng gur yvar bs fvtug.<\/c>";
		dbTblQ[2474].info = "<c>Gur genssvp fvta \"Fgbc! Tvir jnl\" beqref lbh gb fgbc ng gur fgbc yvar naq tvir jnl.<\/c>";
		dbTblQ[2475].info = "<c>Ybnqvat naq haybnqvat iruvpyrf hfhnyyl gnxrf ybatre naq qbrf abg erdhver gur ratvar gb eha. Gurersber, gur ratvar zhfg or fjvgpurq bss. Gur fnzr nccyvrf gb jnvgvat ng yriry pebffvatf.<\/c>";
		dbTblQ[2476].info = "<c>Jura evqvat jvgubhg zbgbeplpyr cebgrpgvir pybguvat, gurer vf n uvture evfx bs vawhel va gur rirag bs n snyy. Gur cebgrpgvba ntnvafg jrngure vasyhraprf vf ybjre. Va gur rirag bs n pynvz, gurer znl or na vafhenapr qvfnqinagntr.<\/c>";
		dbTblQ[2477].info = "<c>Jrnevat rlr-pngpuvat cebgrpgvir pybguvat znxrf gur zbgbeplpyvfg zber ivfvoyr gb bgure zbgbevfgf. Jrnevat uvtuyl pbafcvphbhf pybguvat erqhprf gur evfx bs nppvqragf.<\/c>";
		dbTblQ[2478].info = "<c>Va beqre gb or frra orggre qhevat gur qnl, pbafcvphbhf zbgbeplpyr cebgrpgvir pybguvat fubhyq or jbea. Qevivat jvgu qvccrq urnqyvtugf be qnlgvzr ehaavat yvtugf pbagevohgrf gb vzcebirq ivfvovyvgl qhevat gur qnl.<\/c>";
		dbTblQ[2479].info = "";
		dbTblQ[2480].info = "<c>Ol pbzovavat frireny qrfgvangvbaf, ybatre fvatyr gevcf pna or nibvqrq. Pnecbbyvat nyfb erqhprf artngvir raivebazragny vzcnpgf. Gur fnzr nccyvrf gb gur hfr bs choyvp genafcbeg.<\/c>";
		dbTblQ[2481].info = "<c>Ba fybcrf, vg vf cbffvoyr gb hfr gur oenxvat rssrpg bs gur ratvar ol qevivat va n pbeerfcbaqvatyl ybj trne.<\/c>";
		dbTblQ[2482].info = "<c>Nyy gjb- naq guerr-jurry zbgbe iruvpyrf ner crezvggrq gb ragre raivebazragny mbarf qrfcvgr gur ynpx bs n svar-cnegvpyr onqtr. Gur fnzr tbrf sbe nyy ntevphygheny naq sberfgel genpgbef.<\/c>";
		dbTblQ[2483].info = "<c>Rzvffvbaf pynff, shry pbafhzcgvba naq rssvpvrapl pynff nyybj pbapyhfvbaf gb or qenja nobhg gur raivebazragny sevraqyvarff bs n cnffratre pne.<\/c>";
		dbTblQ[2484].info = "<c>Qevivat jvgu n pbyq ratvar erfhygf va uvture zngrevny jrne. Va nqqvgvba, shry pbafhzcgvba vf vapernfrq.<\/c>";
		dbTblQ[2485].info = "<c>Ubaxvat nf n terrgvat vf nibvqnoyr abvfr, nf vf gur ybhq fynzzvat bs iruvpyr qbbef.<\/c>";
		dbTblQ[2486].info = "";
		dbTblQ[2489].info = "";
		dbTblQ[2490].info = "";
		dbTblQ[2491].info = "<c>Rira jvgu n unaqf-serr flfgrz, znxvat n cubar pnyy juvyr qevivat pna or n evfxl qvfgenpgvba. Gur fnzr nccyvrf gb bcrengvat n anivtngvba qrivpr juvyr qevivat naq hfvat zbovyr cubarf, vapyhqvat jevgvat grkg zrffntrf.<\/c>";
		dbTblQ[2495].info = "<c>Gurer vf n evfx gung crqrfgevnaf jvyy or pneryrff naq bireybbx bapbzvat iruvpyrf. Va nqqvgvba, crqrfgevnaf pbhyq fgbc ng nal gvzr be ghea onpx unys-jnl.<\/c>";
		dbTblQ[2497].info = "<c>Gur plpyvfg biregnxrf gur cnexrq gehpx. Oruvaq gur gehpx, gur pbhefr bs gur ebnq vf qvssvphyg gb frr qhr gb gur oraq. Gurersber, vg vf arprffnel gb erznva oruvaq gur plpyvfg hagvy nsgre gur oraq naq gura biregnxr gur plpyvfg vs arprffnel.<\/c>";
		dbTblQ[2498].info = "<c>Gur plpyvfg pbzvat sebz gur evtug vf qevivat ba gur cevbevgl ebnq. Jr zhfg jnvg naq tvir ure evtug bs jnl.<\/c>\r\n<c>Gur terra pne fgnaqf naq jnvgf gb abg oybpx gur vagrefrpgvba.<\/c>";
		dbTblQ[2499].info = "<c>Gur zbgbevfg qevivat gur erq pne vf qevivat ba gur cevbevgl ebnq. Va fcvgr bs fgnaqvat iruvpyrf va sebag bs uvz, ur jvyy abg jnvg, orpnhfr ur jnagf gb ghea naq fvtanyf guvf jvgu uvf vaqvpngbef. Lbh unir gb jnvg naq tvir cevbevgl gb gur erq pne.<\/c>";
		dbTblQ[2500].info = "<c>N qbt ehaf bagb gur ebnqjnl sebz gur evtug fubegyl orsber zr. V nz nyybjrq gb oenxr va n pbagebyyrq znaare, ohg n unmneq oenxvat pbhyq yrnq gb n pbyyvfvba jvgu gur pne qevivat pybfr oruvaq zr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fjreivat vf abg na bcgvba. Qhr gb bapbzvat genssvp naq gur qvgpu, guvf jbhyq or gbb qnatrebhf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guvf dhrfgvba zhfg abg or pbashfrq jvgu dhrfgvba 1.1.07-156. Gurer, gur qvfgnapr gb gur qbt vf terngre naq gurer vf ab iruvpyr oruvaq zr.<\/c>";
		dbTblQ[2504].info = "<c>Gur crqrfgevna vf nyernql pebffvat gur fgerrg naq unf cevbevgl; zbgbevfgf zhfg jnvg. Gur plpyvfg zhfg abg or biregnxra, fb lbh unir gb jnvg hagvy fur unf pebffrq gur ebnq nf jryy.<\/c>";
		dbTblQ[2505].info = "<c>Gur plpyr cngu raqf naq gur plpyvfg pbhyq vzzrqvngryl fjvgpu gb gur fgerrg.<\/c>";
		dbTblQ[2506].info = "<c>Gur oyhr gehpx vf oybpxvat gur ynar naq vg vf nyfb erfgevpgvat ivfvovyvgl pbafvqrenoyl. Vg vf arprffnel gb oenxr va beqre gb nccebnpu vg pnershyyl. N tneontr gehpx vf pbzvat sebz gur evtug, naq vg pbhyq pebff gur ebnq.<\/c>";
		dbTblQ[2507].info = "<c>Gur ragver ebnqjnl vf n bar-jnl fgerrg naq vf guhf bayl inyvq sbe bar qverpgvba. Guvf nyybjf cnexvat va gur qverpgvba bs geniry ba obgu gur yrsg-unaq fubhyqre naq evtug-unaq fubhyqre.<\/c>";
		dbTblQ[2508].info = "<c>Gubfr vaibyirq va gur nppvqrag zhfg fgbc naq nfpregnva gur pbafrdhraprf bs gur nppvqrag. Gur nppvqrag fvgr zhfg gura or frpherq. Bayl gura pna uryc or cebivqrq vs nalbar vf vawherq.<\/c>";
		dbTblQ[2509].info = "<c>Gur vafgehpgvbaf bs gur cbyvpr nccyl gb gur iruvpyr vzzrqvngryl sbyybjvat vg. Fb vg vf arprffnel gb fgnl oruvaq gur cbyvpr pne naq sbyybj vg. Vs gur cbyvpr pne fgbcf, lbhe iruvpyr zhfg nyfb fgbc.<\/c>";
		dbTblQ[2510].info = "<c>Fhpu na vafgehpgvba tvira ol gur cbyvpr nccyvrf gb gur vzzrqvngryl sbyybjvat iruvpyr.<\/c>";
		dbTblQ[2511].info = "<c>Cbyvpr bssvpref znl fgbc zbgbevfgf sbe genssvp purpxf, vapyhqvat ebnqjbeguvarff purpxf, naq sbe ebnq fheirlf. N cbyvpr bssvpre znl nyfb tvir n fvta gb fgbc ol zrnaf bs nccebcevngr grpuavpny rdhvczrag ba gur rzretrapl iruvpyr, na vaqvpngbe fvtanyyvat qvfp be n erq yvtug. Gurersber, vg vf arprffnel gb sbyybj gur iruvpyr hagvy vg fgbcf. Fvapr vg vf arprffnel gb sbyybj, biregnxvat vf abg crezvggrq.<\/c>";
		dbTblQ[2512].info = "<c>Gur yvtug vf bhg bs beqre. Gurersber, gur genssvp fvtaf gung tenag gur evtug bs jnl gb pebff genssvp ner inyvq. Cevbevgl zhfg guhf or tvira gb pebff-genssvp.<\/c>";
		dbTblQ[2513].info = "<c>Gur pbzovangvba bs genssvp fvtaf cbvagf gb n cevbevgl ebnq gung oraqf gb gur yrsg.<\/c>";
		dbTblQ[2515].info = "<c>Va sberfg nernf, ivfvovyvgl vf bsgra erfgevpgrq, naq pebffvat jvyq navznyf va cnegvphyne ner bayl erpbtavfrq ng n yngr fgntr. Oruvaq uvyygbcf, n oebxra-qbja iruvpyr be n irel fybj iruvpyr pna fhqqrayl bpphcl gur ebnq. Va sebag bs fpubbyf, gurer vf nyjnlf gur evfx gung puvyqera jvyy harkcrpgrqyl eha bagb gur ebnq.<\/c>";
		dbTblQ[2516].info = "<c>Gurer vf n evfx bs n pbyyvfvba jvgu na bapbzvat pne va lbhe bja ynar. Oenxvat vapernfrf gur gvzr jvaqbj fb gung gur oyhr pne pna fgvyy zretr va gvzr.<\/c>";
		dbTblQ[2517].info = "<c>Gur iruvpyr sbyybjvat lbhe iruvpyr vf qevivat gbb pybfr. Vg vf nqivfnoyr gb znxr ebbz sbe vg gb biregnxr. Orsber gung, ubjrire, vg vf arprffnel gb fnsryl pbzcyrgr bar'f bja biregnxvat znabrhier, gurerol biregnxvat obgu gur erq ina naq gur oyhr pne.<\/c>";
		dbTblQ[2518].info = "";
		dbTblQ[2519].info = "<c>Gur gehpx pbzvat sebz gur yrsg zhfg or tvira evtug bs jnl. Gurersber, vg vf arprffnel gb erqhpr lbhe bja fcrrq.<\/c>";
		dbTblQ[2521].info = "<c>Gur oyhr pne chyyf vagb gur ynar erqhpvat vgf fnsrgl qvfgnapr. N ernfbanoyr fnsrgl qvfgnapr pna or erfgberq ol fybjvat qbja.<\/c>";
		dbTblQ[2523].info = "<c>Gur erpbzzraqrq fcrrq bs 130 xz\/u nccyvrf gb ebnqjnlf bhgfvqr bs ohvyg-hc nernf jvgu gjb znexrq ynarf be frcnengr ynarf va rnpu qverpgvba. Vg nyfb nccyvrf gb zbgbejnlf.<\/c>";
		dbTblQ[2524].info = "";
		dbTblQ[2525].info = "";
		dbTblQ[2526].info = "";
		dbTblQ[2527].info = "";
		dbTblQ[2528].info = "<c>Lbh znl bayl biregnxr vs lbhe bja fcrrq vf pbafvqrenoyl uvture guna gur fcrrq bs gur crefba lbh ner jnagvat gb biregnxr.<\/c>";
		dbTblQ[2529].info = "";
		dbTblQ[2531].info = "";
		dbTblQ[2532].info = "";
		dbTblQ[2533].info = "";
		dbTblQ[2534].info = "";
		dbTblQ[2536].info = "";
		dbTblQ[2538].info = "";
		dbTblQ[2539].info = "";
		dbTblQ[2540].info = "";
		dbTblQ[2541].info = "";
		dbTblQ[2542].info = "";
		dbTblQ[2545].info = "<c>Gur hfr bs gur prager fgnaq erdhverf na rira sybbe fcnpr. Gur zbgbeplpyr pna gvc bire vs gur fvqr gvyg vf ybj. Ba fgrrc tenqvragf, gur znff bs gur zbgbeplpyr orpbzrf abgvprnoyr naq vg vf qvssvphyg gb wnpx hc ba gur prager fgnaq.<\/c>";
		dbTblQ[2546].info = "<c>Gur erzbiny bs guebggyr, qvfratntrzrag bs gur pyhgpu naq tragyr oenxvat ner zrnfherf gb fgnovyvmr n jbooyvat zbgbeplpyr.<\/c>";
		dbTblQ[2547].info = "<c>Gur zbqrea NOF ercynprf gur sbezre vagrezvggrag oenxr, v.r., unaq naq sbbg oenxrf pna or nccyvrq jvgu shyy sbepr sbe rzretrapl oenxvat.<\/c>";
		dbTblQ[2548].info = "";
		dbTblQ[2550].info = "<c>Qrfcvgr NOF, gur oenxvat sbepr fubhyq or pnershyyl nccyvrq, nf hapbagebyyrq fgrrevat vzchyfrf pna bpphe jura oenxvat ng na natyr. Gur oenxvat sbepr vasyhraprf gur vafgnyyngvba gbedhr.<\/c>";
		dbTblQ[2551].info = "<c>Zbgbeplpyrf graq gb jbooyr, rfcrpvnyyl jura ybnqrq ba bar fvqr. Qevivat ng n irel uvtu fcrrq pna unir n fvzvyne rssrpg.<\/c>";
		dbTblQ[2552].info = "<c>Zbgbeplpyrf graq gb jbooyr, rfcrpvnyyl jura ybnqrq ba bar fvqr. Qevivat jvgu gur jebat snvevat pna unir n fvzvyne rssrpg.<\/c>";
		dbTblQ[2553].info = "";
		dbTblQ[2554].info = "";
		dbTblQ[2555].info = "";
		dbTblQ[2556].info = "";
		dbTblQ[2557].info = "";
		dbTblQ[2558].info = "<c>Gur znva gernq zhfg unir n cebsvyr qrcgu bs ng yrnfg 1.6 zz ba gur ragver pvephzsrerapr. Gur znva gernq cnggrea vf pbafvqrerq gb or gur jvqr gernq tebbirf va gur zvqqyr bs gur gernq, juvpu bpphcvrf nobhg 3\/4 bs gur gernq jvqgu.<\/c>";
		dbTblQ[2559].info = "<c>Nzbat bgure guvatf, nagvpvcngbel qevivat nvzf gb vqragvsl gur vagragvbaf bs bgure ebnq hfref nf rneyl nf cbffvoyr. Guvf znxrf vg cbffvoyr gb ernpg gb punatrf va gur genssvp fvghngvba va n gvzryl znaare. <\/c>";
		dbTblQ[2561].info = "<c>Gurer vf n zbgbeplpyvfg gb gur yrsg bs lbhe iruvpyr. Vg vf vzcbegnag gb xrrc na rlr ba uvz. Nqqvgvbanyyl, lbh unir gb cnl nggragvba gb gur crqrfgevna orpnhfr ur jvyy fgvyy or ba gur ebnq jura gur genssvp yvtugf punatr.<\/c>";
		dbTblQ[2562].info = "<c>Gur genssvp fvta \"Pebffvat be whapgvba jvgu cevbevgl gb iruvpyrf ba gur evtug\" erthyngrf gur evtug bs jnl. Gur ehyr \"evtug orsber yrsg\" nccyvrf.<\/c>";
		dbTblQ[2563].info = "<c>Bofgehpgvba bs pebff genssvp zhfg or nibvqrq, juvpu vf jul jrngure pbaqvgvbaf (sbt, enva, fabjsnyy, rgp.) zhfg or gnxra vagb nppbhag. Gur jvqgu bs gur ebnqjnl zhfg or gnxra vagb nppbhag, orpnhfr gur jvqre vg vf be gur zber ynarf gurer ner, gur zber gvzr vf arrqrq gb pebff vg. Gur fcrrq naq qvfgnapr bs pebff genssvp zhfg or gnxra vagb nppbhag va beqre gb pebff gur ebnq fnsryl.<\/c>";
		dbTblQ[2564].info = "<c>Vg vf arprffnel gb oenxr orpnhfr vg vf abg lrg pyrne jurgure gur oyhr pne vf yrnivat gur ebhaqnobhg be pbagvahvat vgf wbhearl. Fvapr gur oyhr pne vf nyernql ng gur ebhaqnobhg, vg unf gur evtug bs jnl. Vs ur qbrf abg yrnir gur ebhaqnobhg, ur zhfg or tvira evtug bs jnl.<\/c>";
		dbTblQ[2565].info = "<c>Yrnivat gur tnc zhfg or vaqvpngrq ol vaqvpngvat. Erne genssvp zhfg or bofreirq va beqre gb nibvq raqnatrevat bgure hfref bs gur ebnq. Gur fnzr nccyvrf gb sybjvat genssvp, juvpu zhfg abg or raqnatrerq.<\/c>";
		dbTblQ[2566].info = "<c>Yvzvg znexvatf vaqvpngr, rkgraq be fubegra n fgbc be cnexvat cebuvovgvba vaqvpngrq ryfrjurer. Gur juvgr pne rkgraqf orlbaq gur znexvat naq vf cnexrq vapbeerpgyl.<\/c>";
		dbTblQ[2567].info = "<c>Ng pebffebnqf naq whapgvbaf, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Va guvf fvghngvba, nyy iruvpyrf ner vzzrqvngryl sbeprq gb tvir jnl. Gb erfbyir gur fvghngvba, n qevire zhfg tvir jnl. Sbe guvf checbfr, vg vf erpbzzraqrq gb qb jvgubhg lbhe bja evtug bs jnl naq gb fvtany gung lbh unir tvira jnl jvgu n pyrne unaq fvtany.<\/c>";
		dbTblQ[2568].info = "<c>Ng guvf vagrefrpgvba, genssvp fvtaf qb abg erthyngr gur evtug bs jnl. Gubfr pbzvat sebz gur evtug unir evtug bs jnl. Vg vf guhf arprffnel gb tvir cevbevgl gb gur erq pne. Gur oyhr pne zhfg tvir lbh cevbevgl.<\/c>";
		dbTblQ[2569].info = "<c>Gur genssvp fvta \"Evtug bs jnl ng gur arkg vagrefrpgvba be whapgvba\" erthyngrf gur evtug bs jnl. Rira vs lbh ghea yrsg, lbh unir evtug bs jnl naq gur lryybj pne zhfg jnvg sbe lbh.<\/c>";
		dbTblQ[2570].info = "<c>Genssvp fvta 1 raqf gur evtug bs jnl. Vg erdhverf zbgbevfgf gb \"tenag gur evtug bs jnl\".<\/c>";
		dbTblQ[2571].info = "<c>Gur genssvp fvta vaqvpngrf n genssvp-pnyzrq nern. Puvyqera ner nyybjrq gb cynl nyy bire gur fgerrg gurer. Crqrfgevnaf znl hfr gur ragver jvqgu bs gur ebnq. Zbgbe iruvpyrf zhfg qevir ng n jnyxvat fcrrq.<\/c>";
		dbTblQ[2572].info = "<c>Va nyy pnfrf, n ynar znl bayl or punatrq vs gurer vf ab qnatre gb bgure zbgbevfgf. Fvapr n pbyyvfvba jvgu gur erq pne vf vzzvarag jura chyyvat bhg, gur biregnxvat cebprff zhfg or fgbccrq.<\/c>";
		dbTblQ[2573].info = "<c>Vg'f gbb yngr gb fgbc biregnxvat. Fgbccvat gur cebprff bs biregnxvat pbhyq yrnq gb n qnatrebhf fvghngvba. Gurersber, gur biregnxvat cebprff zhfg or pbzcyrgrq.<\/c>";
		dbTblQ[2574].info = "<c>Na vagreehcgvba bs gur biregnxvat cebprff, juvpu jbhyq erfhyg va zbivat vagb gur ynar oruvaq gur terl ina, pbhyq yrnq gb n qnatrebhf fvghngvba.  Gurersber, gur biregnxvat cebprff zhfg or pbzcyrgrq.<\/c>";
		dbTblQ[2575].info = "<c>Nppryrengvba ba gur zretvat ynar vf crezvggrq va beqre gb zretr bagb gur zbgbejnl ng na nccebcevngr fcrrq. Vg vf crezvggrq gb biregnxr gur terra gehpx.<\/c>";
		dbTblQ[2576].info = "<c>Ntterffvba pna unir n artngvir vzcnpg ba qevivat orunivbhe naq hygvzngryl ba ebnq fnsrgl. Vg fubhyq guhf or nibvqrq.<\/c>";
		dbTblQ[2577].info = "<c>N erfcbafvoyr qevire rkcrpgf zvfgnxrf gb or znqr ol bgure zbgbevfgf. Va pnfr bs sngvthr, ur nibvqf gur hfr bs n zbgbe iruvpyr.<\/c>";
		dbTblQ[2578].info = "<c>Nyy gurfr srryvatf, obgu artngvir naq cbfvgvir, pna vasyhrapr qevivat orunivbhe ol, sbe rknzcyr, pnhfvat qvfgenpgvbaf be vapernfvat gur yvxryvubbq bs evfx-gnxvat orunivbhef.<\/c>";
		dbTblQ[2579].info = "<c>Urnil rlryvqf, vapernfrq lnjavat naq qvssvphygl cnlvat nggragvba ner nyy fvtaf bs sngvthr.<\/c>";
		dbTblQ[2580].info = "<c>Jura biregnxvat, gur znkvzhz crezvggrq fcrrq zhfg or bofreirq. Orpnhfr gur oyhr pne vf nppryrengvat naq rkprrqvat gur fcrrq yvzvg bs 130 xz\/u, gur biregnxvat cebprff zhfg or fgbccrq.<\/c>";
		dbTblQ[2581].info = "<c>Gur fcrrq qvssrerapr orgjrra gur gjb gehpxf vf fznyy. Vg pna guhf or nffhzrq gung gur biregnxvat cebprff bs gur terra gehpx jvyy gnxr ybatre. Gur oyhr pne unf pnhtug hc jvgu gur juvgr gehpx. Vg'f cbffvoyr gung guvf pne vf chyyvat bhg gb biregnxr.<\/c>";
		dbTblQ[2582].info = "<c>N cngu ba juvpu n plpyvfg vf nccebnpuvat pebffrf gur ebnq. Qhr gb gur benatr gehpx, gur fcbg vf qvssvphyg gb frr naq gur plpyvfg vf pheeragyl bhg bs fvtug. Gurersber, pner zhfg or gnxra jura biregnxvat gur gehpx.<\/c>";
		dbTblQ[2583].info = "<c>Ba vgf bja ynar, n zbgbeplpyr nccebnpurf gur bapbzvat genssvp, juvpu vf biregnxvat. Qhr gb gur fubeg qvfgnapr, lbhe bja fcrrq zhfg or erqhprq fb gung gur zbgbeplpyvfg pna chyy vagb gur ynar va gvzr.<\/c>";
		dbTblQ[2584].info = "<c>Gur iruvpyrf bs gur gehpx pbaibl ner abg qevivat ng gur fnzr fcrrq. Gur lryybj gehpx vf erqhpvat vgf qvfgnapr gb gur oyhr gehpx. Vg pbhyq fjvat bhg naq fgneg gb biregnxr.<\/c>";
		dbTblQ[2585].info = "<c>Gur oyhr pne qevirf ba gur zretvat ynar naq nppryrengrf. Vg vf arprffnel gb ranoyr uvz gb zretr.<\/c>";
		dbTblQ[2586].info = "<c>Gur bapbzvat pne jvyy ghea yrsg sebz vgf crefcrpgvir. Va guvf jnl, gur zbgbevfg perngrf fcnpr sbe gur <fgebat>zbgbeplpyvfg, jub vf oruvaq gur nhgbzbovyr<\/fgebat> naq cebonoyl jnagf gb pbagvahr fgenvtug nurnq. Gur zbgbeplpyvfg zhfg or tvira gur evtug bs jnl.<\/c>";
		dbTblQ[2588].info = "<c>Fjvgpuvat ba gur genssvp vasbezngvba ba gur enqvb pna jnea bs zbgbevfgf qevivat va gur jebat qverpgvba. Gur genssvp vasbezngvba pna nyfb vasbez lbh nobhg genssvp wnzf.<\/c>";
		dbTblQ[2589].info = "";
		dbTblQ[2590].info = "";
		dbTblQ[2591].info = "<c>Va gur pnfr bs n ybnq hc gb n urvtug bs 2.50 z ba gbc bs gur gbjvat iruvpyr, gur ybnq zhfg abg cebwrpg sbejneq bire gur iruvpyr. Va nqqvgvba, va gur pnfr bs n ybnq bs hc gb 50 pz nobir gur gbjvat iruvpyr, gur ybnq cebwrpgvba znl cebwrpg hc gb 50 pz orlbaq gur iruvpyr. Vs gur bhgrezbfg raq bs gur ybnq cebwrpgf zber guna 1 z orlbaq gur erne yvtugf bs gur iruvpyr, vg fubhyq or pyrneyl znexrq.<\/c>";
		dbTblQ[2592].info = "";
		dbTblQ[2593].info = "";
		dbTblQ[2594].info = "<c>Creznaragyl vyyhzvangrq fvtaf vaqvpngr jurgure n ynar vf pybfrq be znl or qevira ba. Lbh znl abg fgbc ba ynarf jvgu creznaragyl vyyhzvangrq fvtaf .<\/c>";
		dbTblQ[2595].info = "<c>Flzoby 1 (terra creznarag yvtug fvta) crezvgf qevivat va gur ynar. Flzoby 2 (lryybj pbagvahbhf yvtug fvtany) cebzcgf lbh gb punatr gb gur evtug.<\/c>";
		dbTblQ[2596].info = "";
		dbTblQ[2597].info = "<c>Qevivat ng n uvtu fcrrq erfhygf va uvtu shry pbafhzcgvba. Gur rzvffvba bs cbyyhgnagf nyfb vapernfrf. Gur abvfr yriry vapernfrf.<\/c>";
		dbTblQ[2598].info = "";
		dbTblQ[2603].info = "";
		dbTblQ[2604].info = "";
		dbTblQ[2605].info = "";
		dbTblQ[2607].info = "";
		dbTblQ[2608].info = "";
		dbTblQ[2609].info = "";
		dbTblQ[2610].info = "";
		dbTblQ[2611].info = "";
		dbTblQ[2612].info = "<c>Jvgubhg ergvtugravat gur jurry nggnpuzrag, gur jurry znl pbzr ybbfr sebz gur iruvpyr. Gurer vf nyfb n evfx bs qnzntr gb gur jurry.<\/c>";
		dbTblQ[2613].info = "";
		dbTblQ[2614].info = "";
		dbTblQ[2615].info = "<c>Gur qvfgnapr orgjrra gur cnexrq iruvpyr naq gur fbyvq yvar (gur ynar obhaqnel) zhfg or 3 zrgerf. Guvf nyybjf iruvpyrf gb qevir cnfg jvgubhg univat gb pebff gur fbyvq yvar.<\/c>";
		dbTblQ[2616].info = "";
		dbTblQ[2617].info = "<c>Vg vf vzcbegnag gb qevir fybjyl naq or ernql gb oenxr, orpnhfr n puvyq unf eha bagb gur ebnq oruvaq gur juvgr genafcbegre naq pbhyq pebff gur ebnq.<\/c>\r\n<c>Va nqqvgvba, gur genssvp fvghngvba vf pbashfvat orpnhfr gur crgeby fgngvba vf pnhfvat n dhrhr bs iruvpyrf ernpuvat bagb gur ebnq.<\/c>";
		dbTblQ[2618].info = "<c>Gur pbhefr bs gur ebnq vf qvssvphyg gb frr, naq bapbzvat genssvp zhfg or rkcrpgrq ng nyy gvzrf. Gur qbbe bs gur gehpx vf bcra, fb gur qevire pbhyq whzc bagb gur ebnq. Ng gur whapgvba, gurer pbhyq or genssvp fvtaf gung ner uvqqra ol gur gehpx.<\/c>";
		dbTblQ[2619].info = "<c>Vg vf abg cbffvoyr gb qevir ba gur pebffebnq. Qbvat fb jbhyq oybpx gur fgerrg naq guhf bofgehpg pebff genssvp. Gurersber, vg vf arprffnel gb erznva ng gur fgbc yvar.<\/c>";
		dbTblQ[2620].info = "<c>Qhr gb gur qrirybczrag bs fzbxr, gur fvgr bs gur nppvqrag fubhyq abg or nccebnpurq nal shegure. Gur ghaary zhfg guhf or yrsg ivn gur rzretrapl rkvg. Cyrnfr abgr gung gur vtavgvba xrl be genafcbaqre zhfg erznva va gur iruvpyr. Guvf ranoyrf rzretrapl crefbaary gb zbir gur iruvpyr va na rzretrapl.<\/c>";
		dbTblQ[2621].info = "<c>Gur genssvp fvta beqref lbh gb pbagvahr va gur yrsg qverpgvba. Gur ghea zhfg or vaqvpngrq jvgu gur vaqvpngbe.<\/c>";
		dbTblQ[2622].info = "<c>Gur genssvp fvta erthyngrf gur cnffvat bs bofgnpyrf. Va guvf pnfr, vg vf arprffnel gb qevir cnfg ba gur yrsg.<\/c>";
		dbTblQ[2623].info = "<c>Jura cnffratref ner obneqvat be qvfrzonexvat, gurl znl bayl or cnffrq ba gur evtug ng jnyxvat fcrrq naq bayl ng fhpu n qvfgnapr gung gurer vf ab qnatre gb cnffratref. Gur cngu bs gur cnffratref zhfg abg or bofgehpgrq. Vs arprffnel, nalbar jub qevirf n iruvpyr zhfg jnvg. Cnexvat vf abg crezvggrq 15 zrgerf va sebag bs naq oruvaq gur fvta; bayl fgbccvat vf crezvggrq urer.<\/c>";
		dbTblQ[2624].info = "<c>Gur genssvp fvta fgngrf lbh znl bayl pbagvahr ol gheavat evtug. Gur ghea zhfg or vaqvpngrq jvgu gur vaqvpngbe.<\/c>";
		dbTblQ[2625].info = "<c>Gur genssvp fvta vaqvpngrf n gnkv fgnaq. Pbafrdhragyl, gnkvf ner rkcrpgrq gb neevir naq qrcneg urer.<\/c>";
		dbTblQ[2626].info = "<c>Gur fvta jneavat bs n \"Qnatre cbvag\" vf fhccyrzragrq ol gur nqqvgvbany fvta gung vaqvpngrf \"Jvagre fcbegf crezvggrq\". Guvf zrnaf gung jvagre fcbegf raguhfvnfgf pna nyfb hfr gur ebnq, juvpu vf fbzrguvat gung bgure zbgbevfgf unir gb nqwhfg gb.<\/c>";
		dbTblQ[2627].info = "<c>Gur genssvp fvta vaqvpngrf n phfgbzf bssvpr.<\/c>";
		dbTblQ[2628].info = "<c>Gur genssvp fvta vaqvpngrf gur raq bs n ohvyg-hc nern.<\/c>";
		dbTblQ[2630].info = "<c>Gur qevire'f nggragvba fubhyq nyjnlf or sbphfrq ba gur qevivat npgvivgl va beqre gb unir gur iruvpyr haqre gur orfg cbffvoyr pbageby naq gb or noyr gb sbyybj gur genssvp fvghngvba jvgubhg erfgevpgvba.<\/c>";
		dbTblQ[2631].info = "<c>Nf n ehyr, gur qvfgnapr gb n iruvpyr va sebag zhfg or fhpu gung vg pna or uryq rira vs gur qevire bs gur iruvpyr va sebag bs lbh oenxrf fhqqrayl. Nal vasbezngvba gur cnffratre cebivqrf gung pbagevohgrf gb fnsrgl zhfg or gnxra frevbhfyl.<\/c>";
		dbTblQ[2632].info = "";
		dbTblQ[2633].info = "";
		dbTblQ[2634].info = "<c>Gur erq pne vf biregnxvat va n oraq gung pnaabg or frra. Qhr gb gur uvyygbc, ivfvovyvgl vf erfgevpgrq. Gurersber, bapbzvat genssvp vf bayl ivfvoyr ng n yngr fgntr.<\/c>";
		dbTblQ[2635].info = "";
		dbTblQ[2636].info = "<c>Lbh pbagvahr va gur zvqqyr ynar naq jnvg orpnhfr gur lryybj pne jvyy zretr.<\/c>";
		dbTblQ[2637].info = "<c>Gur oyhr pne va gur yrsg ynar vf fybjre. Gb nibvq biregnxvat sebz gur evtug, erqhpr lbhe bja fcrrq.<\/c>";
		dbTblQ[2638].info = "<c>Gur terl pne unf nppryrengrq ba gur zretvat ynar, biregnxra gur gehpx naq vf rkcrpgrq gb fjvgpu sebz gur yrsg gb gur zvqqyr ynar.<\/c>";
		dbTblQ[2639].info = "<c>Gur oyhr pne ba gur yrsg ynar vf fybjre. Gb nibvq biregnxvat sebz gur evtug, erqhpr lbhe bja fcrrq.<\/c>";
		dbTblQ[2640].info = "<c>Gur erq pne vf biregnxvat, naq qhr gb vgf uvtu fcrrq, vg jvyy or qvssvphyg gb qrpryrengr. Vafgrnq bs gheavat, vg nyybjf gur erq pne gb cnff svefg.<\/c>";
		dbTblQ[2641].info = "<c>Ng qhfx, va gur qnex be jura ivfvovyvgl bgurejvfr erdhverf, gur cerfpevorq yvtugvat rdhvczrag zhfg or hfrq. Guvf nyfb cregnvaf gb qevivat guebhtu n ghaary.<\/c>";
		dbTblQ[2642].info = "<c>Vs lbh zvffrq n zbgbejnl rkvg, gnxr gur arkg bar. Gheavat be fvzvyne znabrhierf ner abg crezvggrq urer.<\/c>";
		dbTblQ[2643].info = "";
		dbTblQ[2644].info = "";
		dbTblQ[2645].info = "";
		dbTblQ[2646].info = "";
		dbTblQ[2647].info = "<c>Gur ybnq zhfg nyjnlf or nqrdhngryl frpherq. Gur iruvpyr zhfg abg znxr nal abvfr gung pna or nibvqrq.<\/c>";
		dbTblQ[2648].info = "";
		dbTblQ[2649].info = "";
		dbTblQ[2651].info = "<c>Fabj naq vpr zhfg or pbzcyrgryl erzbirq sebz gur jvaqbjf gb rafher gur orfg cbffvoyr ivfvovyvgl. Nqqvgvbanyyl, fabj naq vpr zhfg or erzbirq sebz gur zveebef. Fabj naq vpr zhfg nyfb or erzbirq sebz gur ebbs fb gung pbzcbaragf qb abg pbzr ybbfr qhevat gur wbhearl naq raqnatre bgure zbgbevfgf.<\/c>";
		dbTblQ[2652].info = "<c>Fabj naq vpr zhfg or pbzcyrgryl erzbirq sebz gur jvaqbjf naq zveebef gb rafher gur orfg cbffvoyr ivfvovyvgl.  Nqqvgvbanyyl, fabj naq vpr zhfg or erzbirq sebz gur yvtugvat flfgrzf.<\/c>";
		dbTblQ[2653].info = "";
		dbTblQ[2654].info = "<c>Vg vf abg gur qvzrafvbaf bs gur genvyre ohg gur npghny ivfvovyvgl pbaqvgvbaf gung qrgrezvar jurgure nqqvgvbany erne-ivrj zveebef fubhyq or svggrq gb gur iruvpyr. Nf fbba nf abg nyy rffragvny genssvp cebprffrf pna or bofreirq, nqqvgvbany zveebef ner arprffnel.<\/c>";
		dbTblQ[2655].info = "";
		dbTblQ[2656].info = "";
		dbTblQ[2657].info = "";
		dbTblQ[2658].info = "";
		dbTblQ[2659].info = "<c>Gur genssvp fvta vaqvpngrf gur znkvzhz jvqgu bs gur iruvpyr (vapyhqvat ybnq naq zveebef) gung vf crezvggrq sbe gur hfr bs gur yrsg ynar.<\/c>";
		dbTblQ[2660].info = "<c>Qhr gb gur uvture jrvtug, gur erne oenxr pna or nccyvrq zber fgebatyl. Birenyy, zber oenxvat sbepr vf erdhverq qhr gb gur vapernfrq znff.<\/c>";
		dbTblQ[2661].info = "<c>Iruvpyrf jvgubhg NOF ner qvssvphyg gb pbageby jura oenxvat naq graq gb oernx bhg rneyl. Jvgu NOF, fgrrevat novyvgl vf znvagnvarq sbe ybatre.<\/c>";
		dbTblQ[2662].info = "<c>Vapbeerpg ybnqvat pna pnhfr craqhyne zbirzragf. N shegure pnhfr pna or vapbeerpgyl onynaprq jurryf.<\/c>";
		dbTblQ[2663].info = "";
		dbTblQ[2664].info = "";
		dbTblQ[2665].info = "";
		dbTblQ[2666].info = "<c>Bvy ba gur ebnq fhesnpr pna fvtavsvpnagyl vzcnve ebnq tevc naq guhf pnhfr gur iruvpyr gb fjreir.<\/c>\r\n<c>Gurersber, fgebat oenxvat naq wrexl fgrrevat fubhyq or nibvqrq. Gur sver oevtnqr zhfg or vasbezrq fb gung gurl pna erzbir gur genpr bs bvy.<\/c>";
		dbTblQ[2667].info = "<c>Gur iruvpyr va sebag fcynfurf jngre, juvpu pna vzcnve ivfvovyvgl. Cnirzragf jvgu pbooyrfgbarf pna unir ynetre hariraarff, gur qrcgu bs juvpu vf rira zber qvssvphyg gb nffrff va jrg pbaqvgvbaf (orpnhfr bs gur chqqyrf).<\/c>\r\n<c>Gurfr pna or frra yngre orpnhfr bs gur iruvpyr nurnq bs lbh. Gur erfhygvat jrgarff nyfb znxrf gur oenxvat qvfgnapr ybatre.<\/c>";
		dbTblQ[2668].info = "<c>Va erfvqragvny nernf, gurer vf nyjnlf gur evfx gung puvyqera znl fhqqrayl nccrne naq eha bagb gur ebnq.<\/c>\r\n<c>Gur fgenvtug ebnq fhesnpr naq n pyrne ivrj guebhtu n ynpx bs bofgnpyrf, r.t., cnexrq iruvpyrf, znl yrnq gb na vapernfrq qevivat fcrrq.<\/c>\r\n<c>Gur ebnqjnl naq vgf fheebhaqvatf vapernfr gur evfx bs bireybbxvat whapgvbaf.<\/c>";
		dbTblQ[2669].info = "<c>Gur yvtugf bs bapbzvat genssvp pna qnmmyr naq guhf vzcnve ivfvovyvgl. Gurersber, vg vf fnsre gb sbphf ba gur evtug-unaq fvqr bs gur ebnq. Fnsrgl vasbezngvba zhfg or bofreirq va beqre gb or noyr gb ernpg vzzrqvngryl va na rzretrapl.<\/c>";
		dbTblQ[2670].info = "<c>Nypbuby pna erqhpr gur svryq bs ivfvba rira va fznyy dhnagvgvrf. Ng gur fnzr gvzr, vg vzcnvef fcngvny ivfvba.<\/c>";
		dbTblQ[2672].info = "<c>Rira gur bar-bss hfr bs qehtf pna unir na vzzrqvngr rssrpg ba gur obql naq frireryl vzcnve frafbel creprcgvba. Guvf znxrf gur crefba pbaprearq hasvg gb qevir. Gur qhengvba bs guvf rssrpg znl or frireny ubhef be rira ybatre.<\/c>";
		dbTblQ[2673].info = "<c>Gur pbafhzcgvba bs pnaanovf pna or rivqrag sbe frireny jrrxf nsgre vatrfgvba, r.t., guebhtu qrtenqngvba cebqhpgf va gur obql.<\/c>";
		dbTblQ[2674].info = "<c>Vg vf arprffnel gb fjvgpu gb gur ynar bs bapbzvat genssvp. Vs lbh jnag gb qevir cnfg n aneebjvat bs gur ebnqjnl, na bofgnpyr ba gur ebnqjnl be n fgbccvat iruvpyr ba gur yrsg, lbh zhfg nyybj bapbzvat iruvpyrf gb cnff. Va guvf pnfr, jnvg gb frr vs bapbzvat genssvp vf nccebnpuvat. Vs gurer vf ab bapbzvat genssvp, lbh znl cebprrq.<\/c>";
		dbTblQ[2675].info = "<c>Vs lbh jnag gb ghea, lbh zhfg fvtany guvf pyrneyl naq va n gvzryl znaare ol hfvat gur vaqvpngbef. Vs lbh jnag gb ghea evtug, lbh unir gb cbfvgvba lbhe iruvpyr nf sne gb gur evtug nf cbffvoyr. Vs lbh jnag gb ghea yrsg, cbfvgvba lbhe iruvpyr va gur ynarf nf sne gb gur yrsg nf cbffvoyr hc gb gur zvqqyr bs gur vagrefrpgvba. Nf guvf vf n bar-jnl fgerrg naq guhf gurer vf ab bapbzvat genssvp, gur yrsgzbfg ynar fubhyq or hfrq.<\/c>";
		dbTblQ[2676].info = "<c>Gurl ner nccebnpuvat n crqrfgevna pebffvat. Lbh zhfg abg biregnxr.<\/c>";
		dbTblQ[2677].info = "<c>Gur rzretrapl ahzoref ner 110 (cbyvpr) naq 112 (rzretrapl freivprf).<\/c>";
		dbTblQ[2678].info = "<c>Jura gheavat bss, lbh pebff gur qevivat yvar bs gur nccebnpuvat zbgbeplpyvfg. Ur vf qevivat ba gur cevbevgl ebnq naq guhf unf cevbevgl.<\/c>";
		dbTblQ[2679].info = "<c>Gur bgure zbgbevfgf zhfg lvryq. Gur genpgbe zhfg lvryq orpnhfr vg unf gb jnvg sbe gur bapbzvat genssvp ba gur cevbevgl ebnq. Gur zbgbevfg qevivat gur erq pne zhfg lvryq orpnhfr ur be fur jnagf gb ragre gur cevbevgl ebnq.<\/c>";
		dbTblQ[2680].info = "<c>Nalbar qevivat n iruvpyr znl abg pebff gur pbagvahbhf yvar rira cnegvnyyl.<\/c>";
		dbTblQ[2681].info = "<c>Nggragvba: Gur nafjref naq gur fbyhgvba ner pbeerpg! Nalbar qevivat n iruvpyr znl abg pebff gur pbagvahbhf yvar rira cnegvnyyl. Nalbar qevivat n iruvpyr znl cnex ba gur ynar vs gurer vf ng yrnfg 3 z zber ynar orgjrra gur cnexrq iruvpyr naq gur ynar obhaqnel yvar. Ohg: \"... ba guvf cevbevgl ebnq ...\", gurer vf abg n 3z ynar gung erznvaf orgjrra gur cnexvat iruvpyr naq gur ynar obhaqnel.<\/c>";
		dbTblQ[2682].info = "<c>Iruvpyrf va gur evtug-unaq ynar znl pebff gur znexvat vs guvf qbrf abg raqnatre genssvp.<\/c>";
		dbTblQ[2683].info = "<c>Pebffvat gur znexrq prager yvar vf crezvggrq va beqre gb biregnxr be gb nibvq bofgnpyrf \u2013 ohg bayl vs gurer vf ab qnatre gb bgure zbgbevfgf.<\/c>";
		dbTblQ[2684].info = "<c>Frevbhf bssraprf jvyy or chavfurq jvgu gjb cbvagf. Gur erqrzcgvba bs fhpu cbvagf pna gnxr cynpr nsgre svir lrnef ng gur rneyvrfg.<\/c>";
		dbTblQ[2685].info = "<c>Gur oraq vf abg ivfvoyr, fb fcrpvny pnhgvba vf erdhverq. Na bofgnpyr pbhyq or qrgrpgrq yngr, bapbzvat genssvp pbhyq ragre lbhe ynar pbzcyrgryl be cnegvnyyl naq jvyq navznyf pbhyq eha npebff gur ebnq.<\/c>";
		dbTblQ[2686].info = "";
		dbTblQ[2687].info = "<c>Gurer vf n evfx bs n pbyyvfvba jvgu na bapbzvat pne va lbhe bja ynar. Oenxvat vapernfrf gur gvzr jvaqbj fb gung gur pne pna zbir vagb gur ynar.<\/c>";
		dbTblQ[2688].info = "<c>Gur biregnxvat cnffratre pne unf haqrerfgvzngrq gur fvghngvba. Fvapr vg unf nyzbfg biregnxra pbzcyrgryl, lbhe bja fcrrq fubhyq or erqhprq. Guvf nyybjf gur erq pne gb zbir va rneyvre. Va nqqvgvba, vg vf arprffnel gb qevir gb gur rkgerzr evtug va beqre gb perngr nqqvgvbany fcnpr.<\/c>";
		dbTblQ[2689].info = "<c>N pne vf nccebnpuvat gur ebnq sebz gur evtug. Vg vf uneq gb gryy jurgure gur qevire vf tbvat gb oenxr be xrrc tbvat. Gurersber, vg vf vzcbegnag gb xrrc n fnsr qvfgnapr va beqre gb or noyr gb qrpryrengr vzzrqvngryl be rira vavgvngr n unmneq oenxvat va gur rirag bs na rzretrapl.<\/c>";
		dbTblQ[2690].info = "<c>N juvgr pne vf nccebnpuvat gur ebnq sebz gur evtug. Qhr gb gur irtrgngvba, gur ebnq vf qvssvphyg gb frr, fb gur qevire bs gur juvgr pne znl abg frr lbhe pne va gvzr. Gur nvz vf gb erqhpr lbhe fcrrq naq gb xrrc n fnsr qvfgnapr va beqre gb or noyr gb qrpryrengr vzzrqvngryl va na rzretrapl be rira vavgvngr na rzretrapl-oenxvat znabrhier.<\/c>";
		dbTblQ[2691].info = "<c>Gjb ynarf ner zretvat. Gur ina unf nyzbfg pbzcyrgryl biregnxra naq unf gb zretr. Gur mvccre srrq-va zrgubq funyy or hfrq. Va beqre sbe gur ina gb or noyr gb zretr fnsryl, lbh zhfg fybj qbja.<\/c>";
		dbTblQ[2692].info = "<c>Gur genpgbe vf nccebnpuvat gur whapgvba ng n eryngviryl uvtu fcrrq. Vg'f cbffvoyr gung gur qevire jba'g fgbc. Gur qevire znl xrrc tbvat. Gur zbgbeplpyr va sebag znl unir gb fybj qbja qhr gb gur genpgbe.<\/c>";
		dbTblQ[2693].info = "<c>Lbh ner qevivat ng gur znkvzhz crezvggrq fcrrq. Gur erq pne qevirf pybfr hc, ohg guvf vf ab ernfba gb qevir snfgre be rira fybj qbja. Nppbeqvatyl, gur wbhearl zhfg or pbagvahrq ng gur pheerag fcrrq.<\/c>";
		dbTblQ[2695].info = "<c>Orsber gheavat bagb nabgure ebnq, gur genssvp bppheevat gurer zhfg or gnxra vagb nppbhag. Nppbeqvatyl, nggragvba zhfg or cnvq gb iruvpyrf pbzvat sebz gur yrsg. Gurl qevir va gur ynar pbeerfcbaqvat gb gur ynar vagb juvpu gurl jnag gb ghea. Nqqvgvbany nggragvba fubhyq or cnvq gb gur zbgbeplpyr gung vf pheeragyl va gur fnzr ynar.<\/c>";
		dbTblQ[2696].info = "<c>Gur ina va sebag erfgevpgf gur ivrj bs bapbzvat genssvp naq guhf nyfb gung bs gur erq pne. Vg vf cbffvoyr gung gur puvyqera znl pebff gur ebnq.<\/c>";
		dbTblQ[2697].info = "<c>Va gur rirag bs na hanibvqnoyr pbyyvfvba, oenxr nf uneq nf cbffvoyr va beqre gb erqhpr gur rkgrag bs gur pbyyvfvba. Gur qverpgvba bs geniry zhfg or znvagnvarq, nf nibvqnapr pbhyq yrnq gb pbyyvfvba jvgu bgure zbgbevfgf be bofgnpyrf (r.t., gerrf).<\/c>";
		dbTblQ[2698].info = "<c>Gur fvtantr vaqvpngrf n funec yrsg ghea. Qhr gb gur uvyygbc naq gur oraq bs gur ebnq, vg vf abg cbffvoyr gb frr jung vf tbvat ba nebhaq gur pbeare. Gurer znl or na bofgnpyr gurer.<\/c>";
		dbTblQ[2699].info = "";
		dbTblQ[2700].info = "<c>Lbh ner abg nyybjrq gb pbrepr gur crefba nurnq bs lbh. Vg vf arprffnel gb pbagvahr qevivat naq gb jnvg hagvy gurer vf na bccbeghavgl gb biregnxr.<\/c>";
		dbTblQ[2701].info = "";
		dbTblQ[2702].info = "";
		dbTblQ[2703].info = "<c>Gur zbgbeplpyvfg vf biregnxvat va gur yrsg ynar. Gb nibvq n cbffvoyr pbyyvfvba, fgbc naq ghea bayl nsgre vg unf biregnxra.<\/c>";
		dbTblQ[2704].info = "";
		dbTblQ[2705].info = "<c>Gur pneevntrjnl jvqgu vf erqhprq.  Gur genssvp fvta vaqvpngrf gur znkvzhz jvqgu bs gur iruvpyr (vapyhqvat ybnq naq zveebef) gung vf crezvggrq sbe gur hfr bs gur yrsg ynar. Gur nqqvgvbany flzoby vaqvpngrf gung gur jvqgu bs gur ebnq jvyy qrpernfr va 200 zrgerf. Gur iruvpyr jvqgu vaqvpngrq va gur iruvpyr qbphzragf qbrf abg gnxr gur rkgrevbe zveebef vagb nppbhag.<\/c>";
		dbTblQ[2706].info = "";
		dbTblQ[2710].info = "<c>Qevivat yvar ab. 3 pbeerpgyl thvqrf gur iruvpyr gb gur zbgbejnl ragenapr. Ynarf ab. 1 naq 2 jvyy gnxr lbh bagb gur zbgbejnl va gur jebat qverpgvba.<\/c>";
		dbTblQ[2711].info = "<c>Plpyvfgf evqvat r-ovxrf bsgra geniry snfgre guna plpyvfgf evqvat genqvgvbany ovplpyrf. Guvf vf jul gurve fcrrq vf bsgra haqrerfgvzngrq. R-ovxrf naq pbairagvbany ovxrf ybbx nyzbfg vqragvpny, juvpu znxrf vg qvssvphyg gb qvfgvathvfu orgjrra gurz.<\/c>";
		dbTblQ[2712].info = "<c>Pelfgny zrgu vf n uneq qeht gung pna pnhfr qryhfvbaf. Pbafhzref graq gb birerfgvzngr gurve bja cresbeznapr novyvgl. Pbafhzcgvba pna erfhyg va fhqqra rkunhfgvba.<\/c>";
		dbTblQ[2713].info = "<c>Vavgvnyyl, n ynar punatr jbhyq or arprffnel, ohg gur evtug ynar vf nyernql pbzcyrgryl bpphcvrq ol iruvpyrf. Nppbeqvatyl, n evtug ghea zhfg or nibvqrq. Lbh zhfg erznva lbhe va bja ynar rira vs guvf zrnaf n qrgbhe.<\/c>";
		dbTblQ[2714].info = "<c>Iruvpyrf zhfg jnvg va sebag bs gur Fg. Naqerj'f Pebff jura gur oneevref ner ybjrerq be pybfrq.<\/c>";
		dbTblQ[2715].info = "<c>Jura genz cnffratref ner obneqvat be qvfrzonexvat, gurl znl bayl or cnffrq ba gur evtug ng jnyxvat fcrrq naq bayl ng fhpu n qvfgnapr gung gurer vf ab qnatre gb cnffratref.  Gur cngu bs gur genz cnffratref zhfg abg or bofgehpgrq. Vs arprffnel, nalbar jub qevirf n iruvpyr zhfg jnvg. Orpnhfr gur genz fgbcf naq genz cnffratref znl pebff gur ynar, vg vf vzcbegnag gb fgnl oruvaq gur genz.<\/c>";
		dbTblQ[2716].info = "<c>Nalbar qevivat n iruvpyr znl abg hfr n zbovyr gryrcubar be pne gryrcubar vs gur zbovyr gryrcubar be gur unaqfrg bs gur pne gryrcubar zhfg or cvpxrq hc be uryq sbe guvf checbfr. Guvf qbrf abg nccyl vs gur iruvpyr vf fgngvbanel naq gur ratvar vf fjvgpurq bss va gur pnfr bs zbgbe iruvpyrf.<\/c>";
		dbTblQ[2717].info = "<c>Cnegvpvcngvba va choyvp ebnq genssvp erdhverf n ebnqjbegul iruvpyr. Zbgbevfgf zhfg nyfb cbffrff iruvpyr yvnovyvgl vafhenapr. Qevivat jvgubhg vafhenapr pbire pna or chavfurq jvgu n svar be rira vzcevfbazrag.<\/c>";
		dbTblQ[2718].info = "<c>Lbh zhfg tvir bgure cnegvpvcnagf naq vawherq cnegvrf cerfrag ng gur fprar bs gur nppvqrag lbhe anzr naq nqqerff naq zhfg cerfrag lbhe  qevivat yvprapr naq iruvpyr ertvfgengvba hcba erdhrfg.Lbh zhfg nyfb cebivqr vasbezngvba ba lbhe  yvnovyvgl vafhenapr  gb gur orfg bs lbhe xabjyrqtr.<\/c>";
		dbTblQ[2719].info = "<c>Gurer vf urnil genssvp qhevat ehfu ubhe. Guvf erfhygf va zber serdhrag nppryrengvba naq fgnegvat naq guhf pnhfrf uvture shry pbafhzcgvba.<\/c>";
		dbTblQ[2720].info = "<c>Guvf vf n dhrfgvba bayl sbe gur pynff Zbsn!<\/c>\r\n<c>N jrg qehz oenxr fubhyq or qevira pnershyyl va beqre gb qel gur oenxr.<\/c>";
		dbTblQ[2721].info = "<c>Vg vf abg pregnva jurgure gur erq pne jvyy pbzcyrgr gur biregnxvat cebprff va gvzr. Gb cerirag n pbyyvfvba, erqhpr lbhe fcrrq naq qevir sne gb gur evtug.<\/c>";
		dbTblQ[2722].info = "<c>Gur zbgbeplpyr nccebnpurf dhvpxyl naq pbhyq vzzrqvngryl chyy bhg. Biregnxvat vf bayl nyybjrq vs gur qevire qevirf ng n fvtavsvpnagyl uvture fcrrq guna gur crefba gb or biregnxra. Ubjrire, lbhe bja fcrrq vf irel ybj naq erfhygf va n ybat biregnxvat qvfgnapr.<\/c>";
		dbTblQ[2723].info = "<c>N punatr bs ynar vf abg cbffvoyr qhr gb gur genssvp fvghngvba. Fb lbh unir gb fgbc oruvaq gur gnkv.<\/c>";
		dbTblQ[2724].info = "<c>Gur erq pne vf nyernql zbivat vagb gur zvqqyr ynar. Nppbeqvatyl, vg vf arprffnel gb pbagvahr qevivat naq abg gb punatr ynarf.<\/c>";
		dbTblQ[2725].info = "<c>Qevire nffvfgnapr flfgrzf pna envfr snyfr rkcrpgngvbaf, fb gung zbgbevfgf birerfgvzngr gur cbffvovyvgvrf bs grpuabybtl. Gurer vf n qnatre gung zbgbevfgf jvyy eryl ba fhpu flfgrzf gbb terngyl naq guhf jvyy abg cnl nggragvba gb gur genssvp fvghngvba fhssvpvragyl. Gurer vf nyfb n guerng bs artyrpg bs crefbany erfcbafvovyvgl.<\/c>";
		dbTblQ[2726].info = "<c>Natre pna yvzvg gur novyvgl gb qevir, fb vg vf frafvoyr gb fvg oruvaq gur fgrrevat jurry bayl nsgre lbhe natre vf tbar.<\/c>";
		dbTblQ[2727].info = "<c>Gur fvghngvba unf pnhfrq na vaare erfgyrffarff be rira vafrphevgl, juvpu pna vzcnve gur qevire'f novyvgvrf. Na vagrezrqvngr fgbc pna uryc lbh gb pnyz qbja naq pbagvahr lbhe wbhearl fnsryl nsgrejneqf.<\/c>";
		dbTblQ[2728].info = "<c>Ybat jbexvat qnlf zrna n ybg bs culfvpny fgenva. Nppbeqvatyl, n erqhprq novyvgl gb pbapragengr vf cbffvoyr. Gur cerivbhf fgenva pna or rkunhfgvir naq guhf yrnq gb encvq sngvthr.<\/c>";
		dbTblQ[2729].info = "<c>Vg jbhyq or jebat gb or cebibxrq naq gnxr haarprffnel evfxf va ebnq genssvp. Gung vf jul vg vf orfg abg gb or qvfgheorq va fhpu fvghngvbaf.<\/c>";
		dbTblQ[2730].info = "<c>Vafgrnq bs orvat cebibxrq, ab nggragvba fubhyq or cnvq gb fhpu orunivbhe.<\/c>";
		dbTblQ[2731].info = "<c>Qhr gb gur tbbq ebnq fhesnpr naq ivfvovyvgl, lbh znl qevir ng n fcrrq juvpu nyybjf oenxvat jvguva gur ivfvoyr qvfgnapr.<\/c>";
		dbTblQ[2732].info = "<c>Gurer vf gur cbffvovyvgl gung gur zbgbevfg qevivat gur oyhr pne nyfb jnagf gb biregnxr naq vf guhf punatvat ynarf. Nppbeqvatyl, vg vf fgvyy arprffnel gb jnvg naq frr va beqre gb nfpregnva gur fvghngvba.<\/c>";
		dbTblQ[2733].info = "<c>Envy iruvpyrf zhfg or biregnxra ba gur evtug. Bayl gubfr jub pnaabg qb guvf orpnhfr gur genpxf ner gbb sne gb gur evtug ner nyybjrq gb biregnxr ba gur yrsg. Envyjnl iruvpyrf znl nyfb or biregnxra ba gur yrsg va bar-jnl fgerrgf. Qhr gb gur qvssvphyg fcnpr naq ivfvovyvgl pbaqvgvbaf va guvf pnfr, ab biregnxvat jvyy gnxr cynpr sbe gur gvzr orvat.<\/c>";
		dbTblQ[2734].info = "<c>N ybat dhrhr bs iruvpyrf unf sbezrq. Fb gung gur iruvpyrf pna qevir cnfg, lbh fubhyq qevir gb gur ohf fgbc.<\/c>";
		dbTblQ[2735].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner evqvat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Gur zbgbeplpyr oruvaq gur erq pne vf rkcrpgrq gb pbagvahr fgenvtug nurnq naq tvir jnl. Fb jr unir gb jnvg.<\/c>";
		dbTblQ[2736].info = "";
		dbTblQ[2737].info = "<c>Ba ebnqf jvgu ng yrnfg gjb ynarf sbe bar qverpgvba, n erfphr ynar fubhyq or sbezrq gung vf vzzrqvngryl ninvynoyr gb erfphref naq bgure rzretrapl freivprf.<\/c>";
		dbTblQ[2738].info = "";
		dbTblQ[2739].info = "";
		dbTblQ[2740].info = "<c>Veerfcrpgvir bs ubj gur ybnq vf genafcbegrq, gur crezvffvoyr gbgny znff zhfg or bofreirq. N crezvffvoyr ebbs ybnq vf qrsvarq sbe rnpu iruvpyr naq zhfg abg or rkprrqrq.<\/c>";
		dbTblQ[2741].info = "<c>Zvffvat be vapbeerpgyl nqwhfgrq urnqerfgf zrna na vapernfrq evfx bs vawhel va gur rirag bs na nppvqrag. Qrcraqvat ba gur nppvqrag, frirer arpx naq urnq vawhevrf znl bpphe.<\/c>";
		dbTblQ[2742].info = "";
		dbTblQ[2743].info = "<c>Gur qngr sbe gur arkg trareny vafcrpgvba vf ragrerq va gur iruvpyr ertvfgengvba qbphzrag. Vg pna nyfb or ernq ba gur vafcrpgvba fgvpxre ybpngrq ba gur erne yvprafr cyngr.<\/c>";
		dbTblQ[2744].info = "";
		dbTblQ[2745].info = "<c>Gur qevire bs gur iruvpyr vf boyvtrq gb pneel gur vafcrpgvba ercbeg fb gung vg pna or cebqhprq hcba erdhrfg. Nygreangviryl, gur iruvpyr qbphzragf zhfg or pbeerpgrq.<\/c>";
		dbTblQ[2746].info = "";
		dbTblQ[2747].info = "";
		dbTblQ[2748].info = "";
		dbTblQ[2749].info = "<c>Haqre cbvag B.1, gur crezvffvoyr genvyre ybnq vf gb or gnxra sebz Cneg 1 bs gur iruvpyr ertvfgengvba qbphzrag \/ gur ertvfgengvba pregvsvpngr.<\/c>";
		dbTblQ[2750].info = "<c>Vasbezngvba ba gur crezvffvoyr qenjone ybnq bs n cnffratre pne pna or sbhaq va gur bcrengvat znahny. Gur vasbezngvba pna nyfb or sbhaq va Cneg V bs gur ertvfgengvba pregvsvpngr.<\/c>";
		dbTblQ[2751].info = "<c>Qevire nffvfgnapr flfgrzf pna vapernfr ebnq fnsrgl ol, sbe rknzcyr, jneavat gur qevire bs n cerivbhfyl haqrgrpgrq unmneq va n gvzryl znaare. Gurl pna nyfb fhccbeg gur qevire juvyr qevivat.<\/c>";
		dbTblQ[2752].info = "<c>Vs gur ebnq vf qvegl, n ynar qrcnegher jneavat flfgrz znl ab ybatre jbex pbeerpgyl. Gur fnzr qnatre pna nyfb nevfr vs gurer ner ab ebnq znexvatf.<\/c>";
		dbTblQ[2753].info = "<c>Nf n qevire bs na ryrpgevp pne, vg vf vzcbegnag gb xabj jung cbffvovyvgvrf ner ninvynoyr ba fvgr gb punetr gur iruvpyr'f onggrel. Vg vf nyfb vzcbegnag gb xabj jung gur enatr vf naq juvpu snpgbef vasyhrapr vg. Qeviref bs ryrpgevp pnef fubhyq xabj ubj gb orunir nsgre na nppvqrag.<\/c>";
		dbTblQ[2754].info = "<c>Qhevat ybatre wbhearlf jvgu n cheryl ryrpgevp iruvpyr, gur enatr bs gur onggrel jvyy abg or fhssvpvrag gb ernpu gur qrfgvangvba jvgubhg vagreehcgvba. Nppbeqvatyl, vg vf vzcbegnag gb xabj jurer gur onggrel pna or punetrq. Gur qevire fubhyq nyfb xabj gur arprffnel onggrel punetvat gvzr. Vg fubhyq nyfb or xabja ubj rkgerzr bhgfvqr grzcrengherf nssrpg gur enatr bs gur iruvpyr.<\/c>";
		dbTblQ[2755].info = "<c>Gur punetvat pncnpvgl bs n cbjre pbaarpgvba vf qrpvfvir sbe gur punetvat gvzr bs gur onggrel. Bayl pnoyrf nccebirq ol gur znahsnpghere fubhyq or hfrq sbe punetvat.<\/c>";
		dbTblQ[2756].info = "<c>Ryrpgevp pnef ner fvtavsvpnagyl dhvrgre qhr gb gurve zbgbef. Yvxrjvfr, ab qverpg cbyyhgnagf ner rzvggrq qhevat qevivat.<\/c>";
		dbTblQ[2757].info = "<c>Qhr gb gur dhvrg bcrengvat abvfr, ryrpgevp pnef ner bayl abgvprq yngre be abg ng nyy. Gur fnzr nyfb nccyvrf gb plpyvfgf jub qb abg fhfcrpg n iruvpyr oruvaq gurz nf n erfhyg naq guhf ghea jvgubhg cevbe abgvpr.<\/c>";
		dbTblQ[2758].info = "<c>N qrsrpgvir oenxr yvtug fjvgpu, gur shfr be pbagnpg pbhyq or gur pnhfr bs gur snvyher bs gur oenxr yvtugf gb shapgvba. Vg vf nyfb cbffvoyr gung gur yvtug fbhepr vf qrsrpgvir.<\/c>";
		dbTblQ[2759].info = "";
		dbTblQ[2760].info = "";
		dbTblQ[2761].info = "<c>Fjvgpuvat bss gur pehvfr pbageby gbb yngr pna erfhyg va gbb uvtu n fcrrq. Gurer vf n qnatre bs qevivat gbb pybfr gb bgure zbgbevfgf. Gurer vf n evfx bs gbb uvtu n pbearevat fcrrq. Gur znkvzhz crezvffvoyr fcrrq znl or rkprrqrq.<\/c>";
		dbTblQ[2762].info = "<c>Gur pehvfr pbageby urycf gb xrrc gur frg fcrrq pbafgnag. Vg pna guhf nyfb erqhpr shry pbafhzcgvba.<\/c>";
		dbTblQ[2763].info = "<c>Gur hfr bs fhpu n flfgrz erdhverf nggragvba sebz gur qevire fb gung ur be fur pna ernpg vzzrqvngryl va na rzretrapl naq, sbe rknzcyr, vapernfr gur qvfgnapr. Enva be fabjsnyy pna vzcnve gur grpuabybtl. Nf fbba nf gur oenxr be nppryrengbe crqny vf qrcerffrq, gur crqny gnxrf cerprqrapr naq bireevqrf gur nqncgvir pehvfr pbageby flfgrz.<\/c>";
		dbTblQ[2765].info = "";
		dbTblQ[2766].info = "";
		dbTblQ[2767].info = "<c>Zbgbevfgf qevivat iruvpyrf gurl ner hasnzvyvne jvgu znl orunir qvssreragyl jura oenxvat naq guhf erdhver vapernfrq pnhgvba.<\/c>\r\n<c>Bgure pragevshtny sbeprf pna nyfb bpphe jura qevivat guebhtu pheirf. Inevbhf glcrf bs bcrengvat rdhvczrag naq qvssrerag iruvpyr qvzrafvbaf nyfb erdhver vapernfrq pnhgvba.<\/c>";
		dbTblQ[2768].info = "<c>Vs genssvp vf cbbeyl bofreirq, gurer vf n evfx gung bgure ebnq hfref be riragf znl or qrgrpgrq gbb yngr be abg ng nyy.<\/c>\r\n<c>Vs lbh qevir gbb pybfr gb bgure iruvpyrf ba gur ebnq lbh evfx abg orvat noyr gb qrpryrengr snfg rabhtu va gur rirag bs na rzretrapl, juvpu pbhyq erfhyg va n pbyyvfvba.<\/c>\r\n<c>Na vanccebcevngr fcrrq hfhnyyl zrnaf gung gur qevire vf geniryyvat gbb snfg. Guvf ragnvyf frireny evfxf vapyhqvat n ybatre qvfgnapr erdhverq va beqre gb oenxr.<\/c>";
		dbTblQ[2769].info = "<c>N iruvpyr jvgu vgf znva ornz fjvgpurq ba frrzf gb or nccebnpuvat va vgf bja ynar. Erqhpvat gur fcrrq erqhprf gur evfx bs n pbyyvfvba.<\/c>\r\n<c>Bevragngvba ba gur evtug-unaq fvqr bs gur ebnq urycf gb ubyq gur ynar jvgubhg univat gb ybbx qverpgyl vagb gur yvtug fbhepr bs bapbzvat genssvp.<\/c>";
		dbTblQ[2770].info = "<c>Gurer vf n evfx gung gur fcrrq bs bapbzvat genssvp jvyy or haqrerfgvzngrq. Guvf vapernfrf gur evfx bs n pbyyvfvba. Biregnxvat vf bayl nyybjrq vs lbh pna rafher gung nal bapbzvat genssvp jvyy abg or bofgehpgrq qhevat gur ragver gvzr gung lbh ner biregnxvat.<\/c>";
		dbTblQ[2771].info = "<c>Gur plpyvfg ba gur yrsg pna tb fgenvtug nurnq naq pebff gur ebnq. Gur vagragvbaf bs gur plpyvfg ba gur evtug ner abg lrg pyrne. Lbh unir gb jngpu bhg sbe obgu plpyvfgf.<\/c>";
		dbTblQ[2772].info = "<c>Vs lbh qevir ng n fhssvpvrag qvfgnapr, lbh jvyy or noyr gb orggre haqrefgnaq gur genssvp fvghngvba naq guhf cenpgvfr n sne-fvtugrq qevivat fglyr. N fhssvpvrag qvfgnapr thnenagrrf na nqrdhngr ernpgvba gvzr (r.t., sbe oenxvat), juvpu erqhprf gur evfx bs nppvqragf.<\/c>";
		dbTblQ[2773].info = "<c>Vg vf vzcbegnag gb fgnl oruvaq gur plpyvfg. Bgurejvfr gur plpyvfg pbhyq or phg bss cbgragvnyyl erfhygvat va n pbyyvfvba.<\/c>";
		dbTblQ[2774].info = "<c>Nalbar qevivat n iruvpyr znl abg cnex ba gur ebnq vs gurer vf abg n ynar bs ng yrnfg 3 z orgjrra gur cnexrq iruvpyr naq gur ynar obhaqnel yvar. Nal crefba qevivat n iruvpyr fubhyq abg fgbc gb gur yrsg bs gur pbagvahbhf ynar obhaqnel yvar vs gurer vf n uneq fubhyqre be fcrpvny ynar ba gur evtug. Ab fgbccvat vf crezvggrq va gur fcnpr erdhverq ol envyjnl iruvpyrf.<\/c>";
		dbTblQ[2775].info = "<c>Rkprrqvat gur nkyr ybnqf vf abg crezvggrq orpnhfr vg fvtavsvpnagyl vapernfrf gur evfx bs nppvqragf. Gur jrvtug bs gur ybnq zhfg or gnxra vagb nppbhag nppbeqvatyl. Zbgbevfgf ner boyvtrq gb nqrdhngryl frpher gur ybnq ntnvafg fyvccvat. Vs gur ybnq fyvcf, guvf pbhyq vzcnve fnsrgl.<\/c>";
		dbTblQ[2776].info = "<c>Gur plpyvfg pbagvahrf orpnhfr ur vf sbyybjvat gur cevbevgl ebnq naq orpnhfr ur vf evqvat nurnq. V zlfrys unir cevbevgl bire gur lryybj pne orpnhfr V nz pbzvat sebz gur evtug.<\/c>";
		dbTblQ[2778].info = "<c>Gur genpgbe unf fbvyrq gur ebnq. Gur pbagnzvangvba pna npg yvxr n yhoevpngvat svyz naq guhf rkgraq gur oenxvat qvfgnapr. Ng gur fnzr gvzr, gur yhoevpngvat svyz pna nyfb vzcnve qevivat fgnovyvgl be pnhfr gur iruvpyr gb oernx bhg zber dhvpxyl.<\/c>";
		dbTblQ[2779].info = "<c>N gehpx vf biregnxvat, nygubhtu bapbzvat genssvp vf nccebnpuvat. Gur qevire bs gur gehpx vf nffvfgrq ol qrpryrengvba; ur pna chyy va rneyvre. Qevivat gb gur rkgerzr evtug va na rzretrapl pna rafher gung nyy guerr iruvpyrf pna svaq fcnpr arkg gb rnpu bgure ba gur ebnq naq guhf n pbyyvfvba pna or ceriragrq.<\/c>";
		dbTblQ[2780].info = "<c>Gur fnsrgl qvfgnapr jnf fubegrarq ol gur zbgbeplpyr punatvat ynar. Gur cerfpevorq qvfgnapr pna or erfgberq ol fybjvat lbhe iruvpyr qbja.<\/c>";
		dbTblQ[2781].info = "<c>Gur lryybj pne oenxrf sbe ab nccnerag ernfba. Gur qevire cebonoyl fnj gur rkvg yngr naq jvyy guhf pebff vagb zl ynar.<\/c>";
		dbTblQ[2782].info = "<c>Gur ebnqjnl qbrfa'g unir nal ynarf yrsg. Gurersber, vg vf abg crezvffvoyr gb qevir cnfg gur oyhr pne va beqre gb or noyr gb ghea rneyvre. Vafgrnq, jr unir gb fgnl oruvaq uvz.<\/c>";
		dbTblQ[2783].info = "";
		dbTblQ[2784].info = "<c>Gur crefba biregnxvat zhfg abg or bofgehpgrq qhevat gur cebprff. Pbeerfcbaqvatyl, gur pne gheaf bss bayl nsgre gur erq pne unf biregnxra vg.<\/c>";
		dbTblQ[2787].info = "<c>Gur bapbzvat pne vf qevivat gur jebat jnl ba gur zbgbejnl. V pna qenj gur qevire&efdhb;f nggragvba gb guvf jvgu ubeaf naq yvtug fvtaf. Ol qevivat nf sne gb gur evtug nf cbffvoyr, gur evfx bs n pbyyvfvba vf erqhprq. V zhfg abgvsl gur cbyvpr.<\/c>";
		dbTblQ[2788].info = "<c>Qrpryrengvba jbhyq znxr zretvat zber qvssvphyg, nf gurer ner ahzrebhf iruvpyrf ba gur yrsg ynar. Gung'f jul vg vf vzcbegnag gb nppryrengr naq zretr va sebag bs gur gehpx.<\/c>";
		dbTblQ[2789].info = "<c>Gur oneevre cnary vaqvpngrf bar raq bs gur ynar. Gur neebjf vaqvpngr gung lbh unir gb punatr gb gur yrsg ynar. Va pnfr bs urnil genssvp, vg vf arprffnel gb qevir gb gur sebag naq gb hfr gur mvccre srrq-va zrgubq .<\/c>";
		dbTblQ[2790].info = "<c>Vs gur genssvp ba zbgbejnlf naq rkgen-heona ebnqf vf pbatrfgrq jvgu ng yrnfg gjb ynarf sbe bar qverpgvba, iruvpyrf zhfg sbez n serr ynar va gur zvqqyr bs gur qverpgvbany ynar sbe gur cnffntr bs cbyvpr naq nhkvyvnel iruvpyrf, va gur pnfr bs ynarf jvgu guerr ynarf sbe bar qverpgvba orgjrra gur yrsg naq gur zvqqyr ynar. Guvf orunivbhe vf pehpvny sbe erfphref gb trg gb gur fprar dhvpxyl.<\/c>";
		dbTblQ[2791].info = "<c>Npgvingvat gur puvyq fnsrgl ybpx rafherf gung gur pbeerfcbaqvat qbbe pna ab ybatre or bcrarq sebz gur vafvqr, guhf cebgrpgvat gur puvyq.<\/c>";
		dbTblQ[2792].info = "<c>Va gur rirag bs n punatr bs erfvqrapr, gur yvprafvat nhgubevgl zhfg or vasbezrq. Gur fnzr boyvtngvba nccyvrf va gur rirag bs n punatr bs anzr.<\/c>";
		dbTblQ[2793].info = "<c>Vs n punatr bpphef va gur bjarefuvc bs n iruvpyr, gur cerivbhf bjare zhfg abgvsl gur ertvfgengvba nhgubevgl vzzrqvngryl sbe gur checbfr bs pbeerpgvat gur iruvpyr ertvfgengvba. Va guvf pnfr, gur iruvpyr vf gb or erertvfgrerq. Va beqre sbe gur iruvpyr gb erznva ertvfgrerq, yvnovyvgl pbirentr zhfg or cebira.<\/c>";
		dbTblQ[2794].info = "<c>Fpengpurf va gur ivfbe oernx gur yvtug, juvpu yrnqf gb n snfgre tyner rssrpg. Va nqqvgvba, fpengpurf trarenyyl yrnq gb n qrgrevbengvba va ivfvovyvgl.<\/c>";
		dbTblQ[2795].info = "<c>Cebgrpgvir pybguvat pna erqhpr gur evfx bs vawhel, rfcrpvnyyl jura vg pbzrf gb noenfvbaf. Abg jrnevat gurz guhf vapernfrf gur evfx bs vawhel. Tbbq cebgrpgvir pybguvat nyfb bssref cebgrpgvba ntnvafg gur jrngure (r.t., enva). Fhpu cebgrpgvba pnaabg or bssrerq vs lbh qb abg jrne tbbq cebgrpgvir pybguvat. Vafheref ner ragvgyrq gb znxr n erqhpgvba bs gur pbzcrafngvba cebivqrq va gur rirag bs na vafherq rirag vs fhpu pybguvat jnf abg jbea.<\/c>";
		dbTblQ[2796].info = "";
		dbTblQ[2797].info = "";
