		dbTblQ[2833].info = "";
		dbTblQ[2834].info = "";
		dbTblQ[2835].info = "";
		dbTblQ[2836].info = "<c>Gur crefba vf nyernql va genvavat naq fubhyq xabj gung ur be fur vf abg nyybjrq gb qevir n iruvpyr jvgubhg n qevivat yvprapr. Guvf envfrf qbhogf nf gb uvf be ure fhvgnovyvgl sbe qevivat, naq guhf, gur qevivat yvprapr znl or qravrq.<\/c>";
		dbTblQ[2837].info = "<c>Gurer vf n evfx gung gur puvyq vf abg fgebat rabhtu gb xrrc gur qbt haqre pbageby. Guhf, gur qbt pbhyq grne njnl jvgu gur puvyq ehaavat nsgre vg. Gur qbt pbhyq nyfb eha bagb gur ebnq naq chyy gur puvyq oruvaq vg.<\/c>";
		dbTblQ[2838].info = "<c>Gb gur evtug, gur ebnq vf qvssvphyg gb frr. Gung vf jul n iruvpyr pbhyq or nccebnpuvat sebz gur evtug. Zbvfgher naq yrnirf pna vzcnve genpgvba naq guhf vapernfr gur oenxvat qvfgnapr. Gur ebnqjnl aneebjf (frr genssvp fvtaf).<\/c>";
		dbTblQ[2839].info = "<c>N qbt ehaf bagb gur ebnqjnl sebz gur evtug. Gb nibvq n pbyyvfvba, rzretrapl oenxvat vf arprffnel.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur iruvpyr zhfg or qevira fgenvtug nurnq gb nibvq n pbyyvfvba jvgu gur gehpx be gb nibvq yrnivat gur ebnqjnl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guvf dhrfgvba zhfg abg or pbashfrq jvgu dhrfgvba 1.1.07-150. Gurer, gur qvfgnapr gb gur qbt vf fubegre naq n iruvpyr vf pybfr oruvaq.<\/c>";
		dbTblQ[2840].info = "<c>Gur plpyvfg pbzvat sebz gur yrsg vf pebffvat gur vagrefrpgvba, nygubhtu ur fubhyq fgbc. Va beqre gb nibvq na nppvqrag, V oenxr uneq vzzrqvngryl.<\/c>";
		dbTblQ[2841].info = "<c>N fubeg-grez naq guhf dhvpx erfgbengvba bs lbhe qevivat novyvgl vf abg cbffvoyr nsgre pbafvqrenoyr nypbuby pbafhzcgvba. Arvgure pbssrr, jnyxvat abe n fubeg fyrrc jvyy uryc.<\/c>";
		dbTblQ[2842].info = "<c>Iruvpyrf unir gb jnvg va sebag bs gur Fg. Naqerj'f Pebff jura n envyjnl rzcyblrr beqref n fgbc.<\/c>";
		dbTblQ[2843].info = "<c>Ba ybbfr puvccvatf iruvpyrf ner ng evfx bs fyvccvat be oernxvat bhg vs gurl ner fgrrerq, fgebatyl oenxrq be vs n uvtu pragevshtny sbepr npgf va pheirf. Sbe guvf ernfba, oenxvat fubhyq or nibvqrq vs cbffvoyr va gur rirag bs harkcrpgrq ybbfr puvccvatf. Vg vf nyfb nqivfnoyr gb fgnl ba genpx.<\/c>";
		dbTblQ[2844].info = "<c>Va n oraq, gur pragevshtny sbepr npgf bhgjneqf. Pbafrdhragyl, va n yrsg ghea, gur iruvpyr jvyy or chfurq gb gur evtug. Vs gur oenxrf ner nccyvrq fgebatyl va n oraq, gur qevivat fgnovyvgl vf raqnatrerq naq gur iruvpyr znl fxvq.<\/c>";
		dbTblQ[2845].info = "<c>Gur oyhr pne (gb zl yrsg) vf gheavat gb biregnxr. Vg zhfg abg or raqnatrerq, juvpu vf jul V zhfa'g biregnxr sbe gur gvzr orvat.<\/c>\r\n<c>V erqhpr zl fcrrq gb trg gur arprffnel fnsr qvfgnapr gb gur genpgbe ntnva.<\/c>";
		dbTblQ[2846].info = "<c>Gerrf bofpher gur ivrj bs gur shegure pbhefr bs gur ebnq, fb gung vg vf abg cbffvoyr gb frr jurgure bapbzvat genssvp vf nccebnpuvat.<\/c>\r\n<c>Nf ybat nf vg vf abg pregnva gung gurer vf ab bapbzvat genssvp, gur iruvpyr zhfg abg or biregnxra.<\/c>";
		dbTblQ[2847].info = "<c>Gur ivfvovyvgl pbaqvgvbaf ner abg fhssvpvrag gb qrgrpg cbffvoyr bapbzvat genssvp ba gvzr. Gung vf jul V qb abg biregnxr gur gehpx.<\/c>";
		dbTblQ[2848].info = "<c>Gur genpgbe va sebag unf frg gur vaqvpngbe naq guhf pyrneyl fvtanyf gung vg jnagf gb ghea bss. N bapbzvat gehpx vf nccebnpuvat va gur snfg ynar.<\/c>";
		dbTblQ[2849].info = "<c>Gur genpgbe va sebag unf frg gur vaqvpngbe naq guhf pyrneyl fvtanyf gung vg jnagf gb ghea yrsg.<\/c>";
		dbTblQ[2850].info = "<c>Gur ina pbhyq chyy bhg ng nal gvzr gb biregnxr gur iruvpyr va sebag bs uvz. Qhr gb gur uvyygbc, gur ebnqjnl vf abg pbzcyrgryl ivfvoyr; bapbzvat genssvp pbhyq fhqqrayl nccrne.<\/c>";
		dbTblQ[2851].info = "<c>Cnegvpvcngvba va ebnq genssvp erdhverf pbafgnag pnhgvba naq zhghny erfcrpg. Biregnxvat bapbzvat genssvp vf nccebnpuvat ba lbhe bja ebhgr. Oenxr vzzrqvngryl naq qevir nf sne gb gur evtug nf cbffvoyr vs gur biregnxvat qevire qbrf abg fgbc uvf znabrhier.<\/c>";
		dbTblQ[2852].info = "<c>Gur genssvp fvta \"116 puvccvatf be teniry\" jneaf bs hacnirq ebnq fhesnprf naq ybgf bs puvccvatf be teniry ba gur ebnqjnl. Bar fubhyq nyjnlf qevir cnegvphyneyl nggragviryl urer.<\/c>\r\n<c>Fvapr zl iruvpyr jnf pbirerq sbe n ybat gvzr ol gur genpgbe qevivat nurnq, gur pne gheavat yrsg va gur bapbzvat ynar pbhyq bireybbx zr.<\/c>";
		dbTblQ[2853].info = "<c>Vs gur bgure ebnq hfre qbrf abg tenag cevbevgl, qrsrafvir qevivat orunivbhe vf erdhverq, v.r., fcrrq zhfg or erqhprq naq gur evtug bs jnl jnvirq va beqre gb cerirag n pbyyvfvba.<\/c>";
		dbTblQ[2854].info = "<c>Gurer vf na bofgnpyr ba gur ebnq. Gb nibvq vg, ubjrire, jbhyq or evfxl, cnegyl orpnhfr bapbzvat genssvp vf nccebnpuvat. Gurersber lbh ner abg nyybjrq gb znxr na rinfvir zbirzrag. Gur bayl jnl bhg va guvf fvghngvba vf gb fybj qbja vzzrqvngryl.<\/c>";
		dbTblQ[2856].info = "<c>Jura erirefvat, gur qevire zhfg orunir va fhpu n jnl nf gb nibvq raqnatrevat bgure zbgbevfgf. Qhr gb gur yvzvgrq ivfvovyvgl, nggragvir naq fybj qevivat vf erdhverq. Gur zbgbevfg fubhyq cevznevyl ybbx oruvaq gur iruvpyr, ohg ur be fur fubhyq nyfb ybbx gb gur fvqr naq gb gur sebag bs gur iruvpyr sbe cebgrpgvba.<\/c>";
		dbTblQ[2857].info = "<c>Rira jvgu n unaqf-serr flfgrz, qvssvphyg gryrcubar pbairefngvbaf pna nebhfr rzbgvbaf naq guhf artngviryl vasyhrapr qevivat orunivbhe.<\/c>\r\n<c>Ng gur fnzr gvzr, gurer vf gur evfx bs cnlvat gbb zhpu nggragvba gb gur pbairefngvba naq guhf ybfvat fvtug bs gur genssvp fvghngvba. Pbzcyvpngrq genssvp fvghngvbaf, fhpu nf urnil genssvp be fyvccrel ebnqf, erdhver gur qevire'f shyy nggragvba.<\/c>\r\n<c>Qb abg fcrnx ba gur cubar vs qbvat fb qvfgenpgf lbh sebz qevivat.<\/c>";
		dbTblQ[2858].info = "<c>Gur qevivat ona nccyvrf gb gehpxf jvgu n znkvzhz crezvffvoyr znff rkprrqvat 7.5 gbaarf. Gur fnzr nccyvrf gb gehpxf jvgu genvyref.<\/c>";
		dbTblQ[2859].info = "<c>Znvagnvavat gur pbeerpg gler cerffher fvtavsvpnagyl pbagevohgrf gb n ybat freivpr yvsr bs gur glerf.<\/c>\r\n<c>Nagvpvcngbel qevivat nibvqf haarprffnel oenxvat be nppryrengvba, gurerol erqhpvat gler noenfvba.<\/c>";
		dbTblQ[2860].info = "";
		dbTblQ[2861].info = "<c>Rira n arj anivtngvba flfgrz qbrf abg arprffnevyl unir gb unir hc-gb-qngr zncf.<\/c>\r\n<c>Genssvp ebhgrf ner pbafgnagyl punatvat, juvpu vf jul rira gur zbfg hc-gb-qngr znc zngrevny pnaabg or eryvrq hcba bar uhaqerq creprag.<\/c>";
		dbTblQ[2862].info = "";
		dbTblQ[2863].info = "";
		dbTblQ[2864].info = "<c>Vs lbh unir gb oenxr va n pheir, gur zbgbeplpyr jvyy jnag gb evfr ntnva. Gb pbhagrenpg guvf, lbh unir gb ghea uneqre gb xrrc gur pheir yvar.<\/c>";
		dbTblQ[2865].info = "<c>Urnil oenxvat pna yrnq gb n qrgrevbengvba bs gur ebnq tevc.<\/c>\r\n<c>Vs gur ebnq tevc vf vafhssvpvrag, gur iruvpyr znl fyvc bhg bs gur ynar.<\/c>";
		dbTblQ[2866].info = "<c>Fgebat fgrrevat gheaf pna pnhfr fhqqra pragevshtny sbeprf naq guhf cebzbgr fxvqqvat.<\/c>\r\n<c>Snfg qevivat vapernfrf pragevshtny sbeprf va oraqf naq pna gurersber vzcnve ebnq tevc.<\/c>";
		dbTblQ[2867].info = "";
		dbTblQ[2868].info = "";
		dbTblQ[2869].info = "";
		dbTblQ[2870].info = "";
		dbTblQ[2871].info = "<c>Vs gur nzbhag bs pbbynag vf gbb ybj, fhssvpvrag pbbyvat bs gur ratvar vf abg thnenagrrq. Guvf pna yrnq gb bireurngvat naq ratvar qnzntr.<\/c>";
		dbTblQ[2872].info = "";
		dbTblQ[2873].info = "";
		dbTblQ[2874].info = "<c>Gur bvy yhoevpngrf gur ratvar naq guhf cebgrpgf ntnvafg jrne.<\/c>\r\n<c>Ratvar bvy genafcbegf urng raretl shegure naq guhf fhccbegf pbbyvat.<\/c>\r\n<c>Gur bvy fhccbegf pyrnavat, rfcrpvnyyl jura punatrq erthyneyl,ol erzbivat cnegvpyrf.<\/c>";
		dbTblQ[2875].info = "<c>Gler cerffher zhfg or purpxrq nsgre rnpu gler punatr. Jnez nve rkcnaqf, gurersber gur zrnfherzrag fubhyq or znqr jvgu pbyq glerf.<\/c>";
		dbTblQ[2876].info = "<c>Gur flzoby vaqvpngrf gung gur gler vf fhvgnoyr sbe qevivat va jvagre jrngure pbaqvgvbaf.<\/c>\r\n<c>Gur gler unf punenpgrevfgvpf (ehoore pbzcbhaq naq gernq cnggrea) fcrpvnyyl qrfvtarq sbe qevivat va gur fabj naq pbyq.<\/c>";
		dbTblQ[2877].info = "<c>Rnpu gler vf fhowrpg gb n znkvzhz fcrrq yvzvg. Sbe glerf znexrq jvgu Z+F, guvf vf bsgra ybjre guna sbe fhzzre glerf jvgu vqragvpny qvzrafvbaf.<\/c>";
		dbTblQ[2878].info = "<c>Glerf gung ner inyvq sbe n znkvzhz fcrrq bs 190 xz\/u ner zbhagrq. Guvf fcrrq zhfg abg or rkprrqrq.<\/c>";
		dbTblQ[2879].info = "<c>Nsgre rnpu gler punatr, gur gler cerffher zhfg or purpxrq naq nqwhfgrq vs arprffnel.<\/c>\r\n<c>Gur znkvzhz crezvffvoyr fcrrq bs rnpu gler vf yvzvgrq naq vf hfhnyyl ybjre sbe jvagre glerf.<\/c>\r\n<c>Gur fcrrq yvzvg nccyvpnoyr gb gur gler zhfg or gnxra vagb nppbhag.<\/c>";
