		dbTblQ[2902].info = "<c>Zbgbejnlf znl bayl or hfrq ol zbgbe iruvpyrf jvgu n znkvzhz fcrrq bs zber guna <fgebat>60 xz\/u<\/fgebat>, nf qrgrezvarq ol gurve qrfvta.<\/c>\r\n<c>Gur fcrrq qrgrezvarq ol gur pbafgehpgvba glcr pna or sbhaq ba gur iruvpyr qbphzragf.<\/c>";
		dbTblQ[2903].info = "";
		dbTblQ[2904].info = "";
		dbTblQ[2905].info = "<c>Sbe ovplpyrf jvgu nhkvyvnel zbgbe (Zbsn), zbcrqf naq yvtug zbgbeplpyrf, n gernq qrcgu bs ng yrnfg 1 zz vf fhssvpvrag.<\/c>";
		dbTblQ[2906].info = "<c>Vg frrzf nf vs gur puvyq ba gur yrsg vf nobhg gb pebff gur ebnq. Yvxrjvfr, vg pnaabg or ehyrq bhg gung puvyqera ba gur evtug-unaq fvqr bs gur ebnq znl fhqqrayl fgrc vagb gur ebnq. Vg vf guhf vzcbegnag gb erqhpr lbhe fcrrq naq or ernql gb oenxr.<\/c>\r\n<c>V jvyy ersenva sebz biregnxvat sbe gur gvzr orvat.<\/c>";
		dbTblQ[2907].info = "<c>Gur crqrfgevna ba gur yrsg fvqr naq n crqrfgevna va sebag bs gur ohf (unys pbirerq) frrz gb jnag gb pebff gur ebnq. Fb V unir gb fgbc.<\/c>\r\n<c><fcna pynff='peys'><\/fcna>Gur plpyvfg pbagvahrf fgenvtug nurnq ba gur fvqrjnyx.<\/c>";
		dbTblQ[2908].info = "<c>Gur nccebnpuvat ohf pbhyq fjvat bagb lbhe fvqr bs gur ebnq.<\/c>\r\n<c>&aofc;Crefbaf ragrevat gur ebnq oruvaq n ohf zhfg nyjnlf or rkcrpgrq.<\/c>\r\n<c>Gur Dhnq pnaabg qevir bss vzzrqvngryl, fvapr n crefba fgnaqf qverpgyl orsber vg.<\/c>";
		dbTblQ[2909].info = "<c>V unir gb rkcrpg plpyvfgf gb hfr bar-jnl fgerrgf va obgu qverpgvbaf, evqr ba gur jebat fvqr bs plpyr cnguf naq unir ab yvtugvat va gur qnex.<\/c>";
		dbTblQ[2910].info = "<c>Gur plpyvfg unf gb fjvgpu gb gur ebnqjnl orpnhfr bs gur pbafgehpgvba jbex ba gur fvqrjnyx.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur qvfgnapr orgjrra gur fvqrjnyx naq gur genz vf fb fznyy gung biregnxvat vf abg cbffvoyr. V gurersber unir gb oenxr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurer vf ab genz fgbc va fvtug.<\/c>";
		dbTblQ[2911].info = "<c>Rira nf n zbcrq qevire V nyjnlf unir gb rkcrpg zvfpbaqhpg sebz bgure ebnq hfref. Nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[2912].info = "<c>Orsber trggvat vagb n ynar naq ntnva orsber gheavat, cnl nggragvba gb gur genssvp oruvaq lbh.<\/c>";
		dbTblQ[2913].info = "<c>Jura lbh ghea ba gur ebnq, lbh ner na bofgnpyr. Va sbt, va sebag bs oraqf naq va sebag bs uvyygbcf, lbh pna bayl or qrgrpgrq yngr ol bgure ebnq hfref naq gurersber cbfr n qnatre.<\/c>";
		dbTblQ[2914].info = "<c>Vs lbh jnag gb ghea, lbh unir gb yrg bapbzvat iruvpyrf cnff guebhtu. Guvf nccyvrf gb gur gehpx naq gur zbgbeplpyr oruvaq gur gehpx.<\/c>";
		dbTblQ[2915].info = "<c>Vs lbh jnag gb ghea gb gur yrsg, lbh ner bayl nyybjrq gb trg vagb yvar ba ybatvghqvanyyl ynvq envyf vs ab envy iruvpyr vf bofgehpgrq. Gung vf jul lbh unir gb jnvg.<\/c>";
		dbTblQ[2917].info = "<c>Guvf vf n dhrfgvba bayl sbe gur pynff Zbsn!&aofc; Fb lbh ner evqvat n Zbsn.<\/c>\r\n<c>Gur gehpx'f npgvat jebat, ohg V'z fgvyy jnvgvat gb nibvq na nppvqrag.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vs arprffnel V tvir n jneavat fvta.<\/c>";
		dbTblQ[2918].info = "<c>Gnkvf znl nyybj cnffratref gb obneq be nyvtug vs gur genssvp fvghngvba fb crezvgf arkg gb bgure iruvpyrf fgbccrq be cnexrq ba gur uneq fubhyqre be ba gur evtug-unaq fvqr bs gur ebnq.<\/c>";
		dbTblQ[2919].info = "<c>Sbe envy pebffvatf jvgu ybj genssvp ibyhzrf, ab grpuavpny fnsrgl zrnfherf fhpu nf yvtug fvtanyf be oneevref ner erdhverq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nyy guerr nafjref ner gurersber pbeerpg.<\/c>";
		dbTblQ[2920].info = "<c>Jura cbyvpr be nzohynaprf nccebnpu jvgu oyhr yvtugf naq Znegvafubea, lbh unir gb znxr ebbz sbe gurz vzzrqvngryl. Vs gurer vf n erq genssvp yvtug, lbh znl pnershyyl pebff gur fgbc yvar va guvf fvghngvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vs n cbyvpr bssvpre erthyngrf gur genssvp, gura nyy bgure erthyngvbaf fhpu nf fvtaf naq genssvp yvtugf ner fhfcraqrq sbe gur pbeerfcbaqvat genssvp fvghngvba.<\/c>";
		dbTblQ[2921].info = "<c>Jura znxvat n ghea, bapbzvat genssvp unf cevbevgl. Gurersber, gur lryybj iruvpyr zhfg or nyybjrq gb cnff.<\/c>\r\n<c>Gur erq pne vf ba gur znva ebnq, fb V bayl ghea nsgre gur erq pne.<\/c>";
		dbTblQ[2922].info = "<c>Gur genssvp fvta jneaf bs n qnatre cbvag. Vg vaqvpngrf gur qnatre cbfrq ol ubefrf naq evqref ba be pebffvat gur ebnq.<\/c>";
		dbTblQ[2923].info = "<c>Guvf qnatre fvta \"Trsnuefgryyr- Fpuarr- bqre Rvfty&nhzy;ggr\" jneaf bs fhqqra fabj be vpr fyvccrevarff.<\/c>";
		dbTblQ[2924].info = "<c>Gur qnatre fvta \"Trsnuefgryyr - Ivrugevro\" jneaf bs tenmvat navznyf gung znl or ba gur ebnq.<\/c>\r\n<c>Qhr gb gur pnggyr qevir, n qvegl ebnq pnaabg or ehyrq bhg.<\/c>";
		dbTblQ[2925].info = "<c>Gur qnatre fvta jneaf bs n qnatre cbvag ol puvccvatf be teniry. Guvf pna yrnq gb qrgrevbengvba va tevc, juvpu vf jul lbh fubhyq xrrc n terngre qvfgnapr orgjrra lbhe iruvpyr naq gur iruvpyr va sebag bs lbh.<\/c>\r\n<c>Nqqvgvbanyyl, fgebat oenxvat fubhyq or nibvqrq. Gur fnzr nccyvrf gb fhqqra fgrrevat zbirzragf.<\/c>";
		dbTblQ[2926].info = "<c>Gur qnatre fvta jneaf bs n zbinoyr oevqtr. Gur oevqtr pbhyq or bcra be unir fuvsgrq; gurersber, gurer vf na vagreehcgvba bs gur ebnqjnl.<\/c>";
		dbTblQ[2927].info = "<c>Gur qnatre fvta jneaf bs n onax bs n jngrejnl. Cnegvphyneyl va qnexarff, gur fhesnpr bs gur jngre znl or qvssvphyg gb qvfgvathvfu sebz gur ebnq fhesnpr.<\/c>";
		dbTblQ[2928].info = "<c>Gur qnatre fvta jneaf bs n cbgragvny qnatre. Gur nqqvgvbany fvta vaqvpngrf gur npghny qnatre, anzryl snyyvat fgbarf. Gurersber, gurer pbhyq or fgbarf ba gur ebnqjnl. Fgbccvat naq cnexvat fubhyq gurersber or nibvqrq. Gurer vf nyjnlf gur qnatre gung iruvpyrf va sebag znl fhqqrayl oenxr qhr gb snyyvat fgbarf.<\/c>";
		dbTblQ[2929].info = "<c>Gur qnatre fvta jneaf bs n cbgragvny qnatre. Gur nqqvgvbany fvta vaqvpngrf gur npghny qnatre, anzryl nve genssvp. Nve genssvp pna pnhfr fhqqra abvfr naq\/be qvfgenpg bgure zbgbevfgf.<\/c>";
		dbTblQ[2931].info = "<c>Gur ebnq frrzf gb or cnegvnyyl sybbqrq. Gur ynfg genssvp fvta jneaf bs fyvccrel ebnq, gurer vf n evfx bs fxvqqvat. Urer lbh unir gb qevir pnershyyl naq fybjyl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fgrrevat fgebatyl gb gur evtug vf gbb qnatrebhf nf gur rqtr bs gur ebnq vf hacnirq.<\/c>";
		dbTblQ[2932].info = "<c>Gur sbt nssrpgf ivfvovyvgl. Gur ivfvovyvgl vf yrff guna 50 zrgerf. Vs ivfvovyvgl qhr gb sbt, fabj be enva vf yrff guna 50 z, gur fcrrq zhfg abg rkprrq 50 xz\/u hayrff n ybjre fcrrq vf erdhverq.<\/c>\r\n<c>Erne sbt ynzcf znl bayl or hfrq jura gur ivfvovyvgl qhr gb sbt vf yrff guna 50 z. Guhf, fjvgpuvat ba gur sbt ynzcf vf crezvggrq va guvf fvghngvba.<\/c>";
		dbTblQ[2933].info = "<c>Gur bapbzvat zbgbeplpyr naq gur hapyrne pbhefr bs gur ebnq sbeovq biregnxvat ng guvf cbvag.<\/c>";
		dbTblQ[2934].info = "<c>Gur terra iruvpyr (oruvaq gur genpgbe genvyre) qevirf sebz gur evtug bagb zl ynar, nygubhtu vg fubhyq unir jnvgrq. V gurersber unir gb oenxr gb nibvq na nppvqrag.<\/c>\r\n<c><fcna pynff='peys'><\/fcna>Gur zbgbeplpyr naq gur lryybj pne tvir ab ernfba gb unir gb oenxr.<\/c>";
		dbTblQ[2935].info = "";
		dbTblQ[2936].info = "<c>Jura qevivat hcuvyy ba zbgbejnlf, gehpxf bsgra qevir dhvgr fybjyl, juvpu yrnqf gb ynetr fcrrq qvssreraprf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guvf yrnqf gb fhqqra biregnxvat znarhiref bs bgure ebnq hfref.<\/c>";
		dbTblQ[2937].info = "<c>Va gur zveebef, lbh pna frr ubj gur zbgbeplpyvfg zretrf vagb gur rkvg ynar naq gura nppryrengrf.<\/c>\r\n<c>Gur zbgbeplpyvfg ragref gur oyvaq fcbg bs gur iruvpyr, naq gur qevire bs gur iruvpyr pbhyq bireybbx gur zbgbeplpyvfg jura gheavat jvgubhg ybbxvat bire uvf be ure fubhyqre orsberunaq.<\/c>";
		dbTblQ[2938].info = "<c>Nal qvfgenpgvba juvyr qevivat pna yrnq gb trggvat bss gur ebnq be bireybbxvat n genssvp fvta.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur fgbccvat qvfgnapr znl or ybatre, ohg abg fubegre.<\/c>";
		dbTblQ[2939].info = "<c>Gur znva pnhfrf bs nppvqragf nzbat lbhat qeviref:<\/c>\r\n<hy>\r\n<yv>Ebnqfvqr nterrzragf<\/yv>\r\n<yv>Rkprffvir fcrrq va pheir<\/yv>\r\n<yv>biregnxvat reebe<\/yv>\r\n<\/hy>\r\n<c>Va nqqvgvba gb gur bsgra ynpxvat qevivat rkcrevrapr, n avtugyl qvfpb ivfvg vf sbyybjrq ol n zvfwhqtzrag bs qevivat novyvgl naq culfvpny svgarff.<\/c>";
		dbTblQ[2940].info = "<c>Bhgfvqr ivyyntrf, zbcrqf ner nyybjrq gb hfr plpyr cnguf, va heona nernf bayl vs na nqqvgvbany fvta nyybjf guvf.<\/c>";
		dbTblQ[2941].info = "<c>Jura biregnxvat n gehpx jvgu n genvyre, gur biregnxvat qvfgnapr vf ybatre naq gur jvqgu bs gur gehpx pna nyfb znxr vg zber qvssvphyg gb frr iruvpyrf nurnq naq bapbzvat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur biregnxvat pna gnxr cynpr, n pyrne ivrj naq gb gur rkpyhfvba bs bgure ebnq hfref abg bayl qverpgyl oruvaq gur gehpx.<\/c>";
		dbTblQ[2942].info = "<c>Puvyqera haqre gur ntr bs 12 jub ner fznyyre guna 150 pz znl evqr va zbgbe iruvpyrf ba frngf sbe juvpu fnsrgl orygf ner cerfpevorq bayl vs puvyq erfgenvagf ner hfrq. Guhf, va yvtug bs gur ntr bs gur puvyq, gurer vf ab jnl nebhaq gur hfr bs na nccebirq erfgenvag flfgrz.<\/c>";
		dbTblQ[2943].info = "<c>Gur ybnq, vapyhqvat ybnq frphevat rdhvczrag naq ybnqvat qrivprf, zhfg or fgbjrq naq frpherq va fhpu n jnl gung gurl qb abg fyvc rira va gur rirag bs shyy oenxvat be fhqqra rinfvir zbirzrag.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur ybnq zhfg abg bofgehpg lbh be vzcnve ebnq fnsrgl.<\/c>";
		dbTblQ[2944].info = "<c>Ybnq frphevat rdhvczrag sbe cnffratre pnef vf ynfuvat fgencf naq fnsrgl argf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jurry pubpxf frpher gur pne be genvyre, abg gur ybnq.<\/c>";
		dbTblQ[2945].info = "<c>Na nqqvgvbany ebbs ybnq punatrf gur prager bs tenivgl bs gur iruvpyr hcjneqf, gurerol vasyhrapvat gur qevivat punenpgrevfgvpf. Gur oenxvat qvfgnapr orpbzrf ybatre qhr gb gur nqqvgvbany znff naq gur pne pna nppryrengr zber fybjyl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur frafvgvivgl gb pebffjvaq vapernfrf qhr gb gur ynetre fhesnpr nern naq qbrf abg qrpernfr.<\/c>";
		dbTblQ[2946].info = "";
		dbTblQ[2947].info = "<c>Bcrengvat n anivtngvba qrivpr juvyr qevivat qvfgenpgf naq fubhyq or nibvqrq.<\/c>";
		dbTblQ[2948].info = "<c>Bcrengvat n anivtngvba qrivpr juvyr qevivat qvfgenpgf naq fubhyq or nibvqrq.<\/c>";
		dbTblQ[2949].info = "<c>Cnegvpvcngvat va vyyrtny ebnq enprf pna yrnq gb frevbhf ebnq nppvqragf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur cnegvpvcngvba pna or chavfurq jvgu hc gb gjb lrnef vzcevfbazrag (FgTO &frpg; 315q). Gur nggrzcg vf nyfb chavfunoyr ol ynj.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur qevivat yvprafr pna or jvguqenja.<\/c>";
		dbTblQ[2950].info = "";
		dbTblQ[2951].info = "";
		dbTblQ[2952].info = "<c>Lbh zhfg bofreir gur cebuvovgvbaf ba gur npghny urvtug naq ner boyvtrq gb vasbez gurzfryirf nobhg gur npghny qvzrafvbaf bs gur iruvpyr vapyhqvat ybnq.<\/c>";
		dbTblQ[2953].info = "";
		dbTblQ[2954].info = "";
		dbTblQ[2955].info = "";
		dbTblQ[2956].info = "";
		dbTblQ[2957].info = "<c>Bire gvzr, jngre npphzhyngrf va gur oenxr syhvq, juvpu punatrf gur cebcregvrf bs gur syhvq. Gurer vf n evfx gung gur oenxr flfgrz jvyy ab ybatre bcrengr eryvnoyl haqre uvtu ybnqf. Punatvat gur oenxr syhvq va n gvzryl znaare ryvzvangrf guvf qnatre.<\/c>";
		dbTblQ[2958].info = "";
		dbTblQ[2960].info = "";
		dbTblQ[2961].info = "<c>Fgnegvat sebz gur ynfg trareny vafcrpgvba, gur arkg vafcrpgvba vf qhr va 24 zbaguf.<\/c>";
		dbTblQ[2962].info = "";
		dbTblQ[2963].info = "<c>Gur puvyq ba gur evtug unf nyernql jnyxrq va sebag bs gur genafcbegre naq frrzf gb jnag gb pebff gur ebnq. Lbh fubhyq fybj qbja, va beqre gb or noyr gb fgbc ba gvzr vs arprffnel.<\/c>";
		dbTblQ[2964].info = "<c>Va fcvgr bs fabj be pbirerq \"mroen pebffvatf\", gur fvta fgvyy nccyvrf. Gur crqrfgevna zhfg guhf or noyr gb pebff. Qhr gb gur cbffvoyl fyvccrel ebnq fhesnpr, vg vf vzcbegnag gb oenxr pnershyyl.<\/c>";
		dbTblQ[2965].info = "<c>Gur qnatre fvta jneaf bs n cbgragvny qnatre, anzryl fabj be vpr. Guvf qnatre rkvfgf ng grzcrengherf bs 3 qrterrf Pryfvhf be ybjre. Gurersber, vg vf arprffnel gb erqhpr lbhe fcrrq.<\/c>";
		dbTblQ[2966].info = "<c>Nyy guerr nafjref ner pbeerpg.<\/c>\r\n<c>Biregnxvat vf abg crezvggrq vs gur genssvp fvghngvba vf hapyrne naq vs qhevat gur ragver biregnxvat cebprff nal bofgehpgvba bs bapbzvat genssvp pnaabg or rkpyhqrq.<\/c>";
		dbTblQ[2967].info = "<c>Gur plpyvfg frrzf gb xabj gur cnffref-ol, juvpu vf jul n fhqqra fgbc vf gb or rkcrpgrq.<\/c>\r\n<c>Gur plpyvfg shegure njnl yvsgf uvf yrsg nez naq frrzf gb fjvat bhg gb gur yrsg.<\/c>";
		dbTblQ[2968].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur cnexvat ybg vf jryy bpphcvrq naq pbashfvat. V unir gb or cercnerq sbe pneryrff crbcyr naq gheavat iruvpyrf.<\/c>";
		dbTblQ[2969].info = "<c>Gur genssvp fvghngvba vf pbashfvat qhr gb gur pne ba gur evtug fvqr bs gur ebnq naq erdhverf fcrpvny pnhgvba. V zhfg tvir jnl gb plpyvfgf pbzvat sebz gur evtug.<\/c>";
		dbTblQ[2970].info = "<c>V jnag gb ghea yrsg, fb V unir gb jngpu bhg sbe gur plpyvfg ba gur yrsg. Gur plpyvfg ba gur evtug frrzf gb or tbvat ba gur ebnq, fb V unir gb jngpu bhg sbe lbh gbb.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>V qb abg unir gb cnl nggragvba gb gur cnexvat pne.<\/c>";
		dbTblQ[2971].info = "<c>Ng gur ortvaavat bs gur ivqrb gur qnatre fvta \"tevg, teniry\" jnf gb or frra, gurersber lbh unir gb rkcrpg ebyyvat teniry.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur qevire bs gur gheavat pne vf bofgehpgrq va gur ivrj ol gur genpgbe naq ur zvtug bireybbx zr.<\/c>";
		dbTblQ[2972].info = "<c>Abg bayl nypbuby, ohg nyfb <fgebat>zrqvpngvba<\/fgebat> naq <fgebat>qehtf<\/fgebat> pna vzcnve gur novyvgl gb qevir.<\/c>";
		dbTblQ[2973].info = "<c>Gur pbafhzcgvba bs pnaanovf guerngraf gb vzcnve nggragvba naq pbapragengvba.<\/c>";
		dbTblQ[2974].info = "<c>Nalbar jub jnagf gb biregnxr unf gb orunir va fhpu n jnl gung gurer vf ab qnatre gb gur genssvp oruvaq uvf be ure iruvpyr. Jura biregnxvat, fhssvpvrag yngreny pyrnenapr zhfg or znvagnvarq sebz bgure zbgbevfgf, va cnegvphyne sebz crqrfgevnaf naq plpyvfgf. Gur unaq fvtanyf hfrq ol gur qevire bs gur ubefr-qenja pneevntr zhfg or bofreirq, nf gurl pna fvtany n punatr va gur qverpgvba bs geniry.<\/c>";
		dbTblQ[2975].info = "<c>FgIB 5 Nof. 4:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"Jura biregnxvat, n fhssvpvrag yngreny qvfgnapr gb bgure ebnq hfref zhfg or znvagnvarq. Jura biregnxvat ba sbbg, ol ovplpyr be ol n fznyy ryrpgevp iruvpyr, fhssvpvrag yngreny qvfgnapr zhfg or<fgebat> ng yrnfg 1.5 z va gbjaf<\/fgebat> naq <fgebat>ng yrnfg 2 z bhgfvqr gbjaf<\/fgebat>.<\/c>";
		dbTblQ[2976].info = "<c>Vs lbh jnag gb qevir cnfg n aneebjvat bs gur ebnqjnl, na bofgnpyr ba gur ebnqjnl be n fgbccvat iruvpyr ba gur yrsg, lbh zhfg nyybj bapbzvat iruvpyrf gb cnff. Guvf qbrf abg nccyl vs vaqvpngrq bgurejvfr ol genssvp fvtaf.<\/c>";
		dbTblQ[2977].info = "<c>Zbgbevfgf nccebnpuvat rnpu bgure naq jnagvat gb ghea yrsg zhfg ghea va sebag bs rnpu bgure, hayrff gur genssvp fvghngvba be gur qrfvta bs gur whapgvba erdhverf gurz abg gb ghea hagvy gur iruvpyrf unir cnffrq bar nabgure.<\/c>";
		dbTblQ[2978].info = "<c>Zbgbevfgf nccebnpuvat rnpu bgure naq jnagvat gb ghea yrsg zhfg ghea va sebag bs rnpu bgure, hayrff gur genssvp fvghngvba be gur qrfvta bs gur whapgvba erdhverf gurz abg gb ghea hagvy gur iruvpyrf unir cnffrq bar nabgure.<\/c>";
		dbTblQ[2979].info = "<c>Zbgbevfgf jub jnag gb ghea yrsg fubhyq or va gur ynarnf sne nf cbffvoyr gb gur yrsg. Nf guvf vf n ebnq jvgu bapbzvat genssvp, lbh zhfg fgnl va gur evtug ynar va guvf pnfr.<\/c>";
		dbTblQ[2980].info = "<c>Va fcvgr bs qevivat ba n cevbevgl ebnq, gur ghea zhfg or vaqvpngrq ol n ghea fvtany. Gur bapbzvat pne vf gheavat bagb nabgure ebnq naq zhfg nyfb vaqvpngr.<\/c>";
		dbTblQ[2981].info = "<c>FgIB 9 Nof. 3:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nalbar jub jnagf gb ghea zhfg nyybj bapbzvat iruvpyrf gb cnff, envy iruvpyrf, ovplpyrf jvgu nhkvyvnel zbgbef, <fgebat>ovplpyrf<\/fgebat> naq zvavngher ryrpgevp iruvpyrf <fgebat>rira vs gurl ner qevivat va gur fnzr qverpgvba ba be arkg gb gur ebnqjnl.<\/fgebat><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guvf nyfb nccyvrf gb choyvp ohfrf naq bgure iruvpyrf gung hfr znexrq fcrpvny ynarf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><fgebat>Fcrpvny nggragvba zhfg or cnvq gb crqrfgevnaf<\/fgebat>; vs arprffnel, znvagranapr zhfg or pneevrq bhg.<\/c>";
		dbTblQ[2982].info = "<c>Gb zl yrsg gurer vf na biregnxvat pne.<\/c>";
		dbTblQ[2983].info = "<c>N genzjnl vf nccebnpuvat sebz oruvaq. Fvapr lbh zvtug unir gb fgbc va gur envy nern qhr gb bapbzvat genssvp ba gur bccbfvgr ynar, lbh zhfg abg ragre gur envy nern naq bofgehpg gur genzjnl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr lbh ner ba n cevbevgl ebnq, lbh unir evtug bs jnl bire genssvp pbzvat sebz gur evtug.<\/c>";
		dbTblQ[2984].info = "<c>Gur zbgbeplpyr oruvaq gur bapbzvat iruvpyr unf evtug bs jnl naq lbh unir gb jnvg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr lbh ghea va sebag bs gur bapbzvat iruvpyr lbh qb abg unir gb jnvg orpnhfr bs guvf iruvpyr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"Bapbzvat iruvpyrf gung jnag gb ghea yrsg zhfg ghea va sebag bs rnpu bgure, ....\". (&frpg; 9 cnentencu 4 fragrapr 2 FgIB)<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr lbh ner ba n cevbevgl ebnq, lbh unir evtug bs jnl bire genssvp pbzvat sebz gur evtug.<\/c>";
		dbTblQ[2985].info = "<c>Sbe gur gehpx, ivfvovyvgl gb gur fvqr naq gb gur erne vf bsgra frireryl erfgevpgrq naq gur oyvaq fcbg vf ynetr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr gur gehpx jnagf gb ghea evtug, gur qnatre bs orvat bireybbxrq vf dhvgr uvtu.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gb nibvq n pbyyvfvba, fgbc ba gur evtug oruvaq gur gehpx.<\/c>";
		dbTblQ[2986].info = "<c>Va pregnva fvghngvbaf, gur unmneq jneavat yvtug zhfg or fjvgpurq ba. Vs n zhygv-ynar iruvpyr fgbcf ng n cbvag jurer vg pnaabg or qrgrpgrq va gvzr nf n fgngvbanel bofgnpyr, unmneq yvtugf zhfg or fjvgpurq ba vzzrqvngryl. Jura orvat gbjrq, obgu iruvpyrf zhfg fjvgpu ba gur unmneq jneavat yvtugf. Nalbar bcrengvat n erthyneyl fpurqhyrq ohf be n znexrq fpubby ohf zhfg fjvgpu ba unmneq jneavat yvtugf jura nccebnpuvat n ohf fgbc naq juvyr cnffratref ner obneqvat be nyvtugvat, cebivqrq gung gur nhgubevgl erfcbafvoyr sbe ebnq genssvp haqre angvbany ynj (ebnq genssvp nhgubevgl) unf beqrerq fhpu orunivbhe sbe pregnva ohf fgbcf.<\/c>";
		dbTblQ[2987].info = "<c>Vapbeerpgyl hfrq ohyof naq urnqyvtugf frg gbb uvtu pna qnmmyr bapbzvat genssvp. Fvzvyneyl, vapbeerpg gnvy-urnil ybnqvat pna vasyhrapr yvtugvat naq pnhfr bapbzvat genssvp gb or qnmmyrq.<\/c>";
		dbTblQ[2988].info = "<c>Lbh fgnl oruvaq gur genzjnl va beqre gb nibvq raqnatrevat gur cnffratref trggvat ba naq bss gur genzjnl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr lbh ner whfg orsber na vagrefrpgvba naq pnaabg frr guvf vagrefrpgvba naq bapbzvat genssvp, lbh zhfg abg biregnxr gur genzjnl ba gur yrsg.<\/c>";
		dbTblQ[2989].info = "<c>Bar zhfg nyjnlf nffhzr gung crqrfgevnaf qb abg nyjnlf orunir pbeerpgyl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va gur cvpgher lbh pna frr gung crqrfgevnaf naq gur plpyvfg ner nobhg gb pebff gur fgerrg. Gur genssvp vfynaq naq gur fpbepurq pheo, pna pbagevohgr gb gur snpg gung crqrfgevnaf naq gur plpyvfg guvax gurl unir cevbevgl.<\/c>";
		dbTblQ[2990].info = "<c>Gur trfgher bs bar be obgu nezf bhgfgergpurq fvqrjnlf npebff gur qverpgvba bs geniry cresbezrq ol n cbyvpr bssvpre pbagebyyvat gur genssvp vaqvpngrf: \"Fgbc ng gur vagrefrpgvba\". Guvf nccyvrf gb gur iruvpyr pbzvat sebz gur evtug.<\/c>\r\n<c>Ng gur fnzr gvzr, gur fgerrg vf bcra gb pebff genssvp, fb gung lbh zhfg pebff gur pebffvat.<\/c>";
		dbTblQ[2991].info = "<c>Vs gur genssvp yvtug vf synfuvat lryybj, cnl nggragvba gb gur genssvp fvtaf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Urer gur genssvp fvta \"Tvir jnl!\" vaqvpngrf gb gur ebnq hfre gung ur zhfg tvir jnl gb cbffvoyr pebff genssvp ng gur pebffvat.<\/c>";
		dbTblQ[2992].info = "<c>Gur erthyngvba \"evtug orsber yrsg\" qbrf abg nccyl gb iruvpyrf yrnivat pbhagel ynarf be sberfg genpxf. Fb V qevir svefg.<\/c>";
		dbTblQ[2993].info = "<c>Ng guvf vagrefrpgvba, cevbevgl vf tvira gb gubfr pbzvat sebz gur evtug. Gurersber, gur plpyvfg unf cevbevgl.<\/c>";
		dbTblQ[2994].info = "<c>Gur qnatre fvta jneaf bs n qnatre cbvag ol n pebffjnyx. Guvf pebffjnyx pna or qvssvphyg gb vqragvsl.<\/c>";
		dbTblQ[2995].info = "<c>Gur genssvp fvta vaqvpngrf n tenqvrag bs 10 cre prag. Ba qbjauvyy tenqvragf, tenivgngvbany sbeprf rafher n ybatre oenxvat qvfgnapr guna ba syng greenva.<\/c>\r\n<c>Vs gur oenxrf ner nccyvrq sbe n ybatre crevbq bs gvzr ba qbjauvyy fybcrf, gur oenxr znl urng hc pbafvqrenoyl bire gvzr, pnhfvat gur oenxvat rssrpg gb qvzvavfu.<\/c>";
		dbTblQ[2996].info = "<c>Gur genssvp fvta vaqvpngrf n tenqvrag. Fhpu na vapyvar pna or irel qrznaqvat ba gur ratvar, fb vg znl or arprffnel gb punatr qbja trne.<\/c>";
		dbTblQ[2997].info = "<c>Gur nqqvgvbany punenpgre 1000-32 zrnaf \"plpyvfgf pebffvat sebz evtug naq yrsg\".<fcna pynff='peys'><\/fcna>Fvapr ryrpgevp fpbbgref ner nyfb nyybjrq ba plpyr cnguf va Treznal, guvf nqqvgvbany fvta nyfb nccyvrf gb gurz.<\/c>";
		dbTblQ[2998].info = "<c>Urer gur genssvp fvta 244.3 \"Ortvaavat bs n ovplpyr mbar\" jvgu nqqvgvbany fvta \"erfvqragf serr\" vf fubja.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Zbcrqf ner abg ovplpyr genssvp.<\/c>\r\n<by>\r\n<yv>Iruvpyrf bgure guna ovplpyrf naq fznyy ryrpgevp iruvpyrf nf qrsvarq ol gur rXSI znl abg hfr ovplpyr mbarf hayrff guvf vf crezvggrq ol nqqvgvbany fvtaf. ...<\/yv>\r\n<yv>N znkvzhz fcrrq bs 30 xz\/u nccyvrf gb gur qevivat genssvp. Plpyvat genssvp znl arvgure or raqnatrerq abe uvaqrerq. Vs arprffnel, zbgbe iruvpyr genssvp zhfg shegure erqhpr gur fcrrq.<\/yv>\r\n<yv>Vg vf crezvggrq gb qevir arkg gb rnpu bgure jvgu ovplpyrf naq fznyy ryrpgevp iruvpyrf va gur frafr bs gur rXSI.<\/yv>\r\n<\/by>";
		dbTblQ[2999].info = "<c><fgebat><rz>FgIB &frpg;39, 5:<\/rz><\/fgebat><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><rz>Znexvatf naq plpyr thvqnapr znexvatf ner nyfb genssvp fvtaf.<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><rz>Gurl ner onfvpnyyl juvgr.<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><fgebat><rz>Bayl nf grzcbenel znexvatf gurl ner lryybj; gura gurl pnapry gur juvgr znexvatf.<\/rz><\/fgebat><\/c>";
		dbTblQ[3000].info = "<c>Urer gur genssvp fvta 244.3 \"Ortvaavat bs n ovplpyr mbar\" jvgu nqqvgvbany fvta \"erfvqragf serr\" vf fubja.<\/c>\r\n<by>\r\n<yv>Iruvpyrf bgure guna ovplpyrf naq fznyy ryrpgevp iruvpyrf nf qrsvarq ol gur rXSI znl abg hfr ovplpyr mbarf hayrff guvf vf crezvggrq ol nqqvgvbany fvtaf. ...<\/yv>\r\n<yv>N znkvzhz fcrrq bs 30 xz\/u nccyvrf gb gur qevivat genssvp. Plpyvat genssvp znl arvgure or raqnatrerq abe uvaqrerq. Vs arprffnel, zbgbe iruvpyr genssvp zhfg shegure erqhpr gur fcrrq.<\/yv>\r\n<yv>Vg vf crezvggrq gb qevir arkg gb rnpu bgure jvgu ovplpyrf naq fznyy ryrpgevp iruvpyrf va gur frafr bs gur rXSI.<\/yv>\r\n<\/by>";
		dbTblQ[3001].info = "<c>Zbcrqf ner abg ovplpyr genssvp.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nyy gubfr jub unir n yrtvgvzngr vagrerfg va qevivat vagb gur fgerrg - v.r. jub unir n pbaprea - ner pbafvqrerq erfvqragf. Ivfvgbef sebz erfvqragf ner nyybjrq gb ragre.<\/c>\r\n<c>Urer gur genssvp fvta 244.3 \"Ortvaavat bs n ovplpyr mbar\" jvgu nqqvgvbany fvta \"erfvqragf serr\" vf fubja.<\/c>\r\n<by>\r\n<yv>Iruvpyrf bgure guna ovplpyrf naq fznyy ryrpgevp iruvpyrf nf qrsvarq ol gur rXSI znl abg hfr ovplpyr mbarf hayrff guvf vf crezvggrq ol nqqvgvbany fvtaf. ...<\/yv>\r\n<yv>N znkvzhz fcrrq bs 30 xz\/u nccyvrf gb gur qevivat genssvp. Plpyvat genssvp znl arvgure or raqnatrerq abe uvaqrerq. Vs arprffnel, zbgbe iruvpyr genssvp zhfg shegure erqhpr gur fcrrq.<\/yv>\r\n<yv>Vg vf crezvggrq gb qevir arkg gb rnpu bgure jvgu ovplpyrf naq fznyy ryrpgevp iruvpyrf va gur frafr bs gur rXSI.<\/yv>\r\n<\/by>";
		dbTblQ[3002].info = "<c>Gur genssvp fvta vaqvpngrf na rzretrapl fgbc naq rzretrapl onl. Va pnfr bs rzretrapl, na nggrzcg fubhyq or znqr gb fgbc gurer.<\/c>";
		dbTblQ[3003].info = "<c>Nalbar qevivat n iruvpyr znl bayl qevir bire znexrq cebgrpgvir fgevcf sbe plpyvfgf vs arprffnel. Plpyvfgf zhfg abg or raqnatrerq.<\/c>\r\n<c>Cnexvat be ubyqvat vf abg crezvggrq ba gur cebgrpgvir fgevc.<\/c>";
		dbTblQ[3004].info = "<c>Nyy guerr nafjref ner pbeerpg.<\/c>\r\n<c>Pneoherggbef be shry vawrpgvba flfgrzf erdhver erthyne znvagranapr gb cerirag uvtu shry pbafhzcgvba naq rkprffvir rzvffvbaf. Gur fnzr nccyvrf gb gur nve svygre naq gur vtavgvba flfgrz.<\/c>";
		dbTblQ[3005].info = "<c>Erthyne iruvpyr znvagranapr, r.t. pneohergbe, shry vawrpgvba flfgrz, nve svygre naq vtavgvba flfgrz, vf n cererdhvfvgr sbe raivebazragnyyl sevraqyl naq raretl-fnivat qevivat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vapbeerpg gver vasyngvba cerffher pna yrnq gb uvture shry pbafhzcgvba.<\/c>";
		dbTblQ[3006].info = "<c>Shry pbafhzcgvba vf ybjre ng ybj ecz, fb nibvq uvtu ratvar fcrrq, qevir va n uvtu trne naq qb abg nppryrengr fgebatyl.<\/c>";
		dbTblQ[3007].info = "<c>Fuvsgvat hc rneyl naq qevivat va gur uvturfg trne cbffvoyr erqhprf shry pbafhzcgvba. N cebnpgvir qevivat fglyr nyfb pbagevohgrf gb ybj shry pbafhzcgvba.<\/c>";
		dbTblQ[3008].info = "<c>Qevivat ng ybj erif erqhprf iruvpyr abvfr.<\/c>\r\n<c>Fcrpvny ybj-abvfr gverf nyfb pbagevohgr gb abvfr erqhpgvba.<\/c>";
		dbTblQ[3009].info = "<c>Fgebat rkunhfg fzbxr vaqvpngrf n qrsrpgvir rkunhfg flfgrz gung eryrnfrf cbyyhgnagf haarprffnevyl. Ercnve vf erdhverq. Gur ratvar vf abg erdhverq sbe ybatre jnvgvat crevbqf; vg fubhyq or fjvgpurq bss va guvf pnfr.<\/c>\r\n<c>Haarprffnel qevivat onpx naq sbegu haarprffnevyl cbyyhgrf gur raivebazrag.<\/c>";
		dbTblQ[3010].info = "<c>Haarprffnevyl snfg nppryrengvba vf n abvfr ahvfnapr gung fubhyq or nibvqrq. Qnzntrq zhssyref nyfb trarengr haarprffnel abvfr.<\/c>\r\n<c>Qevivat snfg va ybj trnef zrnaf uvtu ratvar fcrrq naq guhf nibvqnoyr abvfr cbyyhgvba.<\/c>";
		dbTblQ[3011].info = "<c>Punatvat gb n uvture trne rneyl urycf gb erqhpr shry pbafhzcgvba. Fuvsgvat gur trne qbja yngr unf n fvzvyne rssrpg, nf uvtu fcrrqf ner nyfb nibvqrq.<\/c>";
		dbTblQ[3012].info = "<c>Cynlvat jvgu gur nppryrengbe juvyr jnvgvat ng gur genssvp yvtug erfhygf va haarprffnel shry pbafhzcgvba. N urpgvp, harira qevivat fglyr nyfb vapernfrf shry pbafhzcgvba.<\/c>";
		dbTblQ[3013].info = "<c>N onynaprq qevivat fglyr ng ybj erif pbagevohgrf gb ybj shry pbafhzcgvba naq rzvffvbaf. Gur evtug gler cerffher nyfb pbagevohgrf gb ybj shry pbafhzcgvba naq rzvffvbaf.<\/c>";
		dbTblQ[3014].info = "<c>Nsgre gur ratvar unf orra vtavgrq, gur iruvpyr fubhyq or fgnegrq vzzrqvngryl gb nibvq haarprffnel ratvar bcrengvba. Qevivat ng ybj erif pbagevohgrf gb ybj shry pbafhzcgvba.<\/c>";
		dbTblQ[3015].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jura qevivat jvgu na bcra ivfbe, n syl, sbe rknzcyr, pna trg vagb gur rlr naq frireryl vzcnve ivfvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va urnil enva, ivfvovyvgl vf frireryl vzcnverq ol gur ivfbe.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>N punatr va gur ebnq fhesnpr punatrf gur qevivat orunivbe noehcgyl naq vf gurersber nyfb n fbhepr bs qnatre.<\/c>";
		dbTblQ[3016].info = "<c>Gur crefba biregnxvat unf gb fgbc gur biregnxvat cebprff, nccneragyl n iruvpyr vf pbzvat gbjneqf uvz. V yrg gur biregnxvat iruvpyr trg vagb gur ynar va sebag bs zr gb nibvq n qnatrebhf fvghngvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gura zl fnsrgl qvfgnapr vf ab ybatre fhssvpvrag. Gurersber, V erqhpr zl fcrrq gb perngr gur arprffnel fnsrgl qvfgnapr ntnva.<\/c>";
		dbTblQ[3017].info = "<c>Nyy guerr nafjref ner pbeerpg.<\/c>\r\n<c>Vs gur oenxr vf urnivyl ratntrq ba bar fvqr, gur qevivat fgnovyvgl vf raqnatrerq.<\/c>\r\n<c>Gbb zhpu cynl ebof gur fgrrevat bs vgf cerpvfvba. Fhpu cerpvfvba vf arprffnel gb fnsryl qevir gur iruvpyr. Va nqqvgvba, qevivat fgenvtug vf ab ybatre thnenagrrq.<\/c>\r\n<c>N aba-shapgvbavat urnqynzc znxrf zl ivfvba qvssvphyg naq V nz creprvirq yrff jryy ol bgure ebnq hfref.<\/c>";
		dbTblQ[3018].info = "<c>Jvgu ndhncynavat, gur glerf ybfr qverpg pbagnpg jvgu gur ebnq fhesnpr, juvpu zrnaf gung gur iruvpyr pna ab ybatre or fgrrerq naq oenxrq va n pbagebyyrq znaare. Qhr gb gur frireryl yvzvgrq znabrhienovyvgl, gur iruvpyr znl qrivngr sebz gur ebnq fhesnpr.<\/c>";
		dbTblQ[3019].info = "<c>N yngreny qvfcynprzrag bs gur zbgbeplpyr vf guerngrarq ol n fhqqra fvqr jvaq. Guvf pna bpphe jura biregnxvat n gehpx.<\/c>\r\n<c>Fvzvyneyl, jvaq pbaqvgvbaf pna fhqqrayl punatr jura qevivat bire n oevqtr be yrnivat n ghaary.<\/c>";
		dbTblQ[3020].info = "<c>Ba gur bccbfvgr ynar n zbgbeplpyr pbzrf gbjneqf zr, uvqqra ol gur genpgbe va sebag bs zr. Gurersber V qb abg biregnxr.<\/c>";
		dbTblQ[3021].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Crqrfgevnaf ner nyjnlf gb or rkcrpgrq va cnexvat naq erfg nernf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Lbh zhfg nyfb nyjnlf rkcrpg gb frr gehpxf cnexrq vapbeerpgyl naq iruvpyrf yrnivat gur cnexvat nern.<\/c>";
		dbTblQ[3022].info = "<c>Ba gur bccbfvgr ynar n plpyvfg vf biregnxra ol n pne. Sbe guvf checbfr gur pne zhfg fjreir bagb zl ynar.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gb nibvq n qnatrebhf fvghngvba, V unir gb ghea nf sne evtug nf cbffvoyr naq oenxr.<\/c>";
		dbTblQ[3023].info = "<c>Gur genpgbe frrzf gb qevir vagb gur fgerrg. Qhr gb vgf ybj fcrrq naq vgf birenyy yratgu jvgu genvyre, guvf cebprff jvyy gnxr ybatre. Vg vf nyfb abg lrg cbffvoyr gb cerqvpg jurgure gur genpgbe jvyy ghea yrsg be evtug.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gb nibvq n qnatrebhf fvghngvba V nz ernql gb oenxr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurer vf ab fcrrq yvzvg. Gur zbgbeplpyr vf nyfb ab fbhepr bs qnatre.<\/c>";
		dbTblQ[3024].info = "<c>Jura evqvat va n tebhc jvgu frireny zbgbeplpyrf, lbh fubhyq evqr bssfrg gb vapernfr gur fnsrgl qvfgnapr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va beqre abg gb birefgenva varkcrevraprq evqref naq guhf chg gurz va n qnatrebhf fvghngvba, gur tebhc unf gb sbyybj gurve evqvat fglyr.<\/c>\r\n<c>Gurer ner genssvp fvghngvbaf, fhpu nf n erq yvtug, jurer n tebhc pnaabg fgnl gbtrgure.<\/c>";
		dbTblQ[3025].info = "<c>Gurer vf ab fvta gung gur genpgbe vf qevivat ba gur ebnq be gung gur gehpx vf gheavat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur qevire bs gur bapbzvat naq gheavat iruvpyr unq uvf ivrj bofpherq ol gur gehpx, ur pbhyq unir bireybbxrq zr naq ghearq bss vzzrqvngryl.<\/c>";
		dbTblQ[3026].info = "<c>Va gur pnfr bs sngvthr, bayl erpbirel urycf. Gur obql zhfg or tvira na nccebcevngryl ybat oernx.<\/c>";
		dbTblQ[3027].info = "<c>N tbbq qevire vf pbafvqrengr bs bgure zbgbevfgf naq qevirf qrsrafviryl. N evfxl qevivat fglyr vf abg crezvggrq.<\/c>";
		dbTblQ[3028].info = "<c>Ba ebnqf jvgu frireny ynarf sbe bar qverpgvba, vs vg vf abg cbffvoyr gb qevir pbagvahbhfyl ba bar ynar be vs bar ynar raqf, gur iruvpyrf ceriragrq sebz pbagvahvat fubhyq or nyybjrq gb cnff bire gb gur nqwnprag ynar va fhpu n jnl gung gurfr iruvpyrf pna nygreangr jvgu n iruvpyr geniryyvat ba gur pbagvahbhf ynar vzzrqvngryl orsber gur aneebjvat ortvaf (mvccre srrq-va zrgubq).<\/c>";
		dbTblQ[3029].info = "";
		dbTblQ[3030].info = "<c>N fnsr fgnaq zhfg or rafherq qhevat cnexvat. Sbe guvf ernfba, gur cnexvat nern fubhyq unir n fbyvq onfr.<\/c>";
		dbTblQ[3031].info = "<c>Erzbir gur vtavgvba xrl naq ybpx gur fgrrevat ybpx gb cerirag hanhgubevmrq hfr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur onggrel vf dhvpxyl erpbaarpgrq naq qvfpbaarpgvat vg gurersber qbrf abg cebivqr cebgrpgvba ntnvafg hanhgubevmrq hfr.<\/c>";
		dbTblQ[3032].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Zbgbeplpyrf zhfg nyjnlf or cnexrq fnsryl naq fgnoyr, gur tebhaq zhfg abg tvir jnl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>N fvqr fgnaq gung vf abg pbzcyrgryl sbyqrq va pna pnhfr bofgehpgvbaf juvyr evqvat.<\/c>";
		dbTblQ[3033].info = "<c>Vs gur zbgbe snvyf, gur oenxr obbfgre vf abg ninvynoyr. Va beqre gb npuvrir n pbzcnenoyr oenxvat sbepr, nccylvat n uvture crqny cerffher vf arprffnel. Gbjvat ol zrnaf bs n gbj one perngrf n pbaarpgvba gung npgf orgjrra gur iruvpyrf va obgu qverpgvbaf naq guhf cebivqrf fbzr fhccbeg qhevat oenxvat.<\/c>\r\n<c>Vs na ryrpgevp iruvpyr vf gbjrq, gur znahsnpghere'f vafgehpgvbaf zhfg or bofreirq.<\/c>";
		dbTblQ[3034].info = "<c>Gur urnqyvtugf zhfg or fjvgpurq ba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Qevivat jvgu gur cnexvat yvtug nybar vf abg nyybjrq. Gur vyyhzvangvba bs gur ebnq vf vafhssvpvrag naq ng gur fnzr gvzr gurer vf n evfx bs orvat erpbtavmrq yngr ol bapbzvat genssvp.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur uvtu ornz znl bayl or fjvgpurq ba ba ebnqf juvpu qb abg unir n pbagvahbhf, fhssvpvrag vyyhzvangvba. Va pnfr bs bapbzvat genssvp, gur uvtu ornz zhfg nyjnlf or fjvgpurq bss vzzrqvngryl.<\/c>";
		dbTblQ[3035].info = "<c>Vg vf vzcbegnag gb bcra gur qbbe pnershyyl naq gb znvagnva gur arprffnel fnsrgl qvfgnapr. Zbgbeplpyrf ner abg nyybjrq gb \"jvttyr\" orgjrra gur iruvpyrf be qevir ba gur uneq fubhyqre.<\/c>";
		dbTblQ[3038].info = "<c>Nyy guerr nafjref ner pbeerpg.<\/c>\r\n<c>Nabgure crefba pna bayl or gnxra nybat vs gur zbgbeplpyr unf gur nccebcevngr ertvfgengvba. Gur cnffratre zhfg jrne n fnsrgl uryzrg.<\/c>";
		dbTblQ[3039].info = "<c>Qhr gb gur vapernfrq znff, gur zbgbeplpyr bayl orpbzrf fgnovyvmrq ng uvture fcrrqf. Gur raynetrq fhesnpr vapernfrf gur fhfprcgvovyvgl gb jvaq. Gur oenxvat orunivbhe bs gur zbgbeplpyr pna nyfb punatr.<\/c>";
		dbTblQ[3040].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va cnffratre frngf jurer na bcrengvbany nveont vf vafgnyyrq va sebag, ernejneq-snpvat onol erfgenvagf zhfg abg or vafgnyyrq, v.r. gur cnffratre nveont zhfg or qrnpgvingrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Bs pbhefr, gur onol zhfg or fgenccrq va gur onol pneevre.<\/c>";
		dbTblQ[3041].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Yhttntr arg, grafvba oryg naq nagv-fyvc zng ner fhvgnoyr sbe frphevat ybnqf.<\/c>";
		dbTblQ[3042].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur jneavat gevnatyr zhfg or ernql gb unaq ng nyy gvzrf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va beqre gb nibvq raqnatrevat gur bpphcnagf, r.t. va gur rirag bs n unmneq oenxvat znarhire, yhttntr fubhyq or frpherq ntnvafg fyvccvat naq urnil yhttntr fubhyq or fgbjrq nf sne qbja nf cbffvoyr.<\/c>";
		dbTblQ[3043].info = "";
		dbTblQ[3044].info = "";
		dbTblQ[3045].info = "";
		dbTblQ[3046].info = "<c>Gur fnsrgl rdhvczrag vapyhqrf n fhvgnoyr wnpxrg rdhvccrq jvgu cebgrpgbef, sbe rknzcyr. Fhvgnoyr zbgbeplpyr gebhfref ner nyfb vapyhqrq nf cneg bs gur fnsrgl rdhvczrag.<\/c>";
		dbTblQ[3047].info = "<c>Zbgbeplpyr obbgf cebivqr nqrdhngr cebgrpgvba rira jura fgngvbanel, fubhyq gur zbgbeplpyr gvc bire.<\/c>\r\n<c>Gurl nyfb cebgrpg ntnvafg ulcbgurezvn naq gur nffbpvngrq qnzntr gb bar&efdhb;f urnygu.<\/c>\r\n<c>Vs ab fhvgnoyr zbgbeplpyr obbgf unir orra jbea, gur vafhenapr pynvz znl or ng evfx.<\/c>";
		dbTblQ[3048].info = "<c>Fpengpurf va gur ivfbe jbefra bar'f ivrj. Va nqqvgvba, gur evfx bs tyner sebz gur fha be bapbzvat genssvp vapernfrf pbafvqrenoyl.<\/c>";
		dbTblQ[3049].info = "<c>Na nccebirq fnsrgl uryzrg zhfg or jbea gb cebgrpg ntnvafg urnq vawhevrf.<\/c>";
		dbTblQ[3050].info = "<c>Nalbar qevivat n iruvpyr znl abg hfr n zbovyr be pne gryrcubar vs gur zbovyr gryrcubar be gur unaqfrg bs gur pne gryrcubar zhfg or cvpxrq hc be uryq sbe guvf checbfr. Guvf qbrf abg nccyl vs gur iruvpyr vf fgngvbanel naq gur ratvar vf fjvgpurq bss va gur pnfr bs zbgbe iruvpyrf. Bcrengvba bs gur qrivpr znl perngr n qvfgenpgvba gung pbhyq raqnatre ebnq fnsrgl.<\/c>";
		dbTblQ[3051].info = "";
		dbTblQ[3052].info = "";
		dbTblQ[3053].info = "<c>Gur nppvqrag fvgr vf ybpngrq va gur vzzrqvngr ivpvavgl bs gur ebnq. Gurersber, vg vf vzcbegnag gb erqhpr lbhe fcrrq va beqre abg gb raqnatre nalbar. Guvf pna or qbar ol qevivat sne gb gur yrsg.<\/c>";
		dbTblQ[3054].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur qnatre fvta \"101-54 vafhssvpvrag pyrnenapr tnhtr\" jneaf bs oenapurf va gur nvefcnpr nobir gur ebnq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurersber, oenxvat naq rinfvir iruvpyrf nf jryy nf oenapurf ba gur ebnq zhfg or rkcrpgrq.<\/c>";
		dbTblQ[3055].info = "<c>Zbgbeplpyrf qb abg arrq na raivebazragny fgvpxre. Bayl sbhe-jurryrq iruvpyrf arrq n fgvpxre naq ner nyybjrq gb ragre gur raivebazragny mbar jvgu n terra cnegvphyngr znggre fgvpxre.<\/c>";
		dbTblQ[3056].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nyy zrnfherf gung erqhpr cbyyhgnag rzvffvbaf pbagevohgr gb raivebazragnyyl sevraqyl qevivat.<\/c>";
		dbTblQ[3057].info = "<c>Qhevat \"xvpx qbja\", gur ratvar fuvsgf qbja naq nppryrengrf ng znkvzhz fcrrq, juvpu vapernfrf shry pbafhzcgvba rabezbhfyl. Gurersber, nibvqvat \"xvpx qbjaf\" vf pbafvqrerq shry-rssvpvrag.<\/c>\r\n<c>Vs gur ratvar bs n pne jvgu nhgbzngvp genafzvffvba vf fjvgpurq bss juvyr qevivat, genafzvffvba qnzntr znl bpphe.<\/c>";
		dbTblQ[3058].info = "<c>Orpnhfr zbgbe iruvpyrf pna qevir snfgre guna 25 xz\/u nsgre gurl unir orra gharq gb qb fb, gurer vf n evfx bs chavfuzrag sbe qevivat jvgubhg n qevivat yvprapr. Gur grpuavpny zbqvsvpngvbaf erfhyg va gur pnapryyngvba bs gur bcrengvat crezvg naq vafhenapr pbire. Gurer vf nyfb n guerng bs chavfuzrag naq n svar.<\/c>";
		dbTblQ[3059].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur znkvzhz genvyre ybnq naq gur crezvffvoyr qenjone ybnq bs gur genvyre pbhcyvat ner ragrerq va gur iruvpyr ertvfgengvba qbphzrag be va gur ertvfgengvba pregvsvpngr bs gur gbjvat iruvpyr. Gurfr inyhrf zhfg abg or rkprrqrq.<\/c>";
		dbTblQ[3060].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fcrrq erfgevpgvbaf, raivebazragny mbarf naq pvgl gbyyf ner erthyngrq irel qvssreragyl noebnq. N ivbyngvba pna yrnq gb fbzrgvzrf qenfgvp svarf.<\/c>";
		dbTblQ[3061].info = "<c>Gur qevivat punenpgrevfgvpf bs n dhnq ovxr qb abg pbeerfcbaq gb gubfr bs n pne va znal nernf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr bar pnaabg \"ynl\" barfrys jvgu Dhnqf, pbagenel gb gur zbgbeplpyr, vagb gur pheir, gurer vf gur qnatre bs \"gvygvat\" bhgjneq ol pragevshtny sbepr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jrnevat n cebgrpgvir uryzrg vf znaqngbel.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>FGIB &frpg;21n, 2:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><rz>Nalbar jub qevirf zbgbeplpyrf be bcra guerr- be zhygv-jurryrq zbgbe iruvpyrf jvgu n znkvzhz qrfvta fcrrq bs bire 20 xz\/u nf jryy nf evqvat ba be va gurz zhfg jrne n fhvgnoyr fnsrgl uryzrg juvyr qevivat.<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><rz>Guvf qbrf abg nccyl vs znaqngbel fnsrgl orygf ner jbea.<\/rz><\/c>";
		dbTblQ[3062].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>N svefg nvq xvg (&frpg; 35u FgIMB) naq n jneavat gevnatyr (&frpg; 53n FgIMB) zhfg or pneevrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr gurer vf hfhnyyl ab cbjre fgrrevat, gur fgrrevat sbeprf pna or zhpu uvture guna va n pne.<\/c>";
		dbTblQ[3063].info = "<c>Lbh ner ernql gb oenxr jura lbhe unaq vf erfgvat ba gur oenxr yrire. Gur fnzr nccyvrf vs gur sbbg vf cynprq bire gur oenxr crqny.<\/c>";
		dbTblQ[3064].info = "<c>Nyy guerr nafjref ner pbeerpg.<\/c>\r\n<c>Gur hfr bs fvqr-zbhagrq fhvgpnfrf be cnaavref erdhverf na rira jrvtug qvfgevohgvba ba obgu fvqrf. Urnil yhttntr fubhyq or fgbjrq nf ybj nf cbffvoyr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur znkvzhz fcrrq naq gbgny znff erpbzzraqrq ol gur znahsnpghere zhfg nyfb or gnxra vagb nppbhag.<\/c>";
		dbTblQ[3065].info = "<c>Ynar Xrrcvat Nffvfgnag vf n qevire nffvfgnapr flfgrz gung jneaf gur qevire bs n iruvpyr orsber yrnivat gur ynar. Vg qbrf abg ernpg gb gur yngreny qvfgnapr gb bgure iruvpyrf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur fnsrgl qvfgnapr gb gur iruvpyr va sebag vf pbagebyyrq ol gur Qvfgnapr Zrnfherzrag Nffvfgnag.<\/c>";
		dbTblQ[3066].info = "";
		dbTblQ[3067].info = "";
		dbTblQ[3068].info = "<c>Gur bvy cerffher vaqvpngbe yvtug jneaf gur qevire vs gur bvy cerffher vf gbb ybj. Guvf pna yrnq qverpgyl gb ratvar qnzntr. Gur ratvar zhfg or fjvgpurq bss vzzrqvngryl naq gur bvy yriry zhfg or purpxrq.<\/c>";
		dbTblQ[3069].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur yriry bs gur oenxr syhvq naq n zvffvat cerffher cbvag cbfr n evfx.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>N oenxr vaqvpngbe yvtug gung yvtugf hc vaqvpngrf n qrsrpg va gur oenxr flfgrz.<\/c>";
		dbTblQ[3070].info = "<c>Gur yvtugvat hc bs gur oenxr vaqvpngbe yvtug naq bar-fvqrq oenxvat (iruvpyr qbrf abg fgnl ba genpx jura oenxvat) erdhver gur oenxr flfgrz gb or purpxrq.<\/c>";
		dbTblQ[3071].info = "";
		dbTblQ[3072].info = "<c>Gur yrff gur pyhgpu vf npghngrq, gur yrff jrne naq grne.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Ercrngrq fgnegvat naq fgbccvat chgf n fgenva ba gur pyhgpu.<\/c>";
		dbTblQ[3073].info = "<c>Qrcraqvat ba gur iruvpyr, gur ratvar bvy yriry pna or purpxrq ng gur fvtug tynff be gur bvy qvcfgvpx.<\/c>";
		dbTblQ[3074].info = "<c>Qrcraqvat ba gur iruvpyr, gur ratvar bvy yriry pna or purpxrq ng gur qevire vasbezngvba flfgrz be gur Bvy Qvcfgvpx.<\/c>";
		dbTblQ[3075].info = "<c>Obgu gbb uvtu naq gbb ybj gler cerffher pna vapernfr gler jrne. N qrgrevbengvba bs gur qevivat orunivbhe vf nyfb cbffvoyr.<\/c>";
		dbTblQ[3076].info = "";
		dbTblQ[3077].info = "";
		dbTblQ[3078].info = "";
		dbTblQ[3079].info = "";
		dbTblQ[3080].info = "";
		dbTblQ[3081].info = "";
		dbTblQ[3082].info = "";
		dbTblQ[3083].info = "";
		dbTblQ[3084].info = "";
		dbTblQ[3085].info = "<c>Urnil enva jbefraf ivfvovyvgl naq tevc. V gurersber erqhpr zl fcrrq naq nibvq nppryrengvat uneq.<fcna pynff='peys'><\/fcna>Erqhpvat gur fnsrgl qvfgnapr gb gur iruvpyr va sebag vf gurersber rknpgyl gur jebat guvat gb qb.<\/c>";
		dbTblQ[3086].info = "<c>Orpnhfr gur iruvpyr gb or biregnxra vf nyfb va zbgvba, gur biregnxvat qvfgnapr vf bsgra haqrerfgvzngrq. Gur qvfgnapr gb bapbzvat genssvp vf nyfb bsgra birerfgvzngrq.<\/c>\r\n<c>Biregnxvat vf bayl nyybjrq vs lbh pna rafher gung nal bapbzvat genssvp jvyy abg or bofgehpgrq qhevat gur ragver gvzr gung lbh ner biregnxvat.<\/c>";
		dbTblQ[3087].info = "<c>Vs lbh jnag gb ghea, lbh unir gb lvryq gb bapbzvat iruvpyrf, envy iruvpyrf, ryrpgevp ovplpyrf naq plpyvfgf rira vs gurl ner evqvat be qevivat ba be orfvqr gur ebnq va gur fnzr qverpgvba. Guvf nyfb nccyvrf gb fpurqhyrq ohfrf naq bgure iruvpyrf hfvat znexrq fcrpvny ynarf. Fcrpvny pbafvqrengvba zhfg or tvira gb crqrfgevnaf. Vs arprffnel, jnvg.<\/c>";
		dbTblQ[3088].info = "<c>Iruvpyrf cnexrq va n ebj pna vzcnve ivfvovyvgl. Iruvpyrf gung yrnir gur cnexvat ebj znl abg or erpbtavfrq ol bgure zbgbevfgf hagvy yngre. Ba gur bgure unaq, v.r., jura yrnivat gur cnexvat fcnpr, ivfvovyvgl znl or vzcnverq, znxvat vg qvssvphyg gb qrgrpg nccebnpuvat genssvp rneyl.<\/c>";
		dbTblQ[3089].info = "<c><fgebat>Gur genssvp fvghngvba vf hapyrne.<\/fgebat><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurersber, nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[3090].info = "<c>Guvf vf n dhrfgvba bayl sbe gur pynff Zbsn! Fb lbh ner evqvat n Zbsn.<\/c>\r\n<c>V pnaabg eryl ba bgure ebnq hfref gb orunir cebcreyl naq V unir gb frr sbe zlfrys jurgure gur ebnq vf pyrne.<\/c>\r\n<c>Bhgfvqr gbjaf naq pvgvrf, Zbsnf ner nyybjrq gb hfr ovxr ynarf.<\/c>";
		dbTblQ[3091].info = "<c>Ba gur ubhfrf naq va gur erne ivrj zveebef lbh pna frr gung vg vf n eryngviryl fgrrc fybcr. Gur plpyvfgf pna gurersber \"fjvat\" gb gur yrsg naq evtug.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>FgIB &frpg; 5.4 Biregnxvat <fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"... Jura biregnxvat, n fhssvpvrag yngreny qvfgnapr gb gur bgure ebnq hfref zhfg or znvagnvarq. Jura biregnxvat jvgu zbgbe iruvpyrf bs crqrfgevnaf, plpyvfgf naq qeviref bs irel fznyy ryrpgevp iruvpyrf, gur fhssvpvrag yngreny qvfgnapr vf ng yrnfg 1.5 z va gbjaf naq ng yrnfg 2 z bhgfvqr gbjaf. ...\"<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vs plpyvfgf ner biregnxvat va ohvyg-hc nernf, gur qvfgnapr zhfg or ng yrnfg 1.5z.<\/c>";
		dbTblQ[3092].info = "<c>V pna'g yrnir evtug njnl vs n gehpx qevire nfxf zr gb. Svefg bs nyy V unir gb znxr fher gung V qb abg raqnatre be fvtavsvpnagyl bofgehpg pebff genssvp.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vs gur gehpx qevire gura pyrneyl tvirf hc uvf evtug bs jnl, V ghea va sebag bs uvz.<\/c>";
		dbTblQ[3093].info = "<c>N fvatyr-svryq fvtany genafzvggre jvgu n terra neebj vaqvpngrf gung jura gur erq yvtug nccyvrf gb gur fgenvtug-nurnq qverpgvba, vg vf crezvffvoyr gb ghea gb gur evtug. Arireguryrff, vg vf vzcbegnag gb fgbc naq pbafvqre gung yngreny genssvp, plpyvfgf naq crqrfgevnaf unir cevbevgl.<\/c>";
		dbTblQ[3094].info = "<c>Nggragvba, guvf vf abg n ebhaqnobhg, ohg n fb-pnyyrq \" haerny ebhaqnobhg\".<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg vf<fgebat> abg znexrq<\/fgebat> ol gur genssvp fvtaf \"Tvir jnl\" naq \"Ebhaqnobhg\". Urer, nf ng n abezny vagrefrpgvba, gur onfvp ehyr \"evtug orsber yrsg\" nccyvrf. Gur vaqvpngbe zhfg gurersber nyfb or npgvingrq jura ragrevat naq yrnivat gur ebhaqnobhg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><\/c>";
		dbTblQ[3095].info = "<c>Gur qnatre fvta jneaf bs n qnatrebhf cynpr ol n pebffjnyx. Guvf pebffjnyx pna or qvssvphyg gb erpbtavmr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurersber, nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[3097].info = "<c>Gur hfr bs fhpu n flfgrz erdhverf nggragvba sebz gur qevire fb gung ur be fur pna ernpg vzzrqvngryl va na rzretrapl naq, sbe rknzcyr, vapernfr gur qvfgnapr. Enva be fabjsnyy pna vzcnve gur grpuabybtl. Nf fbba nf gur oenxr be nppryrengbe crqny vf qrcerffrq, gur crqny gnxrf cerprqrapr naq bireevqrf gur nqncgvir pehvfr pbageby flfgrz.<\/c>";
		dbTblQ[3098].info = "<c>Nirahrf ner bsgra aneebj, juvpu pna yrnq gb pbasyvpgf jvgu bapbzvat genssvp. Qhr gb gur aneebj gerr cynagvat hariraarff bs gur ebnq fhesnpr znl bpphe, juvpu pna or qhr gb gur ebbgf bs gur gerrf. Va cevapvcyr, lbh znl bayl qevir fb snfg gung vg vf cbffvoyr gb fgbc jvguva fvtug ng nal gvzr. Ubjrire, va ynarf gung ner fb aneebj gung bapbzvat iruvpyrf pbhyq or raqnatrerq, zbgbevfgf zhfg qevir fb fybjyl gung ng yrnfg unys bs gur ivfvoyr qvfgnapr pna or znvagnvarq.<\/c>";
		dbTblQ[3099].info = "<c>Ba jvqr ebnqf jvgubhg rqtr qrirybczrag, gur fcrrq bs n iruvpyr vf bsgra rfgvzngrq gb or gbb ybj. Cebybatrq qevivat ng uvtu fcrrq unf n fvzvyne rssrpg.<\/c>";
		dbTblQ[3100].info = "<c>Nygubhtu vg vf abg nyybjrq, gur bapbzvat erq pne unf pebffrq gur qbhoyr yvar. N pbyyvfvba vf cbffvoyr, lbh zhfg fjvgpu gb gur evtug-unaq ynar.<\/c>";
		dbTblQ[3101].info = "<c>Gur qrre ba gur yrsg fvqr bs gur ebnq pna pebff gur ynar noehcgyl. V zhfg erqhpr zl fcrrq shegure naq or ernql gb oenxr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va beqre abg gb raqnatre gur sbyybjvat iruvpyr, V qb abg cresbez rzretrapl oenxvat.<\/c>";
		dbTblQ[3102].info = "<c>Gur ebnq vf abg fhssvpvragyl ivfvoyr orpnhfr bs gur pheir, gurersber vg vf abg nyybjrq gb biregnxr.<\/c>\r\n<c>Ubax bayl jub biregnxrf bhgfvqr ohvyg-hc nernf be jub frrf uvzfrys be bguref raqnatrerq.<\/c>\r\n<c>V gurersber znvagnva zl fcrrq.<\/c>";
		dbTblQ[3103].info = "<c>J&nhzy;ueraq a&nhzy;pugyvpure Servmrvgsnuegra - vaforfbaqrer na Jbpuraraqra - arvtra rvavtr whatr Xensgsnuere mh rvare ubura Evfvxborervgfpunsg. Nyf Sbytr jveq bsgznyf zvg &hhzy;oreu&bhzy;ugre Trfpujvaqvtxrvg trsnuera.<\/c>";
		dbTblQ[3104].info = "<c>Qrfcvgr gvzr cerffher, gur genssvp ehyrf zhfg or bofreirq.<\/c>";
		dbTblQ[3105].info = "<c>Shyyl bpphcvrq iruvpyrf erdhver pbeerfcbaqvat rkcrevrapr sebz gur qevire. Guvf vf qhr gb snpgbef fhpu nf gur vasyhrapr bs gur shyyl bpphcvrq iruvpyr ba qevivat orunivbhe nf jryy nf n cbffvoyl vagrafvsvrq qvfgenpgvba pnhfrq ol gur cnffratref. N ynpx bs qevivat rkcrevrapr va qnexarff nyfb vapernfrf gur evfx bs nppvqragf.<\/c>";
		dbTblQ[3106].info = "<c>Bcravat gur qbbe jbhyq raqnatre gur plpyvfg evqvat ba zl fvqr bs gur ebnq, fb V zhfg abg bcra gur qbbe abj.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur zbgbeplpyr naq crqrfgevnaf jbhyq abg or raqnatrerq ol bcravat gur qbbe.<\/c>";
		dbTblQ[3107].info = "<c>Nqwhfgvat gur yriry bs urnqynzcf znahnyyl ragnvyf gur evfx bs frggvat gur yriry vapbeerpgyl. Vs gur yriry vf frg gbb ybj, gur ebnq jvyy or vafhssvpvragyl vyyhzvangrq. Vs frg gbb uvtu, gurer vf n evfx bs qnmmyvat bapbzvat genssvp.<\/c>";
		dbTblQ[3108].info = "<c>Gurer vf n iruvpyr arkg gb zr va gur yrsg ynar naq n zbgbeplpyr nccebnpuvat sebz oruvaq va gur evtug ynar gung znl biregnxr zr ba gur evtug.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>V gurersber fgnl va gur zvqqyr ynar.<\/c>";
		dbTblQ[3109].info = "<c>Ynar Punatr Nffvfg (nyfb: Oyvaq Fcbg Nffvfg) jneaf qeviref bs vzcraqvat pbyyvfvbaf jura punatvat ynarf. Urer vg jneaf bs gur iruvpyr va gur zvqqyr ynar. <fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg qbrf abg punatr ynarf ba vgf bja naq qbrf abg oenxr nhgbzngvpnyyl vs gur qvfgnapr gb gur iruvpyr va sebag orpbzrf gbb fznyy.<\/c>";
		dbTblQ[3110].info = "<c>Yhttntr znl or fgberq va cnaavref be ba gur yhttntr enpx. Vg znl nyfb or fgberq va gur gnax ont.<\/c>\r\n<c>Gur gbgny crezvffvoyr znff zhfg abg or rkprrqrq naq gur yvtugvat rdhvczrag zhfg abg or bofpherq.<\/c>";
		dbTblQ[3111].info = "<c>Gur cnffratre zhfg jrne n uryzrg. Qevire naq cnffratre fubhyq nterr ba ehyrf bs pbaqhpg inyvq sbe gur wbhearl. Varkcrevraprq vaqvivqhnyf fubhyq pnershyyl orpbzr snzvyvnevfrq jvgu gur qevivat orunivbhe bs gur zbgbeplpyr.<\/c>";
		dbTblQ[3112].info = "<c>Yhttntr vasyhraprf gur prager bs tenivgl naq guhf gur unaqyvat bs gur zbgbeplpyr. Qhr gb gur raynetrq nern, vg nyfb vapernfrf fhfprcgvovyvgl gb fvqr jvaq. Gur znkvzhz fcrrq erpbzzraqrq ol gur znahsnpghere sbe qevivat jvgu yhttntr funyy or gnxra vagb nppbhag.<\/c>";
		dbTblQ[3113].info = "<c>Vs gur ybnq vf qvfgevohgrq harirayl, gur qevivat fgnovyvgl vf nssrpgrq, rfcrpvnyyl jura oenxvat. N gnvy-urnil ybnq znl pnhfr gur zbgbeplpyr gb fuvzzl naq fjvat.<\/c>";
		dbTblQ[3114].info = "";
		dbTblQ[3115].info = "<c>Erne-ivrj zveebef zhfg or pbeerpgyl nggnpurq naq nqwhfgrq gb gur zbgbeplpyr. Penpxrq be oyvaq zveebef ner abg crezvggrq.<\/c>\r\n<c>Erneivrj zveebef znl cebgehqr yngrenyyl bire gur raq bs gur fgrrevat jurry.<\/c>";
		dbTblQ[3116].info = "<c>Vapbeerpgyl nqwhfgrq urnqyvtugf pna qnmmyr bgure ebnq hfref, znxvat vg zber qvssvphyg sbe gurz gb frr gur pbhefr bs gur ebnq naq sbyybj gur genssvp.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jbea gverf naq gverf jvgu vapbeerpgyl nqwhfgrq nve cerffher unir n terngyl erqhprq tevc ba gur tebhaq, juvpu erfhygf va ybatre oenxvat qvfgnaprf, nzbat bgure guvatf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Pyrne ivfvovyvgl zhfg or rafherq ng nyy gvzrf jura qevivat.<\/c>";
		dbTblQ[3117].info = "<c>Gur evtug-unaq ynar ehyr nyfb nccyvrf ba uvtujnlf va n pbafgehpgvba mbar. V gurersber fjvgpu gb gur evtug-unaq ynar.<\/c>";
		dbTblQ[3118].info = "";
		dbTblQ[3119].info = "<c>Uvtu-fcrrq fgnegf naq harira qevivat pnhfr haarprffnel abvfr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur rkpyhfvir hfr bs nccebirq rkunhfg flfgrzf vf erdhverq ol ynj.<\/c>";
		dbTblQ[3120].info = "<c>Iruvpyrf jvgu tnfbyvar be qvrfry ratvarf, erdhver n svar qhfg fgvpxre.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Pnef jvgu ryrpgevp zbgbef gung ner pyrneyl vqragvsvnoyr jvgu na R yvprafr cyngr nyfb erdhver gur svar qhfg fgvpxre.<\/c>";
		dbTblQ[3121].info = "<c>Irel ybhq zhfvp pna naabl bguref rira jvgu gur jvaqbjf pybfrq. Ng gur fnzr gvzr, vg pna qvfgenpg gur qevire naq guhf rapbhentr qevivat reebef. Abvfrf sebz bhgfvqr ner abg urneq.<\/c>";
		dbTblQ[3122].info = "<c>Qentf gung bppher juvyr qevivat vapernfr rkcbaragvnyyl naq abg yvarneyl jvgu gur vapernfvat fcrrq. Nqqvgvbany 30 xz\/u vafgrnq bs 130 xz\/u pna guhf vapernfr shry pbafhzcgvba ol hc gb 35 creprag.<\/c>\r\n<c>Gur jrvtug naq gernq qrcgu qb abg nssrpg gur shry pbafhzcgvba bs n iruvpyr nf zhpu.<\/c>";
		dbTblQ[3123].info = "<ue \/>\r\n<qvi pynff=\"dhrfgvba-rkcynangvba\">\r\n<c>Qhevat jnvgvat gvzrf, fgbccvat gur ratvar urycf gb eryvrir gur oheqra ba gur raivebazrag naq erqhpr shry pbafhzcgvba.<\/c>\r\n<\/qvi>";
		dbTblQ[3124].info = "";
		dbTblQ[3125].info = "";
		dbTblQ[3126].info = "<c>Ynar Xrrcvat Nffvfg jvgu fgrrevat vagreiragvba pna bayl xrrc gur iruvpyr va ynar vs gur ynar znexvatf ner tbbq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg qbrf abg shapgvba eryvnoyl jvgu cbbe ynar znexvatf.<\/c>";
		dbTblQ[3127].info = "<c>Ynar Xrrcvat Nffvfg jvgu fgrrevat vagreiragvba pna bayl xrrc gur iruvpyr va ynar vs gur ynar znexvatf ner tbbq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg qbrf abg jbex eryvnoyl jura gur ynar znexvatf ner cbbe, jura gheavat be jura biregnxvat.<\/c>";
		dbTblQ[3128].info = "<c>Ynar Xrrcvat Nffvfg jvgu fgrrevat vagreiragvba pna bayl xrrc gur iruvpyr va vgf ynar vs gur ynar znexvatf ner tbbq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg qbrf abg shapgvba eryvnoyl vs gur ynar znexvatf ner cbbe be hapyrne, r.t. va ebnqjbexf naq va cbbe jrngure pbaqvgvbaf gung vzcnve ivfvovyvgl.<\/c>";
		dbTblQ[3129].info = "<c>Ynar Xrrcvat Nffvfg jvgu fgrrevat vagreiragvba pna bayl xrrc gur iruvpyr va vgf ynar vs gur ynar znexvatf ner tbbq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg qbrf abg shapgvba eryvnoyl vs gur ynar znexvatf ner cbbe be hapyrne, r.t. va ebnqjbexf naq va cbbe jrngure pbaqvgvbaf gung vzcnve ivfvovyvgl.<\/c>";
		dbTblQ[3130].info = "<c>Oenxr Nffvfg naq Nhgbabzbhf Rzretrapl Oenxr Nffvfg fhccbeg gur qevire va n qnatrebhf fvghngvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur pehvfr pbageby flfgrz fhccbegf gur qevire rkpyhfviryl va znvagnvavat n pbafgnag fcrrq.<\/c>";
		dbTblQ[3131].info = "<c>Nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[3132].info = "<c>Jura purpxvat lbhe zbgbeplpyr, vg vf pehpvny gb purpx obgu oenxrf. Guvf zhfg or qbar bar nsgre gur bgure.<\/c>";
		dbTblQ[3133].info = "<c>Nsgre n pbyyvfvba jvgu n fbyvq bofgnpyr, purpx jurgure gur gler be evz unf orra qnzntrq. Qnzntr pbhyq yrnq gb n gler ohefgvat be n creznarag ybff bs cerffher.<\/c>";
		dbTblQ[3134].info = "<c>N oybja shfr znl or erfcbafvoyr sbe n snvyvat gnvyyvtug. Ohg vg vf bsgra cbffvoyr gung bayl gur vapnaqrfprag ynzc vf qrsrpgvir naq zhfg or ercynprq.<\/c>";
		dbTblQ[3135].info = "<c>Vs lbhe pne chyyf fgebatyl gb gur yrsg jura oenxvat, gurer znl or n qrsrpg va gur oenxr flfgrz. Lbhe pne zhfg or purpxrq ng gur jbexfubc vzzrqvngryl.<fcna pynff='peys'><\/fcna>Gb nibvq nal evfx, lbh zhfg gurersber pbagvahr gb qevir ng n ybj fcrrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va znal zbqrea iruvpyrf, gur \"unaq oenxr\" vf n \"cnexvat oenxr\" naq oybpxf gur erne jurryf. Gur nafjre vf gurersber vapbeerpg.<\/c>";
		dbTblQ[3136].info = "<c>Vs gur bcrengvba bs gur fgrrevat jurry erdhverf na hahfhnyyl uvtu nzbhag bs sbepr, gur fgrrevat nvq unf cebonoyl snvyrq. Gur fgrrevat trne znl nyfb or qrsrpgvir.<\/c>\r\n<c>Gur iruvpyr zhfg or purpxrq vzzrqvngryl va n jbexfubc.<\/c>";
		dbTblQ[3137].info = "<c>Nyy guerr nafjref unir na vzcnpg ba gver jrne naq ner gurersber pbeerpg.<\/c>";
		dbTblQ[3138].info = "<c>Vzcebcre gver vasyngvba cerffher, rfcrpvnyyl vs vg vf bar-fvqrq, pna terngyl nssrpg gur unaqyvat bs gur iruvpyr.<fcna pynff='peys'><\/fcna>Vs gur gver vasyngvba cerffher vf abg gur pnhfr, V qevir gb gur jbexfubc sbe vafcrpgvba.<\/c>";
		dbTblQ[3139].info = "<c>Qrsrpgf va gur punffvf be fgrrevat flfgrz pna raqnatre qevivat fnsrgl naq zhfg or purpxrq va n jbexfubc.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Qnzntr gb gur cnvagjbex vf bayl n ivfhny vzcnvezrag.<\/c>";
		dbTblQ[3140].info = "<c>Vs gur ohyo bs gur pbageby yvtug sbe na vaqvpngbe vf qrsrpgvir, gur vaqvpngbe ynzc synfurf zhpu snfgre. Gur qrsrpgvir ohyo zhfg or ercynprq nf dhvpxyl nf cbffvoyr.<\/c>";
		dbTblQ[3141].info = "";
		dbTblQ[3142].info = "";
		dbTblQ[3143].info = "<c>Vs gur ubyqre bs n qevivat yvprapr qbrf abg pbzcyl jvgu gur beqre gb cnegvpvcngr va na nqinaprq frzvane jvguva gur gvzr yvzvg, gur qevivat yvprapr jvyy or jvguqenja.<\/c>";
		dbTblQ[3144].info = "";
		dbTblQ[3145].info = "<c>Nalbar jub jnagf gb biregnxr unf gb orunir va fhpu n jnl gung gurer vf ab qnatre gb gur genssvp oruvaq uvf be ure iruvpyr. Jura biregnxvat, fhssvpvrag yngreny pyrnenapr zhfg or znvagnvarq sebz bgure zbgbevfgf, va cnegvphyne sebz crqrfgevnaf naq plpyvfgf.<\/c>\r\n<c><rz>FgIB &frpg;5.4:<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><rz>\"Jura biregnxvat crqrfgevnaf, plpyvfgf naq qeviref bs irel fznyy ryrpgevp iruvpyrf jvgu zbgbe iruvpyrf, gur fhssvpvrag yngreny qvfgnapr vf ng yrnfg 1.5 z va gbjaf&aofc; naq <fgebat>ng yrnfg 2 z bhgfvqr gbjaf<\/fgebat>.\"<\/rz><\/c>";
		dbTblQ[3146].info = "<c>Nalbar jub jnagf gb biregnxr unf gb orunir va fhpu n jnl gung gurer vf ab qnatre gb gur genssvp oruvaq uvf be ure iruvpyr. Jura biregnxvat, fhssvpvrag yngreny pyrnenapr zhfg or znvagnvarq sebz bgure zbgbevfgf, va cnegvphyne sebz crqrfgevnaf naq plpyvfgf.<\/c>\r\n<c><rz>FgIB &frpg;5.4:<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><rz>\"Jura biregnxvat crqrfgevnaf, plpyvfgf naq qeviref bs irel fznyy ryrpgevp iruvpyrf jvgu zbgbe iruvpyrf, gur fhssvpvrag yngreny qvfgnapr vf <fgebat>ng yrnfg 1.5 z va gbjaf<\/fgebat>&aofc; naq ng yrnfg 2 z bhgfvqr gbjaf.\"<\/rz><\/c>";
		dbTblQ[3147].info = "<c>FgIB &frpg; 5.4:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"Jura biregnxvat crqrfgevnaf, plpyvfgf naq qeviref bs irel fznyy ryrpgevp iruvpyrf jvgu zbgbe iruvpyrf, gur fhssvpvrag yngreny qvfgnapr vf ng yrnfg 1.5 z va gbjaf naq ng yrnfg 2 z bhgfvqr gbjaf.\"<\/c>";
		dbTblQ[3150].info = "";
		dbTblQ[3151].info = "<c>Ba gur ovxr cngu gb zl evtug, n plpyvfg vf evqvat va gur fnzr qverpgvba nf zr naq vf pheeragyl va zl \"oyvaq fcbg\".<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jura gheavat evtug, V jbhyq raqnatre gur plpyvfg.<\/c>";
		dbTblQ[3152].info = "<c>Gur zbgbeplpyvfg pbzvat sebz gur yrsg unf abg erqhprq uvf fcrrq. Ur frrzf gb jnag gb pebff gur vagrefrpgvba vzzrqvngryl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gb cerirag na nppvqrag sebz bppheevat, V tvir hc zl evtug bs jnl naq cresbez unmneq oenxvat.<\/c>";
		dbTblQ[3153].info = "<c>Gur plpyvfg jvyy punatr ynarf qhr gb gur cnexrq pnef. Qhr gb gur arneol pheir, juvpu vf qvssvphyg gb frr, fhqqra bapbzvat genssvp vf gb or rkcrpgrq.<\/c>";
		dbTblQ[3154].info = "<c>Va n ghaary, gur fbhaq trarengrq ol iruvpyrf rpubrf, juvpu pna erfhyg va n ybhq naq cbffvoyl qvfgheovat onpxtebhaq abvfr. Fnsrgl vasbezngvba zhfg or bofreirq va beqre gb or noyr gb ernpg vzzrqvngryl va na rzretrapl. Juvyr qevivat va Yvtug rssrpgf, sbe rknzcyr, guebhtu gur ghaary yvtugvat znl unir n qvfgenpgvat rssrpg.<\/c>";
		dbTblQ[3155].info = "<c>Nygubhtu vg vf abg crezvggrq ng guvf cbvag, gur zbgbeplpyvfg biregnxrf zr ba gur yrsg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va beqre abg gb raqnatre gur zbgbeplpyvfg naq gb nibvq na nppvqrag, V fybj qbja naq fgnl ba gur evtug ynar sbe gur gvzr orvat.<\/c>";
		dbTblQ[3156].info = "<c>Gur bapbzvat pne va gur bccbfvgr ynar vf qevivat fgenvtug nurnq naq unf gur evtug bs jnl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>V znl bayl ghea nsgrejneqf, vs na bofgehpgvba bs bgure ebnq hfref (r.t. nyfb crqrfgevnaf) vf rkpyhqrq.<\/c>";
		dbTblQ[3157].info = "<c>Haqre &frpg; 142 bs gur Pevzvany Pbqr (FgTO), uvg-naq-eha bssraqref ner chavfunoyr ol hc gb 3 lrnef' vzcevfbazrag be n svar.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>N qevivat ona be n zrqvpny-cflpubybtvpny rknzvangvba pna nyfb or beqrerq.<\/c>";
		dbTblQ[3158].info = "<c>Gur ryrpgevp fpbbgre vf ba gur cevbevgl ebnq naq vf qevivat fgenvtug nurnq. Gur ryrpgevp fpbbgre qevirf svefg.<\/c>";
		dbTblQ[3159].info = "<c>Va guvf genssvp pvepyr gur evtug bs jnl vf abg erthyngrq ol n genssvp fvta, gurersber gur \"evtug orsber yrsg\" ehyr nccyvrf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>FgIB &frpg; 8:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>(1) Ng vagrefrpgvbaf naq whapgvbaf, jubrire pbzrf sebz gur evtug unf gur evtug bs jnl. Guvf qbrf abg nccyl,<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>1. vs gur evtug bs jnl vf fcrpvnyyl erthyngrq ol genssvp fvtaf (fvtaf 205, 206, 301, 306) be<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>2. sbe iruvpyrf pbzvat sebz n svryq be sberfg cngu bagb nabgure ebnq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>(1n) Vs fvta 215 (genssvp pvepyr) vf cynprq orybj fvta 205 (tvir jnl) ng gur ragenapr gb n genssvp pvepyr, genssvp ba gur pvephyne ebnqjnl unf gur evtug bs jnl. Jura ragrevat fhpu n genssvp pvepyr, gur hfr bs gur qverpgvba vaqvpngbe vf abg crezvggrq.<\/c>";
		dbTblQ[3160].info = "<c>Erfgevpgrq nernf ner ebnq znexvatf gung vaqvpngr gung n pregnva nern bs gur ebnq znl abg or hfrq ol iruvpyrf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>FgIB &frpg; 41<\/c>";
		dbTblQ[3161].info = "<c>Nalbar qevivat n iruvpyr znl abg qevir fybjre guna gur fcrpvsvrq zvavzhz fcrrq, hayrff ebnq, genssvp, ivfvovyvgl be jrngure pbaqvgvbaf boyvtr uvz be ure gb qb fb. Vs lbh pnaabg zrrg gur zvavzhz fcrrq lbh pnaabg hfr gur ebnq.<\/c>";
		dbTblQ[3162].info = "<c>Crqrfgevnaf unir cevbevgl bire gheavat iruvpyrf ng vagrefrpgvbaf naq whapgvbaf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guvf nyfb nccyvrf gb gur ehaare pheeragyl bofpherq ol gur pbafgehpgvba jbexre.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>FgIB &frpg; 9:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>(3)<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nalbar jub jnagf gb ghea zhfg nyybj bapbzvat iruvpyrf gb cnff, envy iruvpyrf, ovplpyrf jvgu nhkvyvnel zbgbef, ovplpyrf naq zvavngher ryrpgevp iruvpyrf rira vs gurl ner geniryvat ba be arkg gb gur ebnqjnl va gur fnzr qverpgvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guvf nyfb nccyvrf gb ohfrf naq bgure iruvpyrf hfvat qrfvtangrq fcrpvny ynarf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><fgebat>Fcrpvny pbafvqrengvba zhfg or tvira gb crqrfgevnaf; vs arprffnel, qeviref zhfg jnvg.<\/fgebat><\/c>";
		dbTblQ[3163].info = "<c>Enva naq jrg yrnirf pna znxr ebnqf qnatrebhfyl fyvccrel. Ybatre oenxvat qvfgnaprf zhfg or rkcrpgrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur pbeerpg orunivbe vf gb erqhpr fcrrq naq nibvq wrexl fgrrevat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurer vf ab ernfba sbe urnil oenxvat naq vg jbhyq nyfb or qnatrebhf, nf gur iruvpyr pbhyq dhvpxyl fgneg gb fyvqr.<\/c>";
		dbTblQ[3164].info = "";
		dbTblQ[3165].info = "<c>N iruvpyr va gur bapbzvat genssvp unf fgbccrq ba gur yrsg ynar. Vg vf cbffvoyr gung bgure crbcyr znl trg bhg bs gur iruvpyr, juvpu vf jul vg vf arprffnel gb fybj qbja. Gur erq pne gung vf nccebnpuvat pbhyq biregnxr. Gung vf jul pnhgvba vf pnyyrq sbe.<\/c>";
		dbTblQ[3166].info = "<c>Gurer vf n evfx bs pbyyvfvba jvgu na navzny, juvpu pna or nibvqrq ol oenxvat va n gvzryl znaare.<\/c>";
		dbTblQ[3167].info = "<c>Gur gehpx erqhprf vgf fcrrq funecyl orpnhfr gurer vf na bowrpg va vgf ynar.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>V vzzrqvngryl erqhpr zl fcrrq, fgnl va zl yrsg ynar naq jngpu gur gehpx. Gur gehpx znl noehcgyl punatr gb gur yrsg ynar gb nibvq gur bofgnpyr.<\/c>";
		dbTblQ[3168].info = "<c>Gur gehpx va sebag vf punatvat ynarf naq vf fybjre va pbzcnevfba. Vg vf arprffnel gb fybj qbja va beqre gb nqwhfg lbhe bja fcrrq.<\/c>";
		dbTblQ[3169].info = "<c>Gur zbgbejnl jvyy or yrsg ivn na rkvg. Qevivat ng uvtu fcrrq sbe ybat crevbqf bs gvzr pna yrnq gb gur fcrrq orvat haqrerfgvzngrq. N ybbx ng gur fcrrqbzrgre fubhyq rafher gung gur fcrrq vf nyernql nqwhfgrq.<\/c>\r\n<c>Gur fcrrq fubhyq or erqhprq bayl ba gur rkvg ynar, fb nf abg gb bofgehpg gur sybjvat genssvp.<\/c>";
		dbTblQ[3170].info = "<c>Va beqre gb nibvq haarprffnel qnatre, lbhzhfg nqwhfg lbhe fcrrq. Nf fbba nf n fnsr bccbeghavgl cerfragf vgfrys, lbh znl biregnxr.<\/c>";
		dbTblQ[3171].info = "<c>Erirefvat vf bayl nyybjrq va bar-jnl fgerrgf sbe irel fubeg qvfgnaprf, r.t. sbe cnexvat, naq gheavat nebhaq va n bar-jnl fgerrg vf cebuvovgrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fb V jnvg hagvy V pna qevir ba.<\/c>";
		dbTblQ[3172].info = "<c>Lbh znl qevir ng n fcrrq juvpu nyybjf oenxvat jvguva gur ivfvoyr qvfgnapr. Vs ab bgure vasyhraprf (r.t., gur jrngure) vzcnve ivfvovyvgl, guvf vf qrgrezvarq ol gur urnqyvtugf.<\/c>";
		dbTblQ[3173].info = "<c>Gur genssvp fvta 223.1 \"Frvgrafgervsra orsnuera\" fgvchyngrf gung gur uneq fubhyqre zhfg or hfrq nf gur evtug ynar.<\/c>\r\n<c>Guvf nccyvrf gb nyy iruvpyrf, abg whfg gehpxf.<\/c>";
		dbTblQ[3174].info = "";
		dbTblQ[3175].info = "";
		dbTblQ[3176].info = "<c>V pna'g cnex abj orpnhfr nabgure iruvpyr vf abj va gur cnexvat fcnpr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"V'yy tvir hc gur cnexvat fcnpr ...\" vf gur evtug nafjre, rira vs vg vf qvssvphyg gb npprcg.<\/c>";
		dbTblQ[3177].info = "<c>Gur oyhr pne unf cevbevgl, vg jnf svefg ng gur cnexvat fcnpr. Ol fgbccvat naq frggvat gur ghea fvtany, ur pyrneyl vaqvpngrq gung ur jnagrq gb qevir vagb gur cnexvat fcnpr.<\/c>";
		dbTblQ[3178].info = "<c>Orpnhfr gur iruvpyr erznvaf ba gur ebnq, vg cbfrf n qnatre gb bgure zbgbevfgf. Bgure zbgbevfgf zhfg or jnearq bs gur qnatre ol synfuvat n jneavat yvtug. Gurer vf na boyvtngvba gb chg hc n jneavat fvta ng n fhssvpvrag qvfgnapr gb jnea gur nccebnpuvat genssvp bs gur qnatre ng na rneyl fgntr. Fgnaqneq inyhrf ner:<\/c>\r\n<hy>\r\n<yv>vaare pvgl: 50 zrgref<\/yv>\r\n<yv>pbhagel ebnq: 100 zrgref<\/yv>\r\n<yv>Serrjnl: 150 gb 400 zrgref<\/yv>\r\n<\/hy>\r\n<c>Jrnevat n uvtu-ivfvovyvgl irfg vf n fnsrgl zrnfher gung urycf gb vzcebir ivfvovyvgl sbe bgure zbgbevfgf.<\/c>";
		dbTblQ[3179].info = "<c>Vs n zhygv-ynar iruvpyr pbzrf gb n fgnaqfgvyy va n cynpr jurer vg pnaabg or erpbtavmrq va gvzr nf n fgngvbanel bofgnpyr, gur unmneq jneavat yvtugf zhfg or fjvgpurq ba vzzrqvngryl. Guvf nyfb nccyvrf gb iruvpyrf gung ner orvat gbjrq njnl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur checbfr bs gur unmneq jneavat yvtugf vf gb rafher gung gur iruvpyr vf erpbtavmrq nf na bofgnpyr va tbbq gvzr ol bgure ebnq hfref. <\/c>";
		dbTblQ[3180].info = "<c>Nyy nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Qrcraqvat ba gur jrvtug bs gur ybnq, vg <fgebat>znl or<\/fgebat> arprffnel gb nqwhfg gver vasyngvba cerffher, gur punffvf naq gur erne ivrj zveebe.<\/c>";
		dbTblQ[3181].info = "<c>Urnq erfgenvagf ner cneg bs n iruvpyr'f cnffvir fnsrgl flfgrz naq fvtavsvpnagyl vapernfr bpphcnag fnsrgl.<fcna pynff='peys'><\/fcna>Va gur rirag bs n pbyyvfvba, rfcrpvnyyl n erne-raq pbyyvfvba, gur urnq erfgenvag erqhprf gur evfx bs vawhel gb gur preivpny fcvar.<\/c>";
		dbTblQ[3182].info = "";
		dbTblQ[3183].info = "<c>Nf jvgu bgure nppvqragf, va gur rirag bs n jvyqyvsr nppvqrag vg vf nyfb arprffnel gb fgbc, fjvgpu ba gur unmneq jneavat yvtugf naq frpher gur fprar bs gur nppvqrag.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur cbyvpr zhfg or abgvsvrq. Nf n ehyr, gur cbyvpr bssvpref jvyy gura nyfb abgvsl gur erfcbafvoyr uhagref be sberfgref.<\/c>";
		dbTblQ[3184].info = "";
		dbTblQ[3185].info = "";
		dbTblQ[3186].info = "";
		dbTblQ[3187].info = "<c>Cnffratre pnef jvgu sebag qevir graq gb haqrefgrre, v.r., gur sebag graqf gb ghea bhg gb gur fvqr vs gur zbgbevfg nppryrengrf gbb zhpu va n pheir.<\/c>";
		dbTblQ[3188].info = "<c>N ynar punatr nffvfgnag pna bayl jbex eryvnoyl vs gur ynar znexvatf ner pyrneyl ivfvoyr.<fcna pynff='peys'><\/fcna>Vs gur nffvfgnag qrgrpgf n iruvpyr va gur eryrinag fnsrgl mbar, vg jvyy abg punatr ynarf.<fcna pynff='peys'><\/fcna>Gur ynar punatr nffvfgnag qbrf abg qrgrpg tncf va bapbzvat genssvp.<\/c>";
		dbTblQ[3189].info = "<c>Fvapr fgrrevat, oenxrf naq nveontf ner vzcbegnag sbe fnsrgl, nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[3190].info = "";
		dbTblQ[3191].info = "";
		dbTblQ[3192].info = "<c>Gur nqncgvir pehvfr pbageby flfgrz nyybjf gur qevire gb fryrpg n pbafgnag qevivat fcrrq orgjrra nccebk. 30 xz\/u naq nccebk. 200 xz\/u (qrcraqvat ba gur iruvpyr). <fcna pynff='peys'><\/fcna>Gur enqne-onfrq flfgrz cnlf nggragvba gb gur genssvp nurnq naq, vs gur fnsrgl qvfgnapr vf gbb fznyy, gur flfgrz nhgbzngvpnyyl erthyngrf gur qvfgnapr rvgure ol erqhpvat gur fcrrq be ol oenxvat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur hfr bs na NTE vf gurersber fhvgnoyr ba uvtujnlf naq serrjnlf. Ba jvaqvat ebnqf, gur qvfgnapr gb gur genssvp nurnq vf zber qvssvphyg gb qrgrpg, fb NTE vf abg fb fhvgnoyr urer.<\/c>";
		dbTblQ[3193].info = "";
		dbTblQ[3194].info = "";
		dbTblQ[3195].info = "";
		dbTblQ[3196].info = "";
		dbTblQ[3197].info = "<c>FgIB <fgebat>&frpg; 12 Fgbccvat naq cnexvat<\/fgebat><\/c>\r\n<c><rz>(3) Cnexvat vf abg crezvggrq<\/rz><fcna pynff='peys'><\/fcna><rz>1. va sebag bs naq oruvaq vagrefrpgvbaf naq whapgvbaf hc gb <fgebat>5 z<\/fgebat> sebz gur vagrefrpgvba bs gur ebnqjnl rqtrf,<\/rz><\/c>\r\n<c><rz>v<fgebat>s n ovplpyr ynar vf ohvyg ba gur evtug fvqr bs gur ebnqjnl<\/fgebat> va gur qverpgvba bs geniry, va sebag bs vagrefrpgvbaf naq whapgvbaf hc gb <fgebat>8 z<\/fgebat> sebz gur vagrefrpgvba bs gur ebnqjnl rqtrf,<\/rz><\/c>";
		dbTblQ[3198].info = "<c>Fvta 244.3 - Fgneg bs n ovplpyr mbar<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"N znkvzhz fcrrq bs 30 xz\/u nccyvrf gb genssvp ba gur ebnq. Ovplpyr genssvp zhfg abg or raqnatrerq be vzcrqrq. Vs arprffnel, zbgbe iruvpyr genssvp zhfg erqhpr fcrrq shegure.\"<\/c>";
		dbTblQ[3199].info = "<c>Bhgfvqr bs gbja, gur fnsrgl qvfgnapr fubhyq or ng yrnfg unys gur fcrrq. Unys bs 100 vf 50, juvpu zrnaf n qvfgnapr bs ng yrnfg 50 z fubhyq or xrcg.<\/c>";
		dbTblQ[3200].info = "<c>Gur biregnxvat qevire unf gb zretr dhvpxyl qhr gb bapbzvat genssvp. Gb znxr guvf cbffvoyr, erqhpr lbhe bja fcrrq.<\/c>";
		dbTblQ[3201].info = "<c>FgIB <fgebat>&frpg; 5 Biregnxva<\/fgebat>t<fcna pynff='peys'><\/fcna><rz>(2) Biregnxvat vf bayl crezvggrq gb nalbar jub pna frr gung gurer vf ab bofgehpgvba gb bapbzvat genssvp qhevat gur ragver biregnxvat cebprff.<\/rz><fcna pynff='peys'><\/fcna><rz>Shegurezber, biregnxvat vf bayl crezvggrq gb nalbar jub vf qevivat ng n fvtavsvpnagyl uvture fcrrq guna gur crefba orvat biregnxra.<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr gur biregnxvat cngu vf abg ivfvoyr qhr gb gur hcpbzvat evtug ghea, biregnxvat vf abg nyybjrq urer.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jura biregnxvat plpyvfgf, gur cerfpevorq yngreny qvfgnapr bhgfvqr ohvyg-hc nernf vf ng yrnfg 2 zrgref. Ba guvf aneebj ebnq, biregnxvat ng \"fvtavsvpnagyl uvture fcrrq\" vf abg cbffvoyr, juvpu vf nabgure ernfba jul biregnxvat vf abg nyybjrq urer.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurer ner ab fvtaf cebuvovgvat biregnxvat.<\/c>";
		dbTblQ[3202].info = "<c>Gur oyhr iruvpyr cnexf bhg, nccneragyl univat bireybbxrq zr. Gb nibvq na nppvqrag, V oenxr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nf gurer vf n zbgbeplpyr ba gur bapbzvat ynar, V pnaabg fjreir gb gur yrsg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"V nppryrengr orpnhfr V unir cevbevgl \" jbhyq zrna gb npprcg na nppvqrag pbafpvbhfyl bayl gb or evtug. Fb jebat nafjre.<\/c>";
		dbTblQ[3203].info = "<c>Gur oebja iruvpyr unf whfg fgbccrq naq gur qevire zvtug trg bhg va n uheel, abg cnlvat nggragvba gb gur genssvp. V zhfg gurersber or ernql gb oenxr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur crqrfgevna vf oruvaq gur qryvirel iruvpyr naq jvyy abg eha noehcgyl bagb gur ebnq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr gurer ner ab cnexrq pnef gb gur evtug bs gur ohf, gurer vf ab ernfba gur ohf pbhyq qevir vagb zl ynar.<\/c>";
		dbTblQ[3204].info = "<c>Gur crqrfgevna unf \"terra\", ur jvyy abg jnvg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur ohf jvyy abg yrnir vzzrqvngryl, fbzr cnffratref ner fgvyy obneqvat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur bapbzvat iruvpyr vf whfg biregnxvat gur ohf naq cbffvoyl gur genssvp yvtug unf orra bofpherq ol gur ohf. V zhfg rkcrpg gung gur qevire jvyy abg fgbc naq tb fgenvtug nurnq.<\/c>";
		dbTblQ[3205].info = "<c>V whfg cnffrq na ryrpgevp fpbbgre va gur ovxr ynar gung vf cebonoyl tbvat fgenvtug. Ng gur zbzrag vg vf va gur \"oyvaq fcbg\".<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>V unir gb fgbc orpnhfr gur ryrpgevp fpbbgre unf cevbevgl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>V qba'g unir gb fgbc orpnhfr bs gur fgerrgpne be orpnhfr bs crqrfgevnaf.<\/c>";
		dbTblQ[3206].info = "<c>N plpyvfg vf pbzvat gbjneqf lbh ba gur yrsg-unaq plpyr cngu, ohg ng gur zbzrag ur vf bofpherq ol gur bapbzvat iruvpyr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur plpyvfg naq bapbzvat iruvpyr unir cevbevgl. Gur pne gheavat vagb gur cevbevgl ebnq sebz gur yrsg zhfg jnvg.Gur plpyvfg naq bapbzvat iruvpyr unir cevbevgl. Gur pne gheavat vagb gur cevbevgl ebnq sebz gur yrsg zhfg jnvg.<\/c>";
		dbTblQ[3207].info = "<c>Vs genssvp vf ng n fgnaqfgvyy, iruvpyrf znl abg bpphcl gur crqrfgevna pebffvat vs gurl unir gb jnvg. Vs gurer vf genssvp pbatrfgvba, lbh zhfg abg bpphcl gur vagrefrpgvba be whapgvba qrfcvgr gur evtug bs jnl be terra yvtug fvtany vs lbh unir gb jnvg.<\/c>";
		dbTblQ[3208].info = "<c>FgIB &frpg; 12 Fgbccvat naq cnexvat<fcna pynff='peys'><\/fcna>(1) Fgbccvat vf abg crezvggrq<fcna pynff='peys'><\/fcna>5. va sebag bs naq va bssvpvnyyl znexrq sver qrcnegzrag npprff ebnqf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fgbccvat znl or crezvggrq va bar-jnl fgerrgf naq ab-cnexvat mbarf.<\/c>";
		dbTblQ[3209].info = "<c>Gur svefg fgrc vf gb frpher gur fprar bs gur nppvqrag, gura gb cebivqr svefg nvq naq gura gb nyreg gur erfphr freivpr.<\/c>";
		dbTblQ[3210].info = "<c>Va gur pnfr bs zvabe nppvqragf jvgubhg nal qbhog nf gb jub vf ng snhyg, vg vf abg arprffnel gb pnyy gur cbyvpr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Lbh fubhyq nyjnlf trg gur cubar ahzore, vqragvgl pneq ahzore naq vafhenapr qrgnvyf. Cubgbf bs gur nppvqrag fvghngvba naq n qrfpevcgvba bs ubj gur nppvqrag bppheerq naq gur qnzntr pnhfrq pna nyfb or urycshy yngre jura cerfragvat gur fvghngvba sbe frggyrzrag bs gur pynvz ol gur vafhenapr pbzcnal.<\/c>";
		dbTblQ[3211].info = "<c>Fvta 244.3 - Fgneg bs n ovplpyr mbar<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><rz>\"Iruvphyne genssvp bgure guna ovplpyr genssvp, nf jryy nf irel fznyy ryrpgevp iruvpyrf nf qrsvarq va gur rXSI, znl abg hfr ovplpyr mbarf hayrff crezvggrq ol nqqvgvbany fvtaf.\"<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fb nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[3212].info = "<c>Jura biregnxvat plpyvfgf, gur cerfpevorq yngreny qvfgnapr va ohvyg-hc nernf vf ng yrnfg 1.5 z.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur nqqvgvbany fvta 1000-32 vaqvpngrf gung plpyvfgf pna nyfb pbzr sebz gur bccbfvgr qverpgvba va guvf bar-jnl fgerrg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Plpyvfgf ner abg nyybjrq gb evqr ba gur fvqrjnyx.<\/c>";
		dbTblQ[3213].info = "<c>Fvta 283 zrnaf \"Nofbyhgr ab fgbccvat\", fgbccvat va sebag bs naq oruvaq vg vf abg nyybjrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nalbar jub yrnirf gur iruvpyr be fgbcf sbe zber guna guerr zvahgrf vf cnexvat. Fb cnexvat vf abg nyybjrq rvgure.<\/c>";
		dbTblQ[3214].info = "<c>Fvta 283 zrnaf \"Nofbyhgr cebuvovgvba gb fgbc\".<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur juvgr neebj vaqvpngrf gur ortvaavat bs na nofbyhgr cebuvovgvba gb fgbc.<\/c>";
		dbTblQ[3215].info = "<c>Gur fvta vaqvpngrf gur sbyybjvat znaqngbel ehyr: \"Fgbccvat vf cebuvovgrq\". Fgbccvat ba gur ebnq vf sbeovqqra.<\/c>";
		dbTblQ[3216].info = "<c>Gur cnexrq iruvpyrf erfgevpg gur ivrj bs gur fvqrjnyx. Puvyqera naq bgure crqrfgevnaf pbhyq eha bhg orgjrra gur iruvpyrf ng nal gvzr. Zbgbevfgf ner qevivat arkg gb cnexrq iruvpyrf. N qbbe pbhyq bcra ng nal gvzr, guerngravat qnatre naq znxvat vg arprffnel gb qevir fybjyl.<\/c>";
		dbTblQ[3217].info = "<c>Jura gur RFC yvtugf hc, gur qevivat fvghngvba vf ab ybatre fgnoyr, V unir gb erqhpr fcrrq.<\/c>";
		dbTblQ[3218].info = "<c>FgIB <fgebat>&frpg; 5 Biregnxvat<\/fgebat><\/c>\r\n<c><rz>(2) Biregnxvat vf bayl crezvggrq gb nalbar jub pna frr gung gurer vf ab bofgehpgvba gb bapbzvat genssvp qhevat gur ragver biregnxvat cebprff.<\/rz><\/c>\r\n<c><rz>Shegurezber, biregnxvat vf bayl crezvggrq gb nalbar jub vf qevivat ng n fvtavsvpnagyl uvture fcrrq guna gur crefba orvat biregnxra.<\/rz><\/c>\r\n<c>Fvapr gur biregnxvat ynar vf abg ivfvoyr qhr gb gur hcpbzvat evtug-unaq oraq, biregnxvat vf abg crezvggrq urer.<fcna pynff='peys'><\/fcna>Jura biregnxvat plpyvfgf, gur cerfpevorq yngreny qvfgnapr bhgfvqr ohvyg-hc nernf vf ng yrnfg 2 z. Vs bapbzvat genssvp pnaabg xrrc n fhssvpvrag yngreny qvfgnapr gb gur plpyvfgf, biregnxvat vf abg crezvggrq urer.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur thvqr yvar vf abg pbagvahbhf, vg znl or pebffrq.<\/c>";
		dbTblQ[3219].info = "<c>Jura biregnxvat plpyvfgf, gur cerfpevorq yngreny qvfgnapr va ohvyg-hc nernf vf ng yrnfg 1.5 z. Fvapr vg vf abg cbffvoyr gb znvagnva n fhssvpvrag yngreny qvfgnapr gb plpyvfgf jura biregnxvat, biregnxvat vf abg crezvggrq urer.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Ubaxvat vf nyfb abg nyybjrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>FgIB <fgebat>&frpg; 16 Jneavat fvtaf<\/fgebat><fcna pynff='peys'><\/fcna><rz>(1) Fbhaq naq yvtug fvtanyf znl bayl or tvira ol,<\/rz><fcna pynff='peys'><\/fcna><rz>1. nalbar biregnxvat bhgfvqr ohvyg-hc nernf (&frpg; 5 (5)) be<\/rz><fcna pynff='peys'><\/fcna><rz>2. jub frrf uvzfrys be bguref va qnatre.<\/rz><\/c>";
		dbTblQ[3220].info = "<c>FgIB <fgebat>&frpg; 5 Biregnxvat<\/fgebat><fcna pynff='peys'><\/fcna><rz>(2) Biregnxvat vf bayl crezvggrq gb nalbar jub pna frr gung gurer vf ab bofgehpgvba gb bapbzvat genssvp qhevat gur ragver biregnxvat cebprff.<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur yrsg-unaq oraq vf abg ivfvoyr naq bapbzvat genssvp zhfg or rkcrpgrq ng nyy gvzrf. Gurersber, fybj qbja gb jnyxvat fcrrq naq qevir nf sne gb gur evtug nf cbffvoyr.<\/c>";
		dbTblQ[3221].info = "<c>Gur bapbzvat pne frrzf gb jnag gb biregnxr gur cnexrq ina. Nf n erfhyg, vg jvyy unir gb fjreir cnegvnyyl vagb zl ynar.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gb nibvq na nppvqrag, V erqhpr zl fcrrq naq qevir nf sne gb gur evtug nf cbffvoyr va zl ynar.<\/c>";
		dbTblQ[3222].info = "<c>Gur thvqryvar fcrrq vf vagraqrq gb nibvq qnatrebhf naq genssvp wnz fvghngvbaf. Uvtu fcrrq naq rfcrpvnyyl uvtu fcrrq qvssreraprf orgjrra gur qvssrerag ynarf yrnqf gb vapernfrq pbatrfgvba naq zber (frevbhf) nppvqragf.<\/c>";
		dbTblQ[3223].info = "<c>Bs pbhefr, nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[3224].info = "<c>Gur tnagel fubjf n qvntbany erq pebff nobir gur fvqr fgevc, guhf gur hfr bs gur fvqr fgevc vf cebuvovgrq.<\/c>\r\n<c><fcna pynff='peys'><\/fcna>Yvxrjvfr, gur fvta 223.2 (oyhr fvta gb gur yrsg naq evtug bs gur tnagel) vaqvpngrf \"Qb abg hfr gur fvqr fgevc\".<fcna pynff='peys'><\/fcna>Gur uneq fubhyqre znl gurersber bayl or hfrq va gur rirag bs n oernxqbja.<\/c>";
		dbTblQ[3225].info = "<c>Jura rkvgvat n cebcregl, crqrfgevnaf trarenyyl unir cevbevgl. Vs ivfvovyvgl vf erfgevpgrq, lbh zhfg or vafgehpgrq.<\/c>\r\n<c>Hfvat bayl gur erne ivrj pnzren vf abg fhssvpvrag.<\/c>";
		dbTblQ[3226].info = "<c>Cnexvat ba fvqrjnyxf vf bayl crezvggrq vs gurer vf nccebcevngr fvtantr (genssvp fvta 315, oyhr fvta: pne ba fvqrjnyx) be vs gurer ner cnexvat fcnpr znexvatf. Bgurejvfr, n trareny cnexvat ona ba fvqrjnyxf nyfb nccyvrf gb zbgbeplpyrf.<\/c>";
		dbTblQ[3227].info = "<c>Jrnevat n onpxcnpx vapernfrf gur fhesnpr nern sbe gur pebffjvaq gb nggnpx, erfhygvat va n uvture fhfprcgvovyvgl gb pebffjvaqf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jrnevat n onpxcnpx fuvsgf gur pragre bs tenivgl hcjneq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur fgrrevat orunivbe qrgrevbengrf qhr gb gur uvture fhfprcgvovyvgl gb pebffjvaq naq gur uvture pragre bs tenivgl.<\/c>";
		dbTblQ[3228].info = "";
		dbTblQ[3229].info = "<c>Fvta 277.1<fcna pynff='peys'><\/fcna><rz>\"Nalbar qevivat n zhygv-ynar zbgbe iruvpyr znl abg biregnxr fvatyr <fgebat>be<\/fgebat> zhygv-ynar iruvpyrf.\"<\/rz><fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fb gurer vf n ona ba biregnxvat, jurer rira fvatyr-ynar iruvpyrf ner abg nyybjrq gb biregnxr.<\/c>";
		dbTblQ[3230].info = "<c>Gur biregnxvat ona nccyvrf gb ohfrf naq cnffratre pnef <fgebat>jvgu<\/fgebat> genvyref.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur biregnxvat ona qbrf abg nccyl gb fvatyr-genpx zbgbe iruvpyrf naq pnef jvgubhg genvyref.<\/c>";
		dbTblQ[3231].info = "";
		dbTblQ[3232].info = "<c>Nobir nyy, raivebazragnyyl sevraqyl qevivat zrnaf qevivat jvgu sberfvtug. Unmneqf ner hfhnyyl qrgrpgrq ng na rneyvre fgntr. Ng gur fnzr gvzr, uvtu fcrrqf ner nibvqrq, juvpu zrnaf gung qnatrebhf fvghngvbaf bpphe yrff serdhragyl.<\/c>";
		dbTblQ[3233].info = "<c>Na ryrpgevp zbgbe unf uneqyl nal jnfgr urng, fb gur urngvat cbjre vf qenja qverpgyl sebz gur onggrel. Gur cbjre pbafhzcgvba sbe guvf vf ol ab zrnaf artyvtvoyr naq unf n pbeerfcbaqvat artngvir vzcnpg ba gur enatr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Jura oenxvat, erphcrengvba gheaf gur ryrpgevp zbgbe vagb n trarengbe. Cneg bs gur xvargvp raretl vf pbairegrq onpx vagb ryrpgevpvgl, guhf punetvat gur onggrel.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fubeg, fgebat nppryrengvba erdhverf n ybg bs raretl naq guhf fubegraf gur enatr.<\/c>";
		dbTblQ[3234].info = "<c>Punatrf gb gur iruvpyr gung erdhver npprcgnapr ol gur iruvpyr yvprafvat nhgubevgl erdhver na vzzrqvngr nffrffzrag; gurer vf ab genafvgvbany crevbq.<\/c>";
		dbTblQ[3235].info = "";
		dbTblQ[3236].info = "<c>Gur ybjre gur fcrrq, gur ybjre gur evfx bs fxvqqvat jvgu gur genvyre.<\/c>";
		dbTblQ[3237].info = "<c>Gur fgnghf qvfcynl bs gur flfgrz fubjf jurgure vg vf fjvgpurq ba, fjvgpurq bss be unf n snhyg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nyy guerr nafjref ner pbeerpg.<\/c>";
		dbTblQ[3238].info = "<c>Gur fcrrq vf abg pbagebyyrq ol gur ynar qrcnegher jneavat flfgrz, abe qbrf vg qrgrpg gur znkvzhz crezvffvoyr fcrrq. Gur flfgrz nyfb qbrf abg qrgrpg ynar tebbirf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur flfgrz jneaf jura havagragvbanyyl yrnivat gur ynar.<\/c>";
		dbTblQ[3239].info = "<c>Ynar Xrrcvat Nffvfg jneaf gur qevire vs gur iruvpyr havagragvbanyyl yrnirf vgf ynar. Vs gur ghea fvtany vf abg npgvingrq, gur flfgrz nffhzrf gung gur ynar punatr vf havagragvbany.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur zvavzhz qvfgnapr gb gur iruvpyr va sebag vf abg qrgrpgrq ol Ynar Xrrcvat Nffvfg; bgure nffvfgnagf be gur qevire ner erfcbafvoyr sbe guvf.<\/c>";
		dbTblQ[3240].info = "<c>Gur ynar qrcnegher jneavat flfgrz pna or vzcnverq va vgf shapgvba va znal fvghngvbaf, r.t. ol:&yg;<\/c>\r\n<hy>\r\n<yv>Fabjsnyy<\/yv>\r\n<yv><fgebat>vafhssvpvrag ebnq vyyhzvangvba<\/fgebat><\/yv>\r\n<yv><fgebat>urnil enva<\/fgebat><\/yv>\r\n<yv><fgebat>zvffvat ynar znexvatf<\/fgebat><\/yv>\r\n<yv>sbt<\/yv>\r\n<yv>qverpg fhayvtug<\/yv>\r\n<yv>gvtug pheirf<\/yv>\r\n<\/hy>";
		dbTblQ[3241].info = "<c>&aofc;Gur frafbef bs gur ynar punatr nffvfgnag qrgrpg gur nern arkg gb gur iruvpyr hc gb nccebk. 4 zrgref naq gb gur erne gurl qrgrpg na nern hc gb nccebk. 50 zrgref.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur jneavat yvtug vaqvpngrf nabgure iruvpyr va guvf nern, fb ab ynar punatr gb gur yrsg znl or znqr ng guvf gvzr.<\/c>";
		dbTblQ[3242].info = "<c>Npgvir Ynar Xrrcvat Nffvfg pna or bireevqqra ol npgvingvat gur ghea fvtany naq ol pbhagrefgrrevat.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur fcrrq vf abg pbagebyyrq ol Ynar Xrrcvat Nffvfg, fb gur flfgrz pnaabg or bireevqqra ol nppryrengvat.<\/c>";
		dbTblQ[3243].info = "<c>Ynar Punatr Nffvfg erdhverf eryngviryl rnfvyl erpbtavmnoyr ynar znexvatf sbe bevragngvba; guvf vf hfhnyyl gur pnfr ba <fgebat>serrjnlf<\/fgebat> naq <fgebat>uvtujnl-yvxr ebnqf<\/fgebat>.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur flfgrz pna or vzcnverq va znal fvghngvbaf, r.t. ol fabjsnyy, enva, sbt, qverpg fhayvtug, gvtug pheirf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Pyrneyl, gur ynar punatr nffvfgnag pnaabg shapgvba ba <fgebat>ebnqf jvgubhg znexvatf<\/fgebat>.<\/c>";
		dbTblQ[3244].info = "<c>Npgvir Ynar Punatr Nffvfg pna punatr ynarf vaqrcraqragyl.<fcna pynff='peys'><\/fcna>Gur frafbef bs gur ynar punatr nffvfgnag qrgrpg gur nern arkg gb gur iruvpyr hc gb 3 - 4 zrgref naq gb gur erne gurl qrgrpg na nern hc gb 50 zrgref. Gur nern va sebag bs gur iruvpyr vf abg qrgrpgrq; bgure nffvfgnagf be gur qevire ner erfcbafvoyr sbe guvf.<fcna pynff='peys'><\/fcna>Ubjrire, gur ynar punatr pneevrq bhg ol gur ynar punatr nffvfgnag zhfg or zbavgberq ol gur qevire.<\/c>";
		dbTblQ[3245].info = "<c>Cbbeyl onynaprq be <fgebat>qnzntrq sebag jurryf<\/fgebat> ner n cbffvoyr ernfba sbe n jbooyvat fgrrevat jurry.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Qrsrpgvir fubpx nofbeoref, n qrsrpgvir fhfcrafvba be n <fgebat>snhygl jurry zbhagvat<\/fgebat> ner nyfb cbffvoyr pnhfrf.<\/c>";
		dbTblQ[3246].info = "<c>Gur snpgf ner pyrne:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>&frpg;21n frng orygf, ...<fcna pynff='peys'><\/fcna>(1) Cerfpevorq frng orygf zhfg or jbea juvyr qevivat; ...<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurersber, gur frng znl ab ybatre or hfrq naq gur frng oryg zhfg or ercynprq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nqwhfgvat gur frng oryg urvtug nqwhfgzrag jbhyq abg erzrql gur qrsrpg.<\/c>";
		dbTblQ[3247].info = "<c>N pehvfr pbageby flfgrz rafherf gung gur cerfrg fcrrq vf znvagnvarq pbafgnagyl juvyr qevivat.<\/c>\r\n<c><fcna pynff='peys'><\/fcna>Qrcerffvat gur nppryrengbe be oenxr crqny bireevqrf gur pehvfr pbageby. <fcna pynff='peys'><\/fcna>Fgrrevat unf ab vasyhrapr ba gur pehvfr pbageby.<\/c>";
		dbTblQ[3248].info = "<c>Gur fcrrq vf nhgbzngvpnyyl erthyngrq, v.r., gur iruvpyr nppryrengrf hagvy gur frg fcrrq vf ernpurq. Jura nccebnpuvat n iruvpyr va sebag ng n ybjre fcrrq, gur flfgrz nqwhfgf vgf bja fcrrq nppbeqvatyl.<\/c>\r\n<c>Gur fnsrgl qvfgnapr erdhverq va rnpu pnfr qrcraqf ba inevbhf snpgbef naq pnaabg or qrgrpgrq be xabja ol gur flfgrz.<\/c>";
		dbTblQ[3249].info = "<c>FgIB &frpg; 12. 3<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"Cnexvat vf abg crezvggrq va sebag bs naq oruvaq vagrefrpgvbaf naq whapgvbaf hc gb 5 z sebz gur vagrefrpgvba bs gur ebnqjnl rqtrf, <fgebat>vs n ovplpyr ynar vf ohvyg ba gur evtug fvqr bs gur ebnqjnl va gur qverpgvba bs geniry, va sebag bs vagrefrpgvbaf naq whapgvbaf hc gb 8 z<\/fgebat> sebz gur vagrefrpgvba bs gur ebnqjnl rqtrf\".<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><\/c>";
		dbTblQ[3250].info = "<c>Gur fcrrq funyy or nqncgrq va cnegvphyne gb ebnq, genssvp, ivfvovyvgl naq jrngure pbaqvgvbaf nf jryy nf gb gur fcrpvsvp novyvgvrf naq punenpgrevfgvpf bs gur iruvpyr naq ybnq.<\/c>\r\n<c>Vs ivfvovyvgl qhr gb sbt, fabj be enva vf yrff guna 50 z, gur fcrrq zhfg abg rkprrq 50 xz\/u hayrff n ybjre fcrrq vf erdhverq.<\/c>\r\n<c>&aofc;<\/c>";
		dbTblQ[3251].info = "<c>Nalbar qevivat n iruvpyr pbzovangvba ybatre guna 7 z zhfg, bhgfvqr bs ohvyg-hc nernf, pbafgnagyl znvagnva fhssvpvrag qvfgnapr sebz gur zbgbe iruvpyr va sebag gb nyybj na biregnxvat zbgbe iruvpyr gb chyy va.<\/c>";
		dbTblQ[3252].info = "<c>Gur ybnq znl cebgehqr gb gur erne hc gb <fgebat>1.50 <\/fgebat>z, ohg hc gb 3 z jura genafcbegrq bire n qvfgnapr bs hc gb 100 xz.<\/c>";
		dbTblQ[3253].info = "<c>N zbgbe iruvpyr bs pngrtbel O znl pneel ab zber guna bar genvyre.<\/c>";
		dbTblQ[3254].info = "";
		dbTblQ[3255].info = "<c>Gur nezonaq naq gur qbt vaqvpngr gung gur crqrfgevna vf ivfhnyyl vzcnverq. Vg vf lbhe qhgl gb fgbc naq yrg gur crqrfgevna pebff gur ebnq.<\/c>";
		dbTblQ[3256].info = "<c>Fpurqhyrq ohfrf, genzf naq znexrq fpubby ohfrf fgbccvat ng ohf fgbcf znl bayl or biregnxra pnershyyl, rira vs gurl ner bapbzvat genssvp.<\/c>\r\n<c>N cnffratre jub unf yrsg gur ohf nccrnef gb jnag gb pebff gur ebnqjnl va sebag bs gur ohf.<\/c>";
		dbTblQ[3257].info = "<c>Gur fcrrq zhfg or erqhprq orpnhfr n puvyq oruvaq gur iruvpyr cnexrq ba gur evtug guerngraf gb eha bagb gur ebnq.<\/c>";
		dbTblQ[3258].info = "<c>Nygubhtu gur plpyvfg qbrf abg tvir n unaq fvtany, fur pbhyq ghea yrsg gb sbyybj gur bgure plpyvfg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Cbffvoyr bapbzvat genssvp pnaabg or qrgrpgrq va gvzr ol gur cnexrq pnef, fb V nz abg nyybjrq gb biregnxr urer.<\/c>";
		dbTblQ[3259].info = "<c>Gur orunivbe bs gur plpyvfg vf abg sberfrrnoyr. Gurer vf n evfx gung ur jvyy qevir bss, juvpu jbhyq erfhyg va n pbyyvfvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur bapbzvat iruvpyr cebuvovgf n fjreir gb gur yrsg unys bs gur ynar.<\/c>";
		dbTblQ[3260].info = "<c>Nyy guerr nafjref ner pbeerpg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur plpyvfg vf irel yvxryl gb fjreir gb gur yrsg naq abg fgbc oruvaq gur iruvpyr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur gehpx unf whfg fgnegrq naq znl or fjreivat gb gur yrsg gb znxr n ghea.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur iruvpyr unf whfg fgbccrq naq gur qevire znl or noyr gb bcra gur qbbe.<\/c>";
		dbTblQ[3261].info = "<c>Va gur rirag bs n cbffvoyr genssvp wnz, vg vf vzcbegnag gb qevir jvgu cnegvphyne nggragvba. Rfcrpvnyyl va pheirf naq orsber perfgf, gur raq bs gur genssvp wnz zhfg or rkcrpgrq ng nal gvzr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg vf nyfb nqivfnoyr gb pbagvahr gb sbyybj gur genssvp ercbegf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur rzretrapl oenxr nffvfgnag pnaabg or hfrq va n cynaarq znaare gb fgbc fnsryl. Vg vagreirarf nhgbzngvpnyyl va n qnatrebhf fvghngvba.<\/c>";
		dbTblQ[3262].info = "<c>Gur plpyvfg pbzvat sebz gur yrsg unf gur evtug bs jnl. Gung'f jul V unir gb fgbc.<\/c>";
		dbTblQ[3263].info = "<c>Gurer vf n evfx bs pbyyvfvba jvgu gur plpyvfg pbzvat sebz gur yrsg. Ur frrzf gb or qevivat ba qrfcvgr gur erq genssvp yvtug, fb gung n pbyyvfvba pna bayl or nibvqrq ol zrnaf bs na rzretrapl oenxvat znabrhier.<\/c>";
		dbTblQ[3264].info = "<c>Erthyne oernxf naq qevax rabhtu syhvqf vf hfrshy va uvtu grzcrengherf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Ng uvtu abba, va gur terngrfg urng gb qevir pregnvayl abg.<\/c>";
		dbTblQ[3265].info = "<c>Jura biregnxvat va gur vaare pvgl, lbh zhfg znvagnva n yngreny qvfgnapr bs ng yrnfg 1.5 zrgref sebz plpyvfgf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr gur pbagvahbhf yvar znl nyfb abg or cnegvnyyl pebffrq, biregnxvat gur plpyvfg vf abg nyybjrq.<\/c>";
		dbTblQ[3266].info = "<c>Gur yrsg ynar raqf, gjb ynarf zretr. Vg vf arprffnel gb qevir hc gb gur aneebjvat bs gur ynar naq gura zretr vagb gur evtug ynar.<\/c>\r\n<c><fcna pynff='peys'><\/fcna>FgIB &frpg; 7 Cnen. 4 fgngrf gung gur punatr gb gur pbagvahbhf ynar fubhyq bayl gnxr cynpr <fgebat>vzzrqvngryl<\/fgebat> orsber gur aneebjvat. V znl gurersber biregnxr gur iruvpyrf va gur evtug ynar naq qb abg lrg punatr gb gur evtug ynar.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><\/c>";
		dbTblQ[3267].info = "<c>Gur FgIB &frpg; 9 cnen. 3 pyrneyl fgngrf urer:<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>\"Nalbar jub jnagf gb ghea zhfg yrg bapbzvat iruvpyrf cnff, envy iruvpyrf, ovplpyrf jvgu nhkvyvnel zbgbef, ovplpyrf naq zvavngher ryrpgevp iruvpyrf rira vs gurl ner geniryvat ba be arkg gb gur ebnqjnl va gur fnzr qverpgvba.\"<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gurersber, V nz abg nyybjrq gb ghea va sebag bs gur ryrpgevp fpbbgre naq zhfg yrg gur plpyvfg cnff.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur fubhyqre ivrj zhfg or pneevrq bhg, nf V zhfg znxr fher gung ab bgure ebnq hfref ner raqnatrerq.<\/c>";
		dbTblQ[3268].info = "<c>Ohfrf ba erthyne ebhgrf, fgerrgpnef naq znexrq fpubby ohfrf gung fgbc ng ohf fgbcf znl bayl or cnffrq jvgu pnhgvba, rira va bapbzvat genssvp.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Zl fcrrq bs 30 xz\/u vf nccebcevngr naq qbrf abg arrq gb or erqhprq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna><fgebat>Gur ohf qbrf abg unir vgf unmneq yvtugf ba<\/fgebat>, fb V qb abg unir gb qevir ng jnyxvat fcrrq.<\/c>";
		dbTblQ[3269].info = "<c>Gur crqrfgevnaf nccrne gb jnag gb pebff gur fgerrg ng gur pebffjnyx naq gurersber unir cevbevgl. Ol erqhpvat zl fcrrq, V fvtany gung gur crqrfgevnaf pna zbir bagb gur fgerrg.<\/c>";
		dbTblQ[3270].info = "<c>Vg vf arprffnel gb jnvg n ernfbanoyr nzbhag bs gvzr nppbeqvat gb gur pvephzfgnaprf naq gb yrnir bar'f anzr naq nqqerff ng gur fprar bs gur nppvqrag. Va nqqvgvba, gur svaqvatf funyy&aofc; nor znqr cbffvoyr ergebfcrpgviryl jvgubhg qrynl vs bar vf ragvgyrq, rkphfrq be erzbirq sebz gur fprar bs gur nppvqrag nsgre gur jnvgvat crevbq unf rkcverq. Gb guvf raq, ng yrnfg gur ragvgyrq vaqvivqhnyf be n arneol cbyvpr fgngvba zhfg or vasbezrq gung gurl unir orra vaibyirq va gur nppvqrag naq tvira gurve nqqerff naq gur ybpngvba bs gurve iruvpyr. Nqqvgvbanyyl, gur ertvfgengvba ahzore naq gur ybpngvba bs gur iruvpyr vaibyirq zhfg or fgngrq naq gur iruvpyr zhfg or znqr ninvynoyr sbe vzzrqvngr svaqvatf sbe n ernfbanoyr crevbq bs gvzr.<\/c>";
		dbTblQ[3271].info = "<c>Jura n cbyvpr bssvpre vf qverpgvat genssvp ng na vagrefrpgvba, uvf vafgehpgvbaf gnxr cerprqrapr bire nyy bgure ehyrf, fhpu nf genssvp yvtugf naq evtug-bs-jnl fvtaf.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vs gur cbyvpr bssvpre ubyqf obgu nezf bhg gb gur fvqr npebff gur qverpgvba bs geniry, vg zrnaf FGBC. V zhfg jnvg orsber gur vagrefrpgvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur zbgbeplpyr zhfg jnvg orpnhfr vg jnagf gb ghea.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur pne pbzvat sebz gur yrsg qevirf svefg.<\/c>";
		dbTblQ[3272].info = "<c>Gur pne pbzvat sebz gur yrsg sbyybjf gur pbhefr bs gur cevbevgl ebnq naq gurersber unf cevbevgl, vg qevirf svefg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Obgu iruvpyrf ba gur cevbevgl ebnq unir cevbevgl bire zr, fb V qevir ynfg.<\/c>";
		dbTblQ[3273].info = "<c>Gur iruvpyr pbzvat sebz gur evtug sbyybjf gur pbhefr bs gur cevbevgl ebnq naq qevirf svefg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr V nz ba gur cevbevgl ebnq, V qevir va sebag bs gur iruvpyr pbzvat sebz gur yrsg.<\/c>";
		dbTblQ[3274].info = "<c>Gur bapbzvat iruvpyr sbyybjf gur pbhefr bs gur gheavat cevbevgl ebnq naq qevirf svefg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Nppbeqvat gb gur onfvp ehyr evtug orsber yrsg, V znl qevir va sebag bs gur iruvpyr pbzvat sebz gur yrsg.<\/c>";
		dbTblQ[3275].info = "<c>Jrgarff vzcnvef ivfvovyvgl naq guhf vzcnvef ebnq fnsrgl. Gur fcynfu jngre bs gur cerprqvat iruvpyrf vaqvpngrf n irel jrg ebnqjnl. Guvf pna fvtavsvpnagyl erqhpr genpgvba.<\/c>";
		dbTblQ[3276].info = "<c>Jura qevivat vagb gur fyvcfgernz bs gur gehpx, gur pebffjvaq vf pbzcrafngrq fb gung gur pne chyyf gb gur evtug. Jura lbh yrnir gur fyvcfgernz, gur pebffjvaq ergheaf, znxvat pbhagre-fgrrevat arprffnel.<\/c>";
		dbTblQ[3277].info = "<c>Gurer ner iruvpyrf va n pne cnex sbe uvxref. Gurersber, bar fubhyq nagvpvcngr crqrfgevnaf jub znl nyfb jnyx ba gur ebnq qhr gb gur fabj. Fabj naq fyhfu ng gur fvqr bs gur ebnq nqqvgvbanyyl aneebj gur ebnqjnl.<\/c>";
		dbTblQ[3278].info = "<c>Va gur pnfr bs ndhncynavat, gur gver unf ybfg pbagnpg jvgu gur ebnq fhesnpr. Gurersber, V zhfg abg fgrre, oenxr, nppryrengr be znxr nal urpgvp qevivat znarhiref ng guvf gvzr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Pnershyyl gnxvat lbhe sbbg bss gur tnf crqny naq xrrcvat gur fgrrevat jurry nf fgenvtug nf cbffvoyr vf gur pbeerpg orunivbe.<\/c>";
		dbTblQ[3279].info = "<c>N oyhr gehpx vf va gur bapbzvat ynar. Nf n erfhyg, biregnxvat jbhyq raqnatre bapbzvat genssvp naq gur arprffnel yngreny qvfgnapr bs 1.5 z gb gur plpyvfgf jbhyq abg or cbffvoyr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guhf, biregnxvat vf bhg bs gur dhrfgvba.<\/c>";
		dbTblQ[3280].info = "<c>Qhr gb gur gerrf ybpngrq pybfr gb gur ebnqjnl, pregnva iruvpyrf znl or sbeprq gb hfr gur pragre bs gur ebnqjnl. Guhf, gurer vf na vapernfrq evfx bs pbyyvfvba naq, ng gur fnzr gvzr, biregnxvat vf znqr zber qvssvphyg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Qhr gb gur pbafgnag punatr bs yvtug naq funqr, fvqr qvfgnaprf nf jryy nf bapbzvat genssvp ner bsgra qvssvphyg gb whqtr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Qhr gb gur znal gerrf, gerr nppvqragf nyfb bpphe zber serdhragyl va nirahrf.<\/c>";
		dbTblQ[3281].info = "<c>Jura biregnxvat va vaare-pvgl genssvp, lbh zhfg znvagnva n yngreny qvfgnapr bs ng yrnfg 1.5 zrgref sebz gur plpyvfg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr guvf vf abg cbffvoyr qhr gb gur genssvp vfynaq, V erqhpr zl fcrrq naq qb abg biregnxr gur plpyvfg.<\/c>";
		dbTblQ[3282].info = "";
		dbTblQ[3283].info = "<c>Genvaf ner bsgra geniryvat fybjyl ol pbzcnevfba. Gb ranoyr bgure ebnq hfref gb biregnxr fnsryl, n qvfgnapr zhfg or znvagnvarq gung nyybjf biregnxvat iruvpyrf gb zretr fnsryl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va guvf pnfr, gur qvfgnapr gb gur iruvpyr va sebag vf nccebcevngr naq zhfg or erfgberq ntnvafg nsgre gur biregnxvat znarhire.<\/c>";
		dbTblQ[3284].info = "<c>Gur uvture gur jrvtug bs n iruvpyr, gur ybatre gur oenxvat qvfgnapr. Qevivat orunivbe, r.t. va pheirf, nyfb punatrf jvgu vapernfvat jrvtug.<\/c>";
		dbTblQ[3285].info = "<c>Ohfrf naq znexrq fpubby ohfrf gung ner nccebnpuvat n fgbc naq unir gurve unmneq jneavat yvtugf ba znl abg or biregnxra.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur biregnxvat zbgbeplpyr vf ba zl yrsg, gurersber V nz nyfb abg nyybjrq gb biregnxr.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur ynpx bs ynar znexvatf vf abg n ernfba sbe cebuvovgvat biregnxvat.<\/c>";
		dbTblQ[3286].info = "<c>Vs lbh jnag gb cnff n aneebjvat bs gur ebnq, na bofgnpyr ba gur ebnq be n fgbccrq iruvpyr ba gur yrsg, lbh zhfg yrg bapbzvat iruvpyrf cnff.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>V gurersber jnvg va sebag bs gur pbafgevpgrq cbvag.<\/c>";
		dbTblQ[3287].info = "<c>Vs lbh jnag gb qevir cnfg n aneebjvat bs gur ebnqjnl, na bofgnpyr ba gur ebnqjnl be n fgbccvat iruvpyr ba gur yrsg, lbh zhfg nyybj bapbzvat iruvpyrf gb cnff. Gurer vf abg rabhtu ebbz gb nyybj lbh gb fnsryl cnff gur plpyvfg, fb lbh unir gb jnvg.<\/c>";
		dbTblQ[3288].info = "<c>Nalbar trggvat ba be bss gur iruvpyr zhfg orunir va fhpu n jnl gung gurer vf ab evfx gb bgure ebnq hfref.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Guvf erdhverf ybbxvat bire gur fubhyqre naq bofreivat gur genssvp va gur yrsg-unaq fvqr zveebe.<\/c>";
		dbTblQ[3289].info = "<c>Zbgbe iruvpyrf zhfg or frpherq ntnvafg hanhgubevmrq hfr. Gurersber, jvaqbjf zhfg or pybfrq naq qbbef ybpxrq.<\/c>";
		dbTblQ[3290].info = "<c>Gur <fgebat>oyhr<\/fgebat> vaqvpngbe ynzc vaqvpngrf gung gur znva ornz vf fjvgpurq ba.<\/c>";
		dbTblQ[3291].info = "";
		dbTblQ[3292].info = "";
		dbTblQ[3293].info = "<c>Gur Rhebcrna nppvqrag ercbeg vf n fgnaqneqvmrq sbez hfrq guebhtubhg Rhebcr gb qbphzrag gur pbhefr bs na nppvqrag.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Vg snpvyvgngrf naq ranoyrf gur erpbeqvat bs gur nppvqrag, rfcrpvnyyl jura qvssrerag ynathntrf ner fcbxra.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Rnpu cnegl vaibyirq va gur nppvqrag svyyf bhg na nppvqrag ercbeg naq rnpu cnegl erprvirf n pbcl bs gur pbzcyrgrq ercbeg.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur vasbezngvba cebivqrq vf abg na nqzvffvba bs thvyg, naq gur dhrfgvba bs yvnovyvgl vf abg pynevsvrq ol gur nppvqrag ercbeg.<\/c>";
		dbTblQ[3294].info = "<c>Oyhr synfuvat yvtug gbtrgure jvgu gur rzretrapl ubea beqref: \"Nyy bgure ebnq hfref zhfg pyrne gur jnl vzzrqvngryl\".<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fvapr gurer vf ab bapbzvat genssvp va fvtug, qevir nf sne gb gur evtug nf cbffvoyr naq erqhpr fcrrq fhssvpvragyl va guvf fvghngvba.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Fgbccvat vf abg arprffnel.<\/c>";
		dbTblQ[3295].info = "";
		dbTblQ[3296].info = "<c>Erphcrengvba ersref gb n grpuavpny cebprff sbe erpbirevat raretl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Va ryrpgevp iruvpyrf, gur ryrpgevp zbgbe orpbzrf n trarengbe nf fbba nf gur sbbg vf gnxra bss gur tnf crqny juvyr qevivat.&aofc; Ryrpgevpny raretl vf erpbirerq naq srq onpx vagb gur onggrel.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Qrcraqvat ba gur erphcrengvba fgeratgu frg, oenxvat vf fgrccrq hc naq zber ryrpgevpvgl vf trarengrq.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gur freivpr oenxr flfgrz vf fcnerq orpnhfr vg vf hfrq yrff bsgra.<\/c>";
		dbTblQ[3297].info = "<c>Punatrf va gur genafzvffvba engvb znl yrnq gb gur pnapryyngvba bs gur bcrengvat yvprapr. Gur fnzr nccyvrf gb ratvar zbqvsvpngvbaf gung freir gb vapernfr cresbeznapr.<\/c>";
		dbTblQ[3298].info = "";
		dbTblQ[3299].info = "<c>Npgvir Cnexvat Nffvfg naq Nqncgvir Pehvfr Pbageby (NPP) pna or npgvingrq naq qrnpgvingrq znahnyyl.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Rzretrapl Oenxr Nffvfg pnaabg or npgvingrq be qrnpgvingrq znahnyyl.<\/c>";
		dbTblQ[3300].info = "";
		dbTblQ[3301].info = "";
		dbTblQ[3302].info = "";
		dbTblQ[3303].info = "<c>Vs gur gver cerffher vf fvtavsvpnagyl gbb ybj, gur gver ybfrf vgf fgnovyvgl. Guvf jbefraf qevivat fgnovyvgl naq fgrrevat orunivbe.<fcna pynff='peys'><\/fcna><fcna pynff='peys'><\/fcna>Gver jrne vapernfrf funecyl naq qbrf abg qrpernfr.<\/c>";
		dbTblQ[3304].info = "";
